INSERT INTO articles VALUES ('130', null, '58', null, '130', '0', 'NEWS00130', 'Chuyện gì đang xảy ra, Google loại bỏ VN khỏi danh sách dẫn đường Google Maps', 'chuyen-gi-dang-xay-ra-google-loai-bo-vn-khoi-danh-sach-dan-duong-google-maps', 'test', '<p id=\"contentCtrl1_lblNewsContent\" style=\"margin: 0px; padding: 0px; color: rgb(51, 51, 51); font-family: Arial, Helvetica, sans-serif; font-size: 12px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 15px; orphans: 2; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);\">\n	<span style=\"margin: 0px; padding: 0px; text-align: center;\">&nbsp;</span><img alt=\"[IMG]\" class=\"bbCodeImage LbImage\" src=\"http://media.tinhte.vn/photo/var/albums/sonlazio/photo-1839913825.PNG?m=1337175668\" style=\"margin: 0px; padding: 0px; border: 0px; vertical-align: top; text-align: center; max-width: 100%; color: rgb(20, 20, 20); font-family: verdana, tahoma, arial; font-size: 13px; line-height: 17px;\" /></p>\n<div style=\"margin: 0px; padding: 0px; color: rgb(51, 51, 51); font-family: Arial, Helvetica, sans-serif; font-size: 12px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 15px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-align: center;\">\n	<br style=\"margin: 0px; padding: 0px;\" />\n	&nbsp;</div>\n<div class=\"messageContent\" style=\"margin: 0px; padding: 0px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; orphans: 2; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webki