INSERT INTO articles VALUES ('354', null, '72', null, '353', '0', 'NEWS00353', 'Bên trong nhà máy in tiền của Mỹ', 'ben-trong-nha-may-in-tien-cua-my', 'Khác với yêu cầu tối mật ở nhiều quốc gia, nhà máy in tiền của Mỹ thường xuyên mở cửa phục vụ khách tham quan. Nhà máy in tiền Mỹ (U.S. Bureau of Engraving and Printing - BEP) được đặt tại đường số 14', '<p style=\"text-align: center;\">\n	<a href=\"http://tvintec.com.vn/m-pc/id-169/may-ban-hang-pos.html\" style=\"padding: 0px; margin: 0px; text-decoration: none; font-family: Arial, Helvetica, sans-serif; color: rgb(59, 97, 159); font-size: 13px; line-height: 20px; text-align: justify;\"><img alt=\"Nhà máy in tiền\" src=\"http://nuocmy.net/Img.ashx?634645663759319303.JPG\" style=\"padding: 0px; margin: 0px;\" /></a><br style=\"padding: 0px; margin: 0px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 20px; text-align: justify;\" />\n	<span style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 20px; text-align: justify;\">&nbsp;L&agrave; nh&agrave; m&aacute;y in tiền quốc gia, BEP chịu tr&aacute;ch nhiệm thiết kế v&agrave; in tất cả c&aacute;c loại tiền giấy của Mỹ.&nbsp;</span><br style=\"padding: 0px; margin: 0px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 20px; text-align: justify;\" />\n	<br style=\"padding: 0px; margin: 0px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 20px; text-align: justify;\" />\n	<br style=\"padding: 0px; margin: 0px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 20px; text-align: justify;\" />\n	<span style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 20px; text-align: justify;\">B&ecirc;n cạnh đ&oacute;, nhờ sở hữu c&ocirc;ng nghệ in h&agrave;ng đầu tr&ecirc;n thế giới, BEP cũng sản xuất nhiều phần của hộ chiếu, vật liệu l&agrave;m thẻ an ninh nội địa, thẻ nhận dạng trong qu&acirc;n đội, giấy chứng nhận nhập cư, nhập tịch, giấy mời của Nh&agrave; Trắng, tr&aacute;i phiếu kho bạc v&agrave; c&aacute;c loại tr&aacute;i phiếu Mỹ kh&aacute;c.</span><br style=\"padding: 0px; margin: 0px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 20px; text-align: justify;\" />\n	<br style=\"padding: 0px; margin: 0px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 20px; text-align: justify;\" />\n	<a href=\"http://tvintec.com.vn/m-pc/id-151/may-in-hoa-don.html\" style=\"padding: 0px; margin: 0px; text-decoration: none; font-family: Arial, Helvetica, sans-serif; color: rgb(59, 97, 159); font-size: 13px; line-height: 20px; text-align: justify;\"><img alt=\"máy in tiền mở\" src=\"http://nuocmy.net/Img.ashx?634645657043975509.jpg\" style=\"padding: 0px; margin: 0px;\" /></a><br style=\"padding: 0px; margin: 0px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 20px; text-align: justify;\" />\n	<br style=\"padding: 0px; margin: 0px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 20px; text-align: justify;\" />\n	<span style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 20px; text-align: justify;\">Nh&agrave; m&aacute;y in tiền mở cửa cho tham quan từ 9h tới 14h từ thứ 2 đến thứ 6 h&agrave;ng tuần (trừ ng&agrave;y lễ). Ri&ecirc;ng từ th&aacute;ng 4 đến th&aacute;ng 8, nh&agrave; m&aacute;y sẽ cho tham quan th&ecirc;m trong khoảng thời gian 5&ndash;7h chiều.</span><br style=\"padding: 0px; margin: 0px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 20px; text-align: justify;\" />\n	<br style=\"padding: 0px; margin: 0px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 20px; text-align: justify;\" />\n	<img alt=\"\" src=\"http://nuocmy.net/Img.ashx?634645660064140813.jpg\" style=\"padding: 0px; margin: 0px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 20px; text-align: justify;\" /><br style=\"padding: 0px; margin: 0px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 20px; text-align: justify;\" />\n	<span style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 20px; text-align: justify;\">Cứ 15 ph&uacute;t sẽ c&oacute; một tour v&agrave;o tham quan, mỗi tour k&eacute;o d&agrave;i 30 ph&uacute;t. Tour tham quan bắt đầu từ cổng sau của nh&agrave; m&aacute;y.</span><br style=\"padding: 0px; margin: 0px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 20px; text-align: justify;\" />\n	<a href=\"http://tvintec.com.vn/m-pc/id-147/may-in-ma-vach.html\" style=\"padding: 0px; margin: 0px; text-decoration: none; font-family: Arial, Helvetica, sans-serif; color: rgb(59, 97, 159); font-size: 13px; line-height: 20px; text-align: justify;\"><img alt=\"khung in tiền\" src=\"http://nuocmy.net/Img.ashx?634645657064879545.jpg\" style=\"padding: 0px; margin: 0px;\" /></a><br style=\"padding: 0px; margin: 0px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 20px; text-align: justify;\" />\n	<br style=\"padding: 0px; margin: 0px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 20px; text-align: justify;\" />\n	<span style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 20px; text-align: justify;\">Đến nh&agrave; m&aacute;y in tiền, kh&aacute;ch tham quan sẽ c&oacute; cơ hội ngắm nh&igrave;n chu tr&igrave;nh in ấn từng mảng lớn, chất đống, cắt nhỏ... của những đồng đ&ocirc;la Mỹ quen thuộc trong giao dịch quốc tế.</span><br style=\"padding: 0px; margin: 0px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 20px; text-align: justify;\" />\n	<img alt=\"\" src=\"http://nuocmy.net/Img.ashx?634645657138355674.jpg\" style=\"padding: 0px; margin: 0px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 20px; text-align: justify;\" /><br style=\"padding: 0px; margin: 0px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 20px; text-align: justify;\" />\n	<br style=\"padding: 0px; margin: 0px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 20px; text-align: justify;\" />\n	<span style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 20px; text-align: justify;\">B&ecirc;n cạnh đ&oacute;, mọi c&acirc;u hỏi, thắc mắc của họ li&ecirc;n quan tới qu&aacute; tr&igrave;nh in tiền đều c&oacute; thể được giải đ&aacute;p cặn kẽ.</span><br style=\"padding: 0px; margin: 0px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 20px; text-align: justify;\" />\n	<img alt=\"\" src=\"http://nuocmy.net/Img.ashx?634645657105127616.jpg\" style=\"padding: 0px; margin: 0px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 20px; text-align: justify;\" /><br style=\"padding: 0px; margin: 0px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 20px; text-align: justify;\" />\n	<br style=\"padding: 0px; margin: 0px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 20px; text-align: justify;\" />\n	<span style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 20px; text-align: justify;\">Qu&aacute; tr&igrave;nh in tiền trải qua nhiều c&ocirc;ng đoạn. Mỗi bản in tiền đều gồm h&agrave;ng loạt c&aacute;c chi tiết phức tạp để g&acirc;y kh&oacute; khăn cho việc l&agrave;m giả.&nbsp;</span><br style=\"padding: 0px; margin: 0px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 20px; text-align: justify;\" />\n	<br style=\"padding: 0px; margin: 0px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 20px; text-align: justify;\" />\n	<img alt=\"\" src=\"http://nuocmy.net/Img.ashx?634645657171115732.jpg\" style=\"padding: 0px; margin: 0px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 20px; text-align: justify;\" /><br style=\"padding: 0px; margin: 0px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 20px; text-align: justify;\" />\n	<span style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 20px; text-align: justify;\">C&aacute;c tờ tiền sẽ bắt đầu được in từ mặt sau. Sau khi qu&aacute; tr&igrave;nh in mặt sau ho&agrave;n tất, c&aacute;c nh&acirc;n vi&ecirc;n kỹ thuật l&agrave;m nhiệm vụ kiểm tra lỗi. Sau đ&oacute;, c&aacute;c tờ tiền cần được để kh&ocirc; trong v&ograve;ng 3 ng&agrave;y rồi mới tiếp tục được in mặt trước</span><br style=\"padding: 0px; margin: 0px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 20px; text-align: justify;\" />\n	<img alt=\"\" src=\"http://nuocmy.net/Img.ashx?634645657151771698.jpg\" style=\"padding: 0px; margin: 0px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 20px; text-align: justify;\" /><br style=\"padding: 0px; margin: 0px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 20px; text-align: justify;\" />\n	<br style=\"padding: 0px; margin: 0px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 20px; text-align: justify;\" />\n	<span style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 20px; text-align: justify;\">Khi cả 2 mặt đ&atilde; được in, c&aacute;c nh&acirc;n vi&ecirc;n kỹ thuật tiếp tục c&ocirc;ng đoạn kiểm tra lỗi v&agrave; tiếp đ&oacute; l&agrave; quy tr&igrave;nh kiểm tra chất lượng bằng m&aacute;y. Tuy nhi&ecirc;n, phần lớn c&aacute;c bản in được kiểm tra tự động bằng m&aacute;y.</span></p>', 'Inside the factory in U.S. currency', 'Unlike the secret requirements in many countries, the U.S. money printer regularly open for visitors. Plant in United States currency (U.S. Bureau of Engraving and Printing - BEP) is located at Road No. 14', '<p>\n	Updating ...</p>\n', '0', null, '0', 'nha-may-in-tien.jpg', '1', '0', null, null, '0', '0', '', '1', 'img/news/', '0', '0', null, null, '2013-07-17 23:35:21', '2013-07-25 06:22:11', null, null, null, null, null, null, null, null, null, null, null, null, null, null);
INSERT INTO articles VALUES ('355', null, '72', null, '354', '0', 'NEWS00354', 'Triển khai chương trình Đại sứ LienVietPostBank', 'trien-khai-chuong-trinh-dai-su-lienvietpostbank', 'Đại sứ LienVietPostBank còn hỗ trợ tra cứu điểm thưởng qua dịch vụ Internet Banking, SMS, gọi điện tới trung tâm dịch vụ khách hàng:', '<div>\n	Đại sứ LienVietPostBank c&ograve;n hỗ trợ tra cứu điểm thưởng qua dịch vụ Internet Banking, SMS, gọi điện tới trung t&acirc;m dịch vụ kh&aacute;ch h&agrave;ng:&nbsp;</div>\n<div>\n	&nbsp;</div>\n<div>\n	&nbsp;</div>\n<div>\n	Ng&acirc;n h&agrave;ng Bưu điện Li&ecirc;n Việt (LienVietPostBank) vừa triển khai chương tr&igrave;nh chăm s&oacute;c kh&aacute;ch h&agrave;ng mang t&ecirc;n Đại sứ LienVietPostBank với mục đ&iacute;ch tri &acirc;n kh&aacute;ch h&agrave;ng, đồng thời x&acirc;y dựng h&igrave;nh ảnh &ldquo;ng&acirc;n h&agrave;ng của mọi người&rdquo;.</div>\n<div>\n	&nbsp;</div>\n<div style=\"text-align: center;\">\n	<img alt=\"ABC\" src=\"/img/ATM2--copy-6e22f.gif\" /></div>\n<div style=\"text-align: center;\">\n	&nbsp;</div>\n<div>\n	Chương tr&igrave;nh Đại sứ LienVietPostBank với th&ocirc;ng điệp &ldquo;Triệu qu&agrave; tặng - một niềm tin&rdquo; &aacute;p dụng cho c&aacute;c kh&aacute;ch h&agrave;ng c&aacute; nh&acirc;n, k&eacute;o d&agrave;i từ nay đến 25/1/2014 hoặc đến khi c&oacute; th&ocirc;ng b&aacute;o chấm dứt chương tr&igrave;nh từ LienVietPostBank.&nbsp;</div>\n<div>\n	&nbsp;</div>\n<div>\n	Ngay khi trở th&agrave;nh kh&aacute;ch h&agrave;ng của LienVietPostBank, kh&aacute;ch h&agrave;ng sẽ được ghi nhận trở th&agrave;nh Đại sứ LienVietPostBank v&agrave; hưởng nhiều ưu đ&atilde;i v&agrave; quyền lợi m&agrave; chương tr&igrave;nh mang lại. LienVietPostBank mong muốn kh&aacute;ch h&agrave;ng nhận biết r&otilde; ưu thế khi trở th&agrave;nh đại sứ của ng&acirc;n h&agrave;ng, từ đ&oacute; khuyến kh&iacute;ch kh&aacute;ch h&agrave;ng thường xuy&ecirc;n sử dụng sản phẩm, dịch vụ cũng như t&iacute;ch cực hơn trong hoạt động truyền th&ocirc;ng quảng b&aacute; thương hiệu ng&acirc;n h&agrave;ng.&nbsp;</div>\n<div>\n	&nbsp;</div>\n<div>\n	Cụ thể, bằng việc sử dụng một hoặc c&aacute;c sản phẩm - dịch vụ như sản phẩm tiền gửi kh&ocirc;ng kỳ hạn, sản phẩm tiền gửi c&oacute; kỳ hạn, sản phẩm t&iacute;n dụng, sản phẩm thấu chi, dịch vụ thu hộ tiền điện v&agrave; dịch vụ thẻ v&agrave; ng&acirc;n h&agrave;ng điện tử, kh&aacute;ch h&agrave;ng sẽ được tặng điểm thưởng v&agrave; điểm thưởng n&agrave;y cho ph&eacute;p quy đổi để lấy c&aacute;c qu&agrave; tặng, bao gồm thẻ điện thoại/voucher mua h&agrave;ng hoặc tiền mặt. Ngo&agrave;i ra, LienVietPostBank c&ograve;n d&agrave;nh tặng điểm cho kh&aacute;ch h&agrave;ng nh&acirc;n dịp sinh nhật hoặc khi giới thiệu kh&aacute;ch h&agrave;ng mới cho LienVietPostBank.</div>\n<div>\n	&nbsp;</div>\n<div>\n	Đặc biệt, ngo&agrave;i việc tặng điểm cho kh&aacute;ch h&agrave;ng theo c&aacute;c nội dung đ&atilde; n&ecirc;u, Đại sứ LienVietPostBank c&ograve;n &aacute;p dụng ch&iacute;nh s&aacute;ch điểm thưởng theo hạng. Với ch&iacute;nh s&aacute;ch n&agrave;y, khi kh&aacute;ch h&agrave;ng đạt một số điểm nhất định, với mỗi điểm t&iacute;ch lũy th&ecirc;m chương tr&igrave;nh sẽ tặng một số điểm thưởng tương ứng theo hạng của kh&aacute;ch h&agrave;ng đang đạt được.&nbsp;</div>\n<div>\n	&nbsp;</div>\n<div>\n	H&igrave;nh thức tặng điểm theo hạng được t&iacute;nh theo cơ chế tặng bậc thang, do đ&oacute; kh&aacute;ch h&agrave;ng c&agrave;ng sử dụng nhiều sản phẩm &ndash; dịch vụ, số điểm thưởng th&ecirc;m c&agrave;ng lớn. C&oacute; 5 hạng, gồm: Chuẩn, Bạc, V&agrave;ng, Bạch Kim, Kim Cương.&nbsp;</div>\n<div>\n	&nbsp;</div>\n<div>\n	Đặc biệt nhất, LienVietPostBank kh&ocirc;ng &aacute;p dụng điều kiện giảm hạng ngay cả khi kh&aacute;ch h&agrave;ng kh&ocirc;ng thường xuy&ecirc;n thực hiện giao dịch hoặc khi quy đổi hết số điểm đ&atilde; đạt được để lấy qu&agrave; tặng. Khi đ&atilde; đạt được hạng cao nhất, kh&aacute;ch h&agrave;ng sẽ được hưởng tỷ lệ thưởng điểm cao nhất trong suốt thời gian diễn ra chương tr&igrave;nh c&ograve;n lại.</div>\n<div>\n	&nbsp;</div>\n<div>\n	Để tra cứu số điểm đ&atilde; t&iacute;ch lũy, ngo&agrave;i c&aacute;c k&ecirc;nh truyền thống, Đại sứ LienVietPostBank c&ograve;n hỗ trợ tra cứu điểm thưởng qua dịch vụ Internet Banking, SMS, gọi điện tới trung t&acirc;m dịch vụ kh&aacute;ch h&agrave;ng: 1800 57 77 58. Kh&aacute;ch h&agrave;ng đăng k&yacute; dịch vụ SMS Banking cũng được nhận th&ocirc;ng b&aacute;o số điểm đ&atilde; đạt được theo định kỳ (2 th&aacute;ng/lần).&nbsp;</div>\n<div>\n	&nbsp;</div>\n<div>\n	Kh&aacute;ch h&agrave;ng c&oacute; thể thực hiện đổi qu&agrave; qua dịch vụ Internet Banking hoặc c&aacute;c điểm giao dịch của LienVietPostBank tr&ecirc;n to&agrave;n quốc. Với chương tr&igrave;nh n&agrave;y, LienVietPostBank l&agrave; một trong những ng&acirc;n h&agrave;ng đầu ti&ecirc;n triển khai chương tr&igrave;nh chăm s&oacute;c kh&aacute;ch h&agrave;ng to&agrave;n diện từ huy động, t&iacute;n dụng đến c&aacute;c dịch vụ kh&aacute;c tr&ecirc;n to&agrave;n hệ thống.</div>\n<div>\n	&nbsp;</div>\n<div>\n	(Nguồn: LienVietPostBank)</div>\n<div>\n	&nbsp;</div>', '', '', '', '0', null, '0', 'ATM2--copy-6e22f.gif', '1', '0', null, null, '0', '0', '', '0', 'img/news/', '0', '0', null, null, '2013-07-29 09:05:55', null, null, null, null, null, null, null, null, null, null, null, null, null, null, null);
INSERT INTO articles VALUES ('356', null, '71', null, '355', '0', 'NEWS00355', 'Kênh Giáo Dục - Đỗ thủ khoa là mục tiêu của cậu bé mồ côi', 'kenh-giao-duc-do-thu-khoa-la-muc-tieu-cua-cau-be-mo-coi', '(Kênh Giáo Dục) - Thủ khoa đại học bị mồ côi từ nhỏ trở thành tâm điểm giáo dục trong đợt tuyển sinh đại học năm 2013. Qua quá trình tìm hiểu hoàn cảnh của các tân sinh viên là thủ khoa các trường của các pv Kênh Giáo DỤc, chúng tôi không khỏi đi từ ngạc nhiên này đến ngạc nhiên khác khi biết được rất nhiều thủ khoa có hoàn cảnh đặc biệt khó khăn mồ côi từ nhỏ.', '<div style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 17px; text-align: justify; background-color: rgb(250, 250, 250);\">\n	<strong>(K&ecirc;nh Gi&aacute;o Dục) -&nbsp;<a href=\"http://kenhgiaoduc.vn/\" style=\"text-decoration: none; color: rgb(0, 128, 0); outline: none;\" title=\"thu khoa dai hoc\" type=\"thu khoa dai hoc\">Thủ khoa đại học</a>&nbsp;bị mồ c&ocirc;i từ nhỏ trở th&agrave;nh t&acirc;m điểm gi&aacute;o dục trong đợt tuyển sinh đại học năm 2013. Qua qu&aacute; tr&igrave;nh t&igrave;m hiểu ho&agrave;n cảnh của c&aacute;c t&acirc;n sinh vi&ecirc;n l&agrave; thủ khoa c&aacute;c trường của c&aacute;c pv K&ecirc;nh Gi&aacute;o DỤc, ch&uacute;ng t&ocirc;i kh&ocirc;ng khỏi đi từ ngạc nhi&ecirc;n n&agrave;y đến ngạc nhi&ecirc;n kh&aacute;c khi biết được rất nhiều thủ khoa c&oacute; ho&agrave;n cảnh đặc biệt kh&oacute; khăn mồ c&ocirc;i từ nhỏ.</strong></div>\n<div style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 17px; text-align: justify; background-color: rgb(250, 250, 250);\">\n	&nbsp;</div>\n<div style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 17px; text-align: justify; background-color: rgb(250, 250, 250);\">\n	&nbsp;L&agrave; học sinh chuy&ecirc;n To&aacute;n nhưng cũng học rất giỏi m&ocirc;n Vật L&yacute;, Nguyễn Ho&agrave;ng Nam thi khối A1 v&agrave; đỗ&nbsp;<a href=\"http://kenhgiaoduc.vn/Nha-Giao/Giao-duc-24h/Do-thu-khoa-la-muc-tieu-cua-cau-be-mo-coi.aspx\" style=\"text-decoration: none; color: rgb(0, 128, 0); outline: none;\" title=\"thủ khoa dai hoc banh khoa tp hcm\" type=\"thủ khoa dai hoc banh khoa tp hcm\">thủ khoa ĐH B&aacute;ch khoa TPHCM</a>&nbsp;với 29 điểm. Trước đ&oacute;, cậu học tr&ograve; trường chuy&ecirc;n L&ecirc; Hồng Phong (TPHCM) đ&atilde; đặt mục ti&ecirc;u đỗ thủ khoa để l&agrave;m m&oacute;n qu&agrave; tặng mẹ.</div>\n<div style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 17px; text-align: justify; background-color: rgb(250, 250, 250);\">\n	&nbsp;</div>\n<div style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 17px; text-align: justify; background-color: rgb(250, 250, 250);\">\n	<strong>Tự nhận m&igrave;nh l&agrave; &ldquo;mọt s&aacute;ch&rdquo;</strong></div>\n<div style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 17px; text-align: justify; background-color: rgb(250, 250, 250);\">\n	&nbsp;</div>\n<div style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 17px; text-align: justify; background-color: rgb(250, 250, 250);\">\n	Bước v&agrave;o năm học 12, đối với cậu học tr&ograve; trường chuy&ecirc;n L&ecirc; Hồng Phong, đ&acirc;y l&agrave; giai đoạn nước r&uacute;t để t&iacute;ch lũy kiến thức đề vượt vũ m&ocirc;n. Trong đợt thi khối A1, &aacute;p lực lớn nhất với Nam ch&iacute;nh l&agrave; m&ocirc;n tiếng Anh. V&igrave; học chuy&ecirc;n To&aacute;n lại đam m&ecirc; m&ocirc;n Vật l&yacute; n&ecirc;n Nam cũng kh&aacute; y&ecirc;n t&acirc;m với vốn kiến thức ở hai m&ocirc;n n&agrave;y, thay v&agrave;o đ&oacute; em ưu ti&ecirc;n thời gian d&agrave;nh để &ocirc;n thi tiếng Anh hơn.</div>\n<div style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 17px; text-align: justify; background-color: rgb(250, 250, 250);\">\n	&nbsp;</div>\n<div style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 17px; text-align: justify; background-color: rgb(250, 250, 250);\">\n	&nbsp;</div>\n<div style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 17px; text-align: justify; background-color: rgb(250, 250, 250);\">\n	&nbsp;</div>\n<div style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 17px; text-align: justify; background-color: rgb(250, 250, 250);\">\n	Trước kỳ thi đại học, Ho&agrave;ng Nam cũng đặt mục ti&ecirc;u của m&igrave;nh l&agrave; đạt danh hiệu thủ khoa. &ldquo;Em d&agrave;nh rất nhiều thời gian để đầu tư cho c&aacute;c m&ocirc;n thi đại học năm nay. Em cố gắng đạt thủ khoa để l&agrave;m m&oacute;n qu&agrave; tặng mẹ. Trước đ&oacute;, mẹ em khuy&ecirc;n rằng, thủ khoa hay kh&ocirc;ng quan trọng, quan trọng nhất l&agrave; phải cố gắng hết sức l&agrave; được&rdquo;, Nam chia sẻ.</div>\n<div style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 17px; text-align: justify; background-color: rgb(250, 250, 250);\">\n	&nbsp;</div>\n<div style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 17px; background-color: rgb(250, 250, 250); text-align: center;\">\n	<img alt=\"Thủ khoa đại học Bách khoa thành phố HCM - ảnh : tuyen sinh kenh giao duc\" longdesc=\"http://kenhgiaoduc.vn/Nha-Giao/Giao-duc-24h/Th%E1%BB%A7%20khoa%20%C4%91%E1%BA%A1i%20h%E1%BB%8Dc%20B%C3%A1ch%20khoa%20th%C3%A0nh%20ph%E1%BB%91%20HCM%20-%20%E1%BA%A3nh%20:%20tuyen%20sinh%20kenh%20giao%20duc\" src=\"http://kenhgiaoduc.vn/Data/upload/images/thu-khoa-dai-hoc-bach-khoa-tp-hcm.jpg\" style=\"border: none; width: 448px; height: 252px;\" title=\"Thủ khoa đại học Bách khoa thành phố HCM - ảnh : tuyen sinh kenh giao duc\" /></div>\n<div style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 17px; text-align: justify; background-color: rgb(250, 250, 250);\">\n	&nbsp;</div>\n<div style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 17px; text-align: justify; background-color: rgb(250, 250, 250);\">\n	Kết quả l&agrave; Nam đạt 10 điểm tr&ograve;n m&ocirc;n To&aacute;n, Vật l&yacute; 9,75 điểm v&agrave; m&ocirc;n tiếng Anh đạt 9 điểm. D&ugrave; đặt mục ti&ecirc;u trước nhưng Nam cũng kh&ocirc;ng khỏi bất ngờ về th&agrave;nh t&iacute;ch thủ khoa của m&igrave;nh. &ldquo;Em rất vui mừng, nhưng cũng bất ngờ khi đạt thủ khoa, em muốn tặng m&oacute;n qu&agrave; n&agrave;y đến mẹ đầu ti&ecirc;n. T&igrave;nh y&ecirc;u thương mẹ d&agrave;nh tất cả cho em, n&ecirc;n đ&oacute; cũng ch&iacute;nh l&agrave; động lực để em cố gắng trong học tập. Em muốn mẹ tự h&agrave;o về người con của m&igrave;nh&rdquo;, &nbsp;Nam vui sướng cho biết.</div>\n<div style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 17px; text-align: justify; background-color: rgb(250, 250, 250);\">\n	Nam chia sẻ th&ecirc;m: &ldquo;Em c&ograve;n đăng k&yacute; thi khối D v&agrave;o ng&agrave;nh Kinh doanh quốc tế, trường ĐH Kinh tế - Luật TPHCM. Thi m&ocirc;n To&aacute;n v&agrave; Vật l&iacute; th&igrave; em kh&aacute; tự tin nhưng m&ocirc;n Văn th&igrave; kh&ocirc;ng chắc lắm&rdquo;.</div>\n<div style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 17px; text-align: justify; background-color: rgb(250, 250, 250);\">\n	&nbsp;</div>\n<div style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 17px; text-align: justify; background-color: rgb(250, 250, 250);\">\n	T&acirc;m sự về quyết định chọn ng&agrave;nh X&acirc;y dựng của m&igrave;nh, Nam chia sẻ: &ldquo;M&atilde;i đến đầu năm học 12, em mới hạ quyết t&acirc;m thi v&agrave;o ng&agrave;nh x&acirc;y dựng, d&ugrave; đ&acirc;y l&agrave; ước mơ từ trước của em&rdquo;. Ho&agrave;ng Nam cho biết m&igrave;nh bắt đầu th&iacute;ch ng&agrave;nh X&acirc;y dựng từ những lần xem c&aacute;c chương tr&igrave;nh tr&ecirc;n ti-vi l&uacute;c b&eacute;. Cậu học tr&ograve; Ho&agrave;ng Nam khi ấy rất th&iacute;ch những m&aacute;y m&oacute;c v&agrave; c&aacute;ch người ta tạo ra những c&ocirc;ng tr&igrave;nh x&acirc;y dựng thật vĩ đại v&agrave; độc đ&aacute;o.</div>\n<div style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 17px; text-align: justify; background-color: rgb(250, 250, 250);\">\n	&nbsp;</div>\n<div style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 17px; text-align: justify; background-color: rgb(250, 250, 250);\">\n	&ldquo;Ước mơ trở th&agrave;nh kỹ sư x&acirc;y dựng thai ngh&eacute;n từ đ&oacute;. Thế nhưng, trong giai đoạn học lớp 10, 11, em &nbsp;lại rất th&iacute;ch ng&agrave;nh Sư phạm. Rất kh&oacute; khăn nhưng em đ&atilde; c&oacute; sự chọn lựa l&agrave; chỉ thi một ng&agrave;nh th&ocirc;i, l&agrave; x&acirc;y dựng. Mẹ rất ủng hộ sự lựa chọn của em&rdquo;, Nam chia sẻ về con đường đến với ng&agrave;nh X&acirc;y dựng của m&igrave;nh.</div>\n<div style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 17px; text-align: justify; background-color: rgb(250, 250, 250);\">\n	&nbsp;</div>\n<div style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 17px; text-align: justify; background-color: rgb(250, 250, 250);\">\n	Nam thừa nhận m&igrave;nh cũng c&oacute; thể gọi l&agrave; &ldquo;mọt s&aacute;ch&rdquo;. Hầu như to&agrave;n bộ thời gian Nam d&agrave;nh cho việc học tập. Nam rất th&iacute;ch đọc t&agrave;i liệu li&ecirc;n quan đến Vật L&yacute;.</div>\n<div style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 17px; text-align: justify; background-color: rgb(250, 250, 250);\">\n	&nbsp;</div>\n<div style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 17px; background-color: rgb(250, 250, 250); text-align: center;\">\n	<img alt=\"Hoàng Nam -thủ khoa trường đại học bách khoa thành phố HCM năm 2013\" longdesc=\"http://kenhgiaoduc.vn/Nha-Giao/Giao-duc-24h/Ho%C3%A0ng%20Nam%20-th%E1%BB%A7%20khoa%20tr%C6%B0%E1%BB%9Dng%20%C4%91%E1%BA%A1i%20h%E1%BB%8Dc%20b%C3%A1ch%20khoa%20th%C3%A0nh%20ph%E1%BB%91%20HCM%20n%C4%83m%202013\" src=\"http://kenhgiaoduc.vn/Data/upload/images/hoang-nam-thu-khoa-dh-bach-khoa.jpg\" style=\"border: none; width: 448px; height: 252px;\" title=\"Hoàng Nam -thủ khoa trường đại học bách khoa thành phố HCM năm 2013\" /></div>\n<div style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 17px; text-align: justify; background-color: rgb(250, 250, 250);\">\n	&nbsp;</div>\n<div style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 17px; text-align: justify; background-color: rgb(250, 250, 250);\">\n	Ch&agrave;ng thủ khoa với gương mặt hiền hậu cũng sở hữu một bảng th&agrave;nh t&iacute;ch học tập rất đ&aacute;ng nể. Nam đạt giải V&agrave;ng m&ocirc;n To&aacute;n tại kỳ thi Olympic Truyền thống 30/4 năm lớp 10, 11. Cũng trong năm học lớp 11, Nam đạt giải Nhất m&ocirc;n To&aacute;n cấp th&agrave;nh phố v&agrave; giải Khuyến kh&iacute;ch cấp quốc gia. Tuyệt vời hơn, trong năm học 12, Nam c&ugrave;ng l&uacute;c đạt giải Nhất m&ocirc;n To&aacute;n v&agrave; m&ocirc;n Vật l&yacute; cấp th&agrave;nh phố. 12 năm liền em đạt danh hiệu học sinh giỏi to&agrave;n diện.</div>\n<div style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 17px; text-align: justify; background-color: rgb(250, 250, 250);\">\n	&nbsp;</div>\n<div style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 17px; text-align: justify; background-color: rgb(250, 250, 250);\">\n	<strong>Học tập tốt để l&agrave;m m&oacute;n qu&agrave; tặng mẹ</strong></div>\n<div style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 17px; text-align: justify; background-color: rgb(250, 250, 250);\">\n	&nbsp;</div>\n<div style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 17px; text-align: justify; background-color: rgb(250, 250, 250);\">\n	Mọi người c&agrave;ng cảm phục hơn về ch&agrave;ng trai ham học Ho&agrave;ng Nam, khi biết rằng em mồ c&ocirc;i cha từ khi c&ograve;n trong bụng mẹ mới được 1 th&aacute;ng. Trước kia, bố của em l&agrave;m việc trong ng&agrave;nh C&ocirc;ng an l&agrave;m việc ở TPHCM. Nhưng sau một tai nạn giao th&ocirc;ng đ&aacute;ng tiếc, người cha th&acirc;n y&ecirc;u của em đ&atilde; m&atilde;i m&atilde;i ra đi. Thiếu vắng cha, Ho&agrave;ng Nam sinh ra v&agrave; lớn l&ecirc;n trong t&igrave;nh y&ecirc;u thương bao la của mẹ. Thấu hiểu g&aacute;nh nặng của mẹ khi một m&igrave;nh nu&ocirc;i con, Nam như &yacute; thức được rằng m&igrave;nh ch&iacute;nh l&agrave; người đ&agrave;n &ocirc;ng trong gia đ&igrave;nh, l&agrave; chỗ dựa v&agrave; cũng ch&iacute;nh l&agrave; cuộc sống của mẹ. Điều đ&oacute; c&agrave;ng khiến em quyết t&acirc;m học h&agrave;nh, l&agrave; người con ngoan hiền để mẹ được vui l&ograve;ng. V&agrave; th&agrave;nh t&iacute;ch thủ khoa l&agrave; m&oacute;n qu&agrave; thật &yacute; nghĩa m&agrave; em d&agrave;nh tặng người mẹ k&iacute;nh y&ecirc;u. Trong cuộc sống hằng ng&agrave;y, mọi t&acirc;m sự Ho&agrave;ng Nam đều kể hết với mẹ. Những c&ocirc;ng việc hay quyết định g&igrave; quan trọng, Nam cũng nhờ sự tư vấn của mẹ. Trong kỳ thi vừa qua, mẹ rất ủng hộ sự chọn lựa v&agrave; tin tưởng v&agrave;o quyết định của Nam.</div>\n<div style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 17px; text-align: justify; background-color: rgb(250, 250, 250);\">\n	&nbsp;</div>\n<div style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 17px; text-align: justify; background-color: rgb(250, 250, 250);\">\n	&nbsp;</div>\n<div style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 17px; text-align: justify; background-color: rgb(250, 250, 250);\">\n	B&ecirc;n cạnh việc học tập, thật th&uacute; vị hơn nữa khi biết rằng, Nam l&agrave; một &ldquo;c&acirc;y Guitar&rdquo; c&oacute; tiếng trong lớp. Sau mỗi giờ học căng thẳng, cậu học tr&ograve; t&igrave;m đến những giai điệu nhẹ nh&agrave;ng v&agrave; s&acirc;u lắng c&ugrave;ng chiếc đ&agrave;n Guitar của m&igrave;nh. Tr&ecirc;n lớp, Nam cũng thường xuy&ecirc;n c&oacute; những m&agrave;n tr&igrave;nh diễn &ldquo;c&acirc;y nh&agrave; l&aacute; vườn&rdquo; để phục vụ phong tr&agrave;o văn nghệ, v&agrave; cũng ki&ecirc;m lu&ocirc;n một v&eacute; trong tốp ca của lớp tham gia văn nghệ cấp trường.</div>\n<div style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 17px; text-align: justify; background-color: rgb(250, 250, 250);\">\n	&nbsp;</div>\n<div style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 17px; text-align: justify; background-color: rgb(250, 250, 250);\">\n	Nam bật m&iacute;, em học đ&agrave;n Guitar từ năm học lớp 3, sau đ&oacute; nghỉ một thời gian đến năm lớp 6 mới học lại nhưng Nam tự học l&agrave; ch&iacute;nh. Ngo&agrave;i ra, cậu bạn cũng rất th&iacute;ch bơi lội, đ&oacute; l&agrave; m&ocirc;n r&egrave;n luyện sức khỏe rất tốt.</div>\n<div style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 17px; text-align: justify; background-color: rgb(250, 250, 250);\">\n	&nbsp;</div>\n<div style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 17px; background-color: rgb(250, 250, 250); text-align: center;\">\n	<img alt=\"Mẹ của thủ khoa rất tự hào về con trai của mình\" longdesc=\"http://kenhgiaoduc.vn/Nha-Giao/Giao-duc-24h/M%E1%BA%B9%20c%E1%BB%A7a%20th%E1%BB%A7%20khoa%20r%E1%BA%A5t%20t%E1%BB%B1%20h%C3%A0o%20v%E1%BB%81%20con%20trai%20c%E1%BB%A7a%20m%C3%ACnh\" src=\"http://kenhgiaoduc.vn/Data/upload/images/me-cua-thu-khoa.jpg\" style=\"border: none; width: 299px; height: 448px;\" title=\"Mẹ của thủ khoa rất tự hào về con trai của mình\" /></div>\n<div style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 17px; text-align: justify; background-color: rgb(250, 250, 250);\">\n	&nbsp;</div>\n<div style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 17px; text-align: justify; background-color: rgb(250, 250, 250);\">\n	C&ocirc; L&ecirc; Thị Tư - mẹ Nam, chia sẻ: &ldquo;Gia đ&igrave;nh rất vui v&igrave; ch&aacute;u Nam lu&ocirc;n cố gắng học tập tốt v&agrave; l&agrave; người con ngoan hiền. T&ocirc;i lu&ocirc;n ủng hộ v&agrave; d&otilde;i theo bước ch&acirc;n của Nam tr&ecirc;n con đường thực hiện ước mơ của m&igrave;nh&rdquo;.</div>\n<div style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 17px; text-align: justify; background-color: rgb(250, 250, 250);\">\n	&nbsp;</div>\n<div style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 17px; text-align: justify; background-color: rgb(250, 250, 250);\">\n	Khi v&agrave;o học lớp 10, giấc mơ du học cứ lớn dần l&ecirc;n trong suy nghĩ của cậu học tr&ograve; chuy&ecirc;n To&aacute;n với niềm tin ở nước ngo&agrave;i c&oacute; m&ocirc;i trường học tập rất tốt. N&oacute;i về dự định sắp tới khi bước ch&acirc;n v&agrave;o trường đại học, Nam cho biết: &ldquo;Nh&agrave; em sẽ mua m&aacute;y t&iacute;nh v&agrave; nối mạng để phục vụ việc học tập. Em cũng đ&atilde; l&ecirc;n kế hoạch đi l&agrave;m gia sư để kiếm th&ecirc;m thu nhập để hỗ trợ mẹ kinh ph&iacute; học tập. Ngo&agrave;i ra, em cũng ưu ti&ecirc;n học tiếng Anh v&agrave; cố gắng học thật tốt để c&oacute; cơ hội đi du học&rdquo;.</div>\n<div style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 17px; text-align: justify; background-color: rgb(250, 250, 250);\">\n	&nbsp;</div>\n<div style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 17px; text-align: justify; background-color: rgb(250, 250, 250);\">\n	Chia sẻ về b&iacute; quyết học tập của m&igrave;nh, Nam cho rằng m&igrave;nh cũng chẳng c&oacute; g&igrave; đặc biệt: &ldquo;Em học kỹ v&agrave; l&agrave;m hết b&agrave;i tập trong s&aacute;ch gi&aacute;o khoa, sau đ&oacute; l&agrave;m th&ecirc;m b&agrave;i tập n&acirc;ng cao. Tr&ecirc;n lớp học, Nam hay tham gia giải một số b&agrave;i tập kh&oacute; m&agrave; c&aacute;c bạn trong lớp t&igrave;m được th&igrave; c&ugrave;ng tham gia giải đề&rdquo;.</div>\n<div style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 17px; text-align: justify; background-color: rgb(250, 250, 250);\">\n	&nbsp;</div>\n<div style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 17px; text-align: justify; background-color: rgb(250, 250, 250);\">\n	Nh&agrave; Ho&agrave;ng Nam kh&ocirc;ng lắp đặt Internet, n&ecirc;n khi nghe giới thiệu một số trang mạng, diễn đ&agrave;n c&oacute; đề thi hay th&igrave; nhờ mẹ tải về, in ra cho em l&agrave;m b&agrave;i. Cả năm lớp 12, hầu như Nam kh&ocirc;ng sử dụng đến m&aacute;y t&iacute;nh v&agrave; tham gia v&agrave;o đời sống mạng. Bản th&acirc;n Nam cũng rất e ngại việc m&igrave;nh kh&ocirc;ng kiềm chế được v&agrave; dễ bị l&ocirc;i cuốn, đam m&ecirc; những tr&ograve; chơi điện tử, hay mất qu&aacute; nhiều thời gian kh&ocirc;ng tham gia v&agrave;o c&aacute;c trang mạng x&atilde; hội.</div>\n<div style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 17px; text-align: justify; background-color: rgb(250, 250, 250);\">\n	&nbsp;</div>\n<div style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 17px; text-align: justify; background-color: rgb(250, 250, 250);\">\n	K&ecirc;nh Gi&aacute;o Dục xin ch&uacute;c mừng t&acirc;n thủ khoa của Trường Đại Học B&aacute;ch Khoa Th&agrave;nh Phố Hồ Ch&iacute; Minh, một ng&ocirc;i trường nổi tiếng, l&agrave; ước mơ của rất nhiều th&iacute; sinh. Ch&uacute;c Ho&agrave;ng Nam sẽ gặt h&aacute;i được nhiều th&agrave;nh c&ocirc;ng mới.</div>\n<div style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 17px; text-align: justify; background-color: rgb(250, 250, 250);\">\n	&nbsp;</div>\n<div style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 17px; background-color: rgb(250, 250, 250); text-align: right;\">\n	<strong>K&ecirc;nh Gi&aacute;o Dục</strong></div>', '', '', '', '0', null, '0', 'hoang-nam-thu-khoa-dh-bach-khoa.jpg', '1', '0', null, null, '0', '0', '', '0', 'img/news/', '0', '0', null, null, '2013-07-29 09:10:11', null, null, null, null, null, null, null, null, null, null, null, null, null, null, null);
INSERT INTO articles VALUES ('357', null, '75', null, '356', '0', 'NEWS00356', 'Những người nào ăn nhiều bánh trung thu không tốt ?', 'nhung-nguoi-nao-an-nhieu-banh-trung-thu-khong-tot', 'Tết trung thu 2013 đang đến gần, bánh trung thu trở thành món quà đầy ý nghĩa. Nhưng chúng ta phải hết sức thận trọng với những chiếc bánh trung thu này.\n \nMặc dù mùi vị của bánh trung thu rất thơm ngon nhưng không phải ai cũng phù hợp với nó. Dưới đây là 6 nhóm người nên thận trọng khi sử dụng bánh trung thu đã được các cộng tác viên, chuyên gia sức khỏe Kênh Giáo Dục tìm hiểu từ nhiều tài liệu khác nhau.\n \n \n1. Người già và trẻ em\n \nChức năng hệ hô hấp và tiêu hóa của người già rất kém, ăn quá nhiều làm dạ dày phải làm việc quá công suất, rất dễ gây ra chứng khó tiêu, tiêu chảy.\n \nVì là món quà của con cái, người thân, bạn bè nên người ta rất tôn trọng bánh trung thu. Rất nhiều người già không nỡ vứt bánh đi, lại lo bánh bị hỏng, vì thế mà ngày nào cũng ăn rất nhiều bánh trung thu. Cách ăn này đối với người già tuyệt đối không tốt. Còn đối với trẻ em, do hệ thống tiêu hóa còn chưa hoàn thiện, cơ quan tiêu hóa còn yếu, khó tiếp nhận được một lượng lớn thực phẩm nhiều đường, nhiều chất béo. Đặc biệt đối với những trẻ em béo phì, nên hạn chế ăn bánh trung thu.\n \nBánh trung thu khoai mon\n \n2. Những người chức năng cơ thể không tốt\n \nNhững người này hay mắc bệnh, thể chất kém.Mặc dù luôn có cảm giác thèm ăn nhưng chức năng tiêu hóa lại không tốt. Vì thế bánh trung thu với họ chỉ là nếm thử, tránh ăn nhiều. Đối với những người viêm gan, thận cũng nên hạn chế ăn bánh trung thu. Nếu không sẽ gây ra sự quá tải của các cơ quan, làm bệnh càng nặng hơn.\n \nNgoài ra những bệnh nhân từng ghép nội tạng cũng không nên ăn nhiều hoặc thận trọng khi sử dụng bánh trung thu.\n \n3. Những người chức năng đường ruột không tốt\n \nVới những người bị viêm dạ dày, loét dạ dày, loét đường ruột thì việc ăn nhiều bánh trung thu là rất không tốt. Những năm gần đây, số người bị trương bụng sau khi ăn bánh trung thu tăng lên rõ rệt. Những người có hệ tiêu hóa không tốt ăn nhiều bánh trung thu không những làm cho axit trong dạ dày tiết ra quá nhiều mà còn làm tổn hại đến lớp màng nhầy vốn đã bị tổn thương. Có trường hợp còn có thể gây ra chảy máu dạ dày, chảy máu đường ruột. Ngoài ra nguy cơ này càng tăng cao đối với những người viêm tuyến tụy cấp tính.\n \nBánh trung thu hat dua\n4. Những người mắc bệnh viêm túi mật, sỏi túi mật\n \n \nNhững bệnh nhân viêm túi mật, sỏi mật phải kiêng thực phẩm nhiều dầu, trong khi đó bánh trung thu được xếp vào loại thực phẩm nhiều dầu. Những người mắc hai bệnh này nếu ăn nhiều rất có thể dẫn đến túi mật co lại, không bài tiết được, thượng vị đau dữ dội, thậm chí còn nguy hiểm đến tính mạng.\n \n5. Người mắc bệnh đái tháo đường\n \nBánh trung thu thuộc loại thực phẩm nhiều dầu, nhiều đường. Nếu nhìn ở góc độ dinh dưỡng thì giá trị dinh dưỡng trong bánh trung thu không cao. Vì thế với những bệnh nhân đái tháo đường thì tốt nhất nên tránh bánh trung thu. Trong một thời gian ngắn mà ăn quá nhiều bánh trung thu làm cho lượng đường trong máu tăng cao dẫn đến rối loạn sự trao đổi chất trong cơ thể. Khi đó bệnh đái tháo đường càng trở nên nghiêm trọng hơn.\n \n \nTrong khi đó, rất nhiều người mắc bệnh đái tháo đường tưởng rằng họ có thể ăn nhiều những loại bánh trung thu không đường. Thật ra, mặc dù những loại bánh trung thu này không trực tiếp mang đường nhưng khi ăn quá nhiều cũng đồng nghĩa với việc lượng đường trong máu tăng cao. Tất nhiên trong trường hợp bắt buộc phải ăn, tốt nhất nên chọn ăn một chút bánh trung thu không đường.\n \n6. Người mắc bệnh mạch máu não, thiếu máu cơ tim\n \nĐối với những người huyết áp cao, thiếu máu cơ tim và những người tăng lipid huyết ăn nhiều bánh trung thu sẽ dẫn đến lượng lipid huyết tăng cao, huyết áp tăng cao. Thậm chí có nguy cơ xuất hiện nhồi máu cơ tim cấp.\n \nĐối với những bệnh nhân mạch máu não thì việc ăn nhiều bánh trung thu thậm chí còn dẫn đến nguy cơ tê liệt não. Do hàm lượng dầu trong bánh trung thu lớn, thậm chí một só loại bánh có nhân bằng mỡ lợn. Những người có hàm lượng cholesterol trong máu cao cũng không nên ăn nhiều bánh trung thu\nThu Anh\nPV. Kênh Giáo Dục.VN', '<div style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 17px; text-align: justify; background-color: rgb(250, 250, 250);\">\n	Tết trung thu 2013 đang đến gần,&nbsp;<a href=\"http://banhkeoonline.com/\" style=\"text-decoration: none; color: rgb(0, 128, 0); outline: none;\" title=\"banh trung thu\" type=\"bánh trung thu\">b&aacute;nh trung thu</a>&nbsp;trở th&agrave;nh m&oacute;n qu&agrave; đầy &yacute; nghĩa. Nhưng ch&uacute;ng ta phải hết sức thận trọng với những chiếc b&aacute;nh trung thu n&agrave;y.</div>\n<div style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 17px; text-align: justify; background-color: rgb(250, 250, 250);\">\n	&nbsp;</div>\n<div style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 17px; text-align: justify; background-color: rgb(250, 250, 250);\">\n	Mặc d&ugrave; m&ugrave;i vị của b&aacute;nh trung thu rất thơm ngon nhưng kh&ocirc;ng phải ai cũng ph&ugrave; hợp với n&oacute;. Dưới đ&acirc;y l&agrave; 6 nh&oacute;m người n&ecirc;n thận trọng khi sử dụng b&aacute;nh trung thu đ&atilde; được c&aacute;c cộng t&aacute;c vi&ecirc;n, chuy&ecirc;n gia sức khỏe K&ecirc;nh Gi&aacute;o Dục t&igrave;m hiểu từ nhiều t&agrave;i liệu kh&aacute;c nhau.</div>\n<div style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 17px; text-align: justify; background-color: rgb(250, 250, 250);\">\n	&nbsp;</div>\n<div style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 17px; text-align: justify; background-color: rgb(250, 250, 250);\">\n	&nbsp;</div>\n<div style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 17px; text-align: justify; background-color: rgb(250, 250, 250);\">\n	<strong>1. Người gi&agrave; v&agrave; trẻ em</strong></div>\n<div style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 17px; text-align: justify; background-color: rgb(250, 250, 250);\">\n	&nbsp;</div>\n<div style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 17px; text-align: justify; background-color: rgb(250, 250, 250);\">\n	Chức năng hệ h&ocirc; hấp v&agrave; ti&ecirc;u h&oacute;a của người gi&agrave; rất k&eacute;m, ăn qu&aacute; nhiều l&agrave;m dạ d&agrave;y phải l&agrave;m việc qu&aacute; c&ocirc;ng suất, rất dễ g&acirc;y ra chứng kh&oacute; ti&ecirc;u, ti&ecirc;u chảy.</div>\n<div style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 17px; text-align: justify; background-color: rgb(250, 250, 250);\">\n	&nbsp;</div>\n<div style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 17px; text-align: justify; background-color: rgb(250, 250, 250);\">\n	V&igrave; l&agrave; m&oacute;n qu&agrave; của con c&aacute;i, người th&acirc;n, bạn b&egrave; n&ecirc;n người ta rất t&ocirc;n trọng b&aacute;nh trung thu. Rất nhiều người gi&agrave; kh&ocirc;ng nỡ vứt b&aacute;nh đi, lại lo b&aacute;nh bị hỏng, v&igrave; thế m&agrave; ng&agrave;y n&agrave;o cũng ăn rất nhiều b&aacute;nh trung thu. C&aacute;ch ăn n&agrave;y đối với người gi&agrave; tuyệt đối kh&ocirc;ng tốt. C&ograve;n đối với trẻ em, do hệ thống ti&ecirc;u h&oacute;a c&ograve;n chưa ho&agrave;n thiện, cơ quan ti&ecirc;u h&oacute;a c&ograve;n yếu, kh&oacute; tiếp nhận được một lượng lớn thực phẩm nhiều đường, nhiều chất b&eacute;o. Đặc biệt đối với những trẻ em b&eacute;o ph&igrave;, n&ecirc;n hạn chế ăn b&aacute;nh trung thu.</div>\n<div style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 17px; text-align: justify; background-color: rgb(250, 250, 250);\">\n	&nbsp;</div>\n<div style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 17px; background-color: rgb(250, 250, 250); text-align: center;\">\n	<img alt=\"Bánh trung thu khoai mon\" longdesc=\"http://kenhgiaoduc.vn/Xa-Hoi/Suc-khoe/B%C3%A1nh%20trung%20thu%20khoai%20mon\" src=\"http://kenhgiaoduc.vn/Data/upload/images/banh%20trung%20thu%20khoai%20mon.jpg\" style=\"border: none; width: 300px; height: 243px;\" title=\"Bánh trung thu khoai mon\" /></div>\n<div style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 17px; text-align: justify; background-color: rgb(250, 250, 250);\">\n	&nbsp;</div>\n<div style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 17px; text-align: justify; background-color: rgb(250, 250, 250);\">\n	<strong>2. Những người chức năng cơ thể kh&ocirc;ng tốt</strong></div>\n<div style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 17px; text-align: justify; background-color: rgb(250, 250, 250);\">\n	&nbsp;</div>\n<div style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 17px; text-align: justify; background-color: rgb(250, 250, 250);\">\n	Những người n&agrave;y hay mắc bệnh, thể chất k&eacute;m.Mặc d&ugrave; lu&ocirc;n c&oacute; cảm gi&aacute;c th&egrave;m ăn nhưng chức năng ti&ecirc;u h&oacute;a lại kh&ocirc;ng tốt. V&igrave; thế b&aacute;nh trung thu với họ chỉ l&agrave; nếm thử, tr&aacute;nh ăn nhiều. Đối với những người vi&ecirc;m gan, thận cũng n&ecirc;n hạn chế ăn b&aacute;nh trung thu. Nếu kh&ocirc;ng sẽ g&acirc;y ra sự qu&aacute; tải của c&aacute;c cơ quan, l&agrave;m bệnh c&agrave;ng nặng hơn.</div>\n<div style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 17px; text-align: justify; background-color: rgb(250, 250, 250);\">\n	&nbsp;</div>\n<div style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 17px; text-align: justify; background-color: rgb(250, 250, 250);\">\n	Ngo&agrave;i ra những bệnh nh&acirc;n từng gh&eacute;p nội tạng cũng kh&ocirc;ng n&ecirc;n ăn nhiều hoặc thận trọng khi sử dụng b&aacute;nh trung thu.</div>\n<div style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 17px; text-align: justify; background-color: rgb(250, 250, 250);\">\n	&nbsp;</div>\n<div style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 17px; text-align: justify; background-color: rgb(250, 250, 250);\">\n	<strong>3. Những người chức năng đường ruột kh&ocirc;ng tốt</strong></div>\n<div style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 17px; text-align: justify; background-color: rgb(250, 250, 250);\">\n	&nbsp;</div>\n<div style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 17px; text-align: justify; background-color: rgb(250, 250, 250);\">\n	Với những người bị vi&ecirc;m dạ d&agrave;y, lo&eacute;t dạ d&agrave;y, lo&eacute;t đường ruột th&igrave; việc ăn nhiều b&aacute;nh trung thu l&agrave; rất kh&ocirc;ng tốt. Những năm gần đ&acirc;y, số người bị trương bụng sau khi ăn b&aacute;nh trung thu tăng l&ecirc;n r&otilde; rệt. Những người c&oacute; hệ ti&ecirc;u h&oacute;a kh&ocirc;ng tốt ăn nhiều b&aacute;nh trung thu kh&ocirc;ng những l&agrave;m cho axit trong dạ d&agrave;y tiết ra qu&aacute; nhiều m&agrave; c&ograve;n l&agrave;m tổn hại đến lớp m&agrave;ng nhầy vốn đ&atilde; bị tổn thương. C&oacute; trường hợp c&ograve;n c&oacute; thể g&acirc;y ra chảy m&aacute;u dạ d&agrave;y, chảy m&aacute;u đường ruột. Ngo&agrave;i ra nguy cơ n&agrave;y c&agrave;ng tăng cao đối với những người vi&ecirc;m tuyến tụy cấp t&iacute;nh.</div>\n<div style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 17px; text-align: justify; background-color: rgb(250, 250, 250);\">\n	&nbsp;</div>\n<div style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 17px; background-color: rgb(250, 250, 250); text-align: center;\">\n	<img alt=\"Bánh trung thu hat dua\" longdesc=\"http://kenhgiaoduc.vn/Xa-Hoi/Suc-khoe/B%C3%A1nh%20trung%20thu%20hat%20dua\" src=\"http://kenhgiaoduc.vn/Data/upload/images/banh%20trung%20thu%20hat%20dua.jpg\" style=\"border: none; width: 300px; height: 300px;\" title=\"Bánh trung thu hat dua\" /></div>\n<div style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 17px; text-align: justify; background-color: rgb(250, 250, 250);\">\n	<strong>4. Những người mắc bệnh vi&ecirc;m t&uacute;i mật, sỏi t&uacute;i mật</strong></div>\n<div style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 17px; text-align: justify; background-color: rgb(250, 250, 250);\">\n	&nbsp;</div>\n<div style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 17px; text-align: justify; background-color: rgb(250, 250, 250);\">\n	&nbsp;</div>\n<div style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 17px; text-align: justify; background-color: rgb(250, 250, 250);\">\n	Những bệnh nh&acirc;n vi&ecirc;m t&uacute;i mật, sỏi mật phải ki&ecirc;ng thực phẩm nhiều dầu, trong khi đ&oacute; b&aacute;nh trung thu được xếp v&agrave;o loại thực phẩm nhiều dầu. Những người mắc hai bệnh n&agrave;y nếu ăn nhiều rất c&oacute; thể dẫn đến t&uacute;i mật co lại, kh&ocirc;ng b&agrave;i tiết được, thượng vị đau dữ dội, thậm ch&iacute; c&ograve;n nguy hiểm đến t&iacute;nh mạng.</div>\n<div style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 17px; text-align: justify; background-color: rgb(250, 250, 250);\">\n	&nbsp;</div>\n<div style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 17px; text-align: justify; background-color: rgb(250, 250, 250);\">\n	<strong>5. Người mắc bệnh đ&aacute;i th&aacute;o đường</strong></div>\n<div style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 17px; text-align: justify; background-color: rgb(250, 250, 250);\">\n	&nbsp;</div>\n<div style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 17px; text-align: justify; background-color: rgb(250, 250, 250);\">\n	B&aacute;nh trung thu thuộc loại thực phẩm nhiều dầu, nhiều đường. Nếu nh&igrave;n ở g&oacute;c độ dinh dưỡng th&igrave; gi&aacute; trị dinh dưỡng trong b&aacute;nh trung thu kh&ocirc;ng cao. V&igrave; thế với những bệnh nh&acirc;n đ&aacute;i th&aacute;o đường th&igrave; tốt nhất n&ecirc;n tr&aacute;nh b&aacute;nh trung thu. Trong một thời gian ngắn m&agrave; ăn qu&aacute; nhiều b&aacute;nh trung thu l&agrave;m cho lượng đường trong m&aacute;u tăng cao dẫn đến rối loạn sự trao đổi chất trong cơ thể. Khi đ&oacute; bệnh đ&aacute;i th&aacute;o đường c&agrave;ng trở n&ecirc;n nghi&ecirc;m trọng hơn.</div>\n<div style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 17px; text-align: justify; background-color: rgb(250, 250, 250);\">\n	&nbsp;</div>\n<div style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 17px; text-align: justify; background-color: rgb(250, 250, 250);\">\n	&nbsp;</div>\n<div style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 17px; text-align: justify; background-color: rgb(250, 250, 250);\">\n	Trong khi đ&oacute;, rất nhiều người mắc bệnh đ&aacute;i th&aacute;o đường tưởng rằng họ c&oacute; thể ăn nhiều những loại b&aacute;nh trung thu kh&ocirc;ng đường. Thật ra, mặc d&ugrave; những loại b&aacute;nh trung thu n&agrave;y kh&ocirc;ng trực tiếp mang đường nhưng khi ăn qu&aacute; nhiều cũng đồng nghĩa với việc lượng đường trong m&aacute;u tăng cao. Tất nhi&ecirc;n trong trường hợp bắt buộc phải ăn, tốt nhất n&ecirc;n chọn ăn một ch&uacute;t b&aacute;nh trung thu kh&ocirc;ng đường.</div>\n<div style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 17px; text-align: justify; background-color: rgb(250, 250, 250);\">\n	&nbsp;</div>\n<div style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 17px; text-align: justify; background-color: rgb(250, 250, 250);\">\n	<strong>6. Người mắc bệnh mạch m&aacute;u n&atilde;o, thiếu m&aacute;u cơ tim</strong></div>\n<div style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 17px; text-align: justify; background-color: rgb(250, 250, 250);\">\n	&nbsp;</div>\n<div style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 17px; text-align: justify; background-color: rgb(250, 250, 250);\">\n	Đối với những người huyết &aacute;p cao, thiếu m&aacute;u cơ tim v&agrave; những người tăng lipid huyết ăn nhiều b&aacute;nh trung thu sẽ dẫn đến lượng lipid huyết tăng cao, huyết &aacute;p tăng cao. Thậm ch&iacute; c&oacute; nguy cơ xuất hiện nhồi m&aacute;u cơ tim cấp.</div>\n<div style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 17px; text-align: justify; background-color: rgb(250, 250, 250);\">\n	&nbsp;</div>\n<div style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 17px; text-align: justify; background-color: rgb(250, 250, 250);\">\n	Đối với những bệnh nh&acirc;n mạch m&aacute;u n&atilde;o th&igrave; việc ăn nhiều b&aacute;nh trung thu thậm ch&iacute; c&ograve;n dẫn đến nguy cơ t&ecirc; liệt n&atilde;o. Do h&agrave;m lượng dầu trong b&aacute;nh trung thu lớn, thậm ch&iacute; một s&oacute; loại b&aacute;nh c&oacute; nh&acirc;n bằng mỡ lợn. Những người c&oacute; h&agrave;m lượng cholesterol trong m&aacute;u cao cũng kh&ocirc;ng n&ecirc;n ăn nhiều b&aacute;nh trung thu</div>\n<div style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 17px; background-color: rgb(250, 250, 250); text-align: right;\">\n	<strong>Thu Anh</strong></div>\n<div style=\"color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 17px; background-color: rgb(250, 250, 250); text-align: right;\">\n	PV. K&ecirc;nh Gi&aacute;o Dục.VN</div>', '', '', '', '0', null, '0', 'banh_trung_thu_hat_dua.jpg', '1', '0', null, null, '0', '0', '', '1', 'img/news/', '0', '0', null, null, '2013-07-29 09:11:56', '2013-07-29 09:13:10', null, null, null, null, null, null, null, null, null, null, null, null, null, null);
INSERT INTO articles VALUES ('358', null, '73', null, '357', '0', 'NEWS00357', '11 cây xăng cần di dời, chấm dứt hoạt động', '11-cay-xang-can-di-doi-cham-dut-hoat-dong', 'Chiều 1/8, Sở Cảnh sát PCCC thành phố Đà Nẵng cho hay, kết quả đợt ra quân kiểm tra các cửa hàng xăng dầu vừa qua cho thấy, có đến 11/96 cần phải', '<p>\n	<strong>Chiều 1/8, Sở Cảnh s&aacute;t PCCC th&agrave;nh phố Đ&agrave; Nẵng cho hay, kết quả đợt ra qu&acirc;n kiểm tra c&aacute;c cửa h&agrave;ng xăng dầu vừa qua cho thấy, c&oacute; đến 11/96 cần phải di dời, chấm dứt kinh doanh.</strong><br />\n	<br />\n	Đ&acirc;y l&agrave; những c&acirc;y xăng c&oacute; vị tr&iacute; nằm trong diện di dời, giải tỏa theo quy hoạch của th&agrave;nh phố; hoặc c&oacute; quy m&ocirc; v&agrave; diện t&iacute;ch qu&aacute; nhỏ kh&ocirc;ng đảm bảo an to&agrave;n ch&aacute;y nổ, vệ sinh m&ocirc;i trường. Một số cửa h&agrave;ng gần c&aacute;c c&ocirc;ng tr&igrave;nh c&ocirc;ng cộng, cần phải khắc phục để đảm bảo an to&agrave;n ph&ograve;ng chống ch&aacute;y nổ.</p>\n<p class=\"justifyfull\" style=\"text-align: justify;\">\n	<img alt=\"11 cây xăng cần di dời, chấm dứt hoạt động \" src=\"http://media.tinmoi.vn/2013/08/02/di-doi-cay-xang.jpg\" style=\"display: block; margin-left: auto; margin-right: auto;\" title=\"11 cây xăng cần di dời, chấm dứt hoạt động \" width=\"500\" /></p>\n<p style=\"text-align: center;\">\n	<span style=\"color: #0000ff;\">Ảnh minh họa (Nguồn Internet)</span></p>\n<p class=\"justifyfull\" style=\"text-align: justify;\">\n	<br />\n	Kết quả tổng kiểm tra, r&agrave; so&aacute;t cho thấy, chỉ c&oacute; 49 c&acirc;y xăng được đ&aacute;nh gi&aacute; an to&agrave;n v&agrave; ổn định; 36 cửa h&agrave;ng c&oacute; cơ sở hạ tầng kỹ thuật cần phải đầu tư, n&acirc;ng cấp v&agrave; khắc phục nhằm đảm bảo phục vụ an to&agrave;n.<br />\n	<br />\n	Theo l&atilde;nh đạo Sở Cảnh s&aacute;t PCCC Đ&agrave; Nẵng, đơn vị y&ecirc;u cầu c&aacute;c c&acirc;y xăng nghi&ecirc;m cấm đỗ xe, để h&agrave;ng khu vực c&acirc;y xăng, tuy nhi&ecirc;n t&igrave;nh trạng n&agrave;y vẫn diễn ra kh&aacute; phổ biến, nhất l&agrave; dọc tuyến QL1A, 14B.<br />\n	<br />\n	Ph&oacute; Chủ tịch UBND th&agrave;nh phố Đ&agrave; Nẵng, &ocirc;ng Ph&ugrave;ng Tấn Viết, chỉ đạo ng&agrave;nh chức năng tăng cường kiểm tra, r&agrave; so&aacute;t điều kiện kinh doanh của cửa h&agrave;ng xăng dầu. Nếu cơ sở n&agrave;o kh&ocirc;ng đảm bảo, sẽ thu hồi giấy chứng nhận kinh doanh.</p>', '0', '0', '0', '0', null, '0', 'di-doi-cay-xang.jpg', '1', '0', null, null, '0', '0', '', '0', 'img/news/', '0', '0', null, null, '2013-08-02 17:10:59', null, null, null, null, null, null, null, null, null, null, null, null, null, null, null);
INSERT INTO articles VALUES ('359', null, '73', null, '358', '0', 'NEWS00358', 'Một “ông lớn” báo online sắp xuất hiện ở Việt Nam', 'mot-ong-lon-bao-online-sap-xuat-hien-o-viet-nam', 'Trên facebook hiện đang lan truyền một fanpage khá nổi với lượng thành viên lên đến hàng nghìn người có tên Báo Đời', '<p>\n	<strong>Tr&ecirc;n facebook hiện đang lan truyền một fanpage kh&aacute; nổi với lượng th&agrave;nh vi&ecirc;n l&ecirc;n đến h&agrave;ng ngh&igrave;n người c&oacute; t&ecirc;n B&aacute;o Đời sống v&agrave; Ph&aacute;p luật. Đặc biệt, trong 1 status tr&ecirc;n fanpage n&agrave;y c&oacute; viết: &ldquo;Sắp ra mắt B&aacute;o Đời Sống &amp; Ph&aacute;p Luật phi&ecirc;n bản điện tử, với những nội dung hấp dẫn v&agrave; c&ocirc;ng nghệ nổi bật nhất&rdquo;.</strong></p>\n<p class=\"justifyfull\" style=\"text-align: justify;\">\n	Điều n&agrave;y cho thấy l&agrave;ng b&aacute;o online sắp c&oacute; th&ecirc;m một th&agrave;nh vi&ecirc;n mới, một &quot;&ocirc;ng lớn&quot; truyền th&ocirc;ng.</p>\n<table align=\"center\" border=\"0\" class=\"justifyfull\" style=\"text-align: justify;\">\n	<tbody>\n		<tr>\n			<td>\n				<p>\n					&nbsp;</p>\n				<p style=\"text-align: center;\">\n					<span style=\"color: #0000ff;\"><img alt=\"\" src=\"http://media.tinmoi.vn/2013/08/02/doi-song-phap-luat2.jpg\" width=\"500\" /> </span></p>\n				<p style=\"text-align: center;\">\n					<span style=\"color: #0000ff;\"><span>Mới xuất hiện, fanpage B&aacute;o Đời sống &amp; Ph&aacute;p luật đ&atilde; thu h&uacute;t hơn 8.000&nbsp;</span>lượt like tr&ecirc;n facebook</span></p>\n			</td>\n		</tr>\n	</tbody>\n</table>\n<p class=\"justifyfull\" style=\"text-align: justify;\">\n	Click v&agrave;o đường link tr&ecirc;n facebook, dẫn đến trang web<a href=\"http://www.tinmoi.vn/?act=share&amp;url=http://www.doisongphapluat.com/\" rel=\"noffolow\" target=\"_blank\"><strong> http://www.doisongphapluat.com/</strong></a>, ch&uacute;ng t&ocirc;i kh&aacute; bất ngờ về giao diện cũng như th&ocirc;ng tin tuyển dụng của tờ b&aacute;o online tương lai n&agrave;y.</p>\n<p class=\"justifyfull\" style=\"text-align: justify;\">\n	Giao diện được thiết kế kh&aacute; bắt mắt về h&igrave;nh ảnh v&agrave; m&agrave;u sắc, giản tiện m&agrave; ấn tượng, với những th&ocirc;ng tin đủ để gợi người đọc li&ecirc;n tưởng đến sự ra đời của một tờ b&aacute;o online mới.</p>\n<table align=\"center\" border=\"0\" class=\"justifyfull\" style=\"text-align: justify;\">\n	<tbody>\n		<tr>\n			<td>\n				<p>\n					<img alt=\"Một “ông lớn” báo online sắp xuất hiện ở Việt Nam\" src=\"http://media.tinmoi.vn/2013/07/31/hui.JPG\" title=\"Một “ông lớn” báo online sắp xuất hiện ở Việt Nam\" width=\"500\" /></p>\n				<p style=\"text-align: center;\">\n					<span style=\"color: #0000ff;\">Th&ocirc;ng b&aacute;o ng&agrave;y ra mắt của tờ b&aacute;o</span></p>\n			</td>\n		</tr>\n	</tbody>\n</table>\n<p class=\"justifyfull\" style=\"text-align: justify;\">\n	Đặc biệt l&agrave; th&ocirc;ng tin &ldquo;chỉ c&ograve;n 32 ng&agrave;y nữa&rdquo; &ndash; chỉ thời điểm ra mắt B&aacute;o Đời sống v&agrave; Ph&aacute;p luật phi&ecirc;n bản điện tử. Điều n&agrave;y đồng nghĩa với việc sau 32 ng&agrave;y nữa, l&agrave;ng b&aacute;o online sẽ đ&oacute;n ch&agrave;o th&ecirc;m một th&agrave;nh vi&ecirc;n mới.</p>\n<p class=\"justifyfull\" style=\"text-align: justify;\">\n	B&aacute;o Đời sống &amp; PL kh&ocirc;ng phải l&agrave; một c&aacute;i t&ecirc;n xa lạ g&igrave;. Đ&acirc;y l&agrave; một trong những tờ b&aacute;o lớn v&agrave; uy t&iacute;n, c&oacute; lương b&aacute;o giấy xuất bản nằm trong top 5 tại Việt Nam. Đ&acirc;y cũng l&agrave; tờ b&aacute;o đ&oacute;ng g&oacute;p nhiều tiếng n&oacute;i mạnh mẽ trong việc phản &aacute;nh c&aacute;c vấn đề ph&aacute;p l&iacute;, li&ecirc;n quan đến ph&aacute;p luật v&agrave; những vấn đề bức x&uacute;c của x&atilde; hội. Thậm ch&iacute; nhiều vấn đề ti&ecirc;u cực của x&atilde; hội cũng được b&aacute;o nhanh ch&oacute;ng cập nhật v&agrave; được độc giả hoan ngh&ecirc;nh.</p>\n<table align=\"center\" border=\"0\" class=\"justifyfull\" style=\"text-align: justify;\">\n	<tbody>\n		<tr>\n			<td>\n				<p>\n					<img alt=\"Một “ông lớn” báo online sắp xuất hiện ở Việt Nam\" src=\"http://media.tinmoi.vn/2013/07/31/agiang.JPG\" title=\"Một “ông lớn” báo online sắp xuất hiện ở Việt Nam\" width=\"500\" /></p>\n				<p style=\"text-align: center;\">\n					<span style=\"color: #0000ff;\">Giao diện tối giản, nền n&atilde; v&agrave; trang nh&atilde;</span></p>\n			</td>\n		</tr>\n	</tbody>\n</table>\n<p class=\"justifyfull\" style=\"text-align: justify;\">\n	Điều đ&aacute;ng quan t&acirc;m hơn l&agrave; phi&ecirc;n bản điện tử của Đời sống &amp; PL (http//www.doisongphapluat.com) sẽ hợp t&aacute;c với C&ocirc;ng ty truyền th&ocirc;ng Netlink. Đ&acirc;y l&agrave; một c&ocirc;ng ty chuy&ecirc;n nghiệp trong việc ph&aacute;t triển c&ocirc;ng nghệ, marketing,... tại Việt Nam. Với những thế mạnh đ&oacute;, hứa hẹn b&aacute;o Đời sống &amp; PL phi&ecirc;n bản điện tử sẽ được x&acirc;y dựng v&agrave; ph&aacute;t triển tr&ecirc;n nền tảng hiện đại, kh&aacute;c biệt so với c&aacute;c b&aacute;o điện tử hiện tại ở Việt Nam.</p>\n<p class=\"justifyfull\" style=\"text-align: justify;\">\n	B&aacute;o ch&iacute; Việt Nam hiện đang ph&aacute;t triển rất mạnh ở mảng online, với lượng th&ocirc;ng tin h&agrave;ng ng&agrave;y khổng lồ v&agrave; đầy ắp. Đ&acirc;y cũng l&agrave; xu hướng tất yếu của một nền b&aacute;o ch&iacute; hiện đại. C&oacute; lẽ ch&iacute;nh v&igrave; nhận thấy tiềm năng ở mảng online n&agrave;y, m&agrave; c&aacute;c tờ b&aacute;o giấy cũng dần quay sang &quot;chăm ch&uacute;t&quot; cho mảng online của m&igrave;nh hơn.</p>\n<p class=\"justifyfull\" style=\"text-align: justify;\">\n	Với sự g&oacute;p mặt của Đời sống &amp; PL online, nền b&aacute;o ch&iacute; Việt Nam sẽ c&oacute; th&ecirc;m nhiều tiếng n&oacute;i đa chiều, cung cấp những th&ocirc;ng tin thiết thực v&agrave; hữu &iacute;ch nhất đến cho độc giả cả nước.</p>', '0', '0', '0', '0', null, '0', 'hui_thumb_248x156.jpg', '1', '0', null, null, '0', '0', '', '1', 'img/news/', '0', '0', null, null, '2013-08-02 17:12:12', null, null, null, null, null, null, null, null, null, null, null, null, null, null, null);
INSERT INTO articles VALUES ('360', null, '73', null, '359', '0', 'NEWS00359', 'Giá vé vui chơi giải trí ở Royal City Mega Mall', 'gia-ve-vui-choi-giai-tri-o-royal-city-mega-mall', 'Royal City Mega Mall hiện đang là “cơn sốt” đối với người dân thủ đô Hà Nội. Và điều mà nhiều người quan tâm nhất hiện nay', '<p>\n	<strong>Royal City Mega Mall hiện đang l&agrave; &ldquo;cơn sốt&rdquo; đối với người d&acirc;n thủ đ&ocirc; H&agrave; Nội. V&agrave; điều m&agrave; nhiều người quan t&acirc;m nhất hiện nay l&agrave; gi&aacute; cả của c&aacute;c khu vui chơi.</strong></p>\n<p class=\"justifyfull\" style=\"text-align: justify;\">\n	&nbsp;</p>\n<table align=\"center\" border=\"0\" class=\"justifyfull\" style=\"text-align: justify;\">\n	<tbody>\n		<tr>\n			<td>\n				<p>\n					<img alt=\"Royal City Mega Mall,giá vé Royal City,giá vé công viên nước Royal City\" src=\"http://media.tinmoi.vn/2013/07/31/HUG_6894.JPG\" title=\"Royal City Mega Mall,giá vé Royal City,giá vé công viên nước Royal City\" width=\"500\" /></p>\n				<p style=\"text-align: center;\">\n					<span style=\"color: #0000ff;\">Mới chỉ mở cửa v&agrave;i ng&agrave;y, Royal City Mega Mall đ&atilde; thu h&uacute;t một lượng kh&aacute;ch &quot;khủng&quot;</span></p>\n			</td>\n		</tr>\n	</tbody>\n</table>\n<p class=\"justifyfull\" style=\"text-align: justify;\">\n	<strong>Mức gi&aacute; ni&ecirc;m yết tại khu vui chơi Royal City Mega Mall</strong></p>\n<p class=\"justifyfull\" style=\"text-align: justify;\">\n	Dưới đ&acirc;y l&agrave; gi&aacute; 1 số dịch vụ vui chơi tại Vincom Mega Mall Royal City. Gi&aacute; t&iacute;nh tr&ecirc;n 2 đối tượng: trẻ em (cao dưới 1m30) v&agrave; người lớn (cao tr&ecirc;n 1m30)</p>\n<p class=\"justifyfull\" style=\"text-align: justify;\">\n	<strong><em>C&ocirc;ng vi&ecirc;n nước: </em></strong></p>\n<p class=\"justifyfull\" style=\"text-align: justify;\">\n	Gi&aacute; v&eacute; v&agrave;o cửa: Trẻ em: 100.000 đ (từ thứ 2 đến thứ 6) v&agrave; 150.000đ (thứ 7 v&agrave; chủ nhật)</p>\n<p class=\"justifyfull\" style=\"text-align: justify;\">\n	Người lớn: 170.000 đ (từ thứ 2 đến thứ 6) v&agrave; 220.000đ (thứ 7 v&agrave; chủ nhật)</p>\n<p class=\"justifyfull\" style=\"text-align: justify;\">\n	Thu&ecirc; khăn tắm: 20.000đ/chiếc</p>\n<p class=\"justifyfull\" style=\"text-align: justify;\">\n	<strong><em>Khu vui chơi Vinpearl Games Royal City:</em></strong></p>\n<p class=\"justifyfull\" style=\"text-align: justify;\">\n	Nằm tại tầng ngầm B1 của TTTM Vincom Mega Mall Royal City, gồm khu đặt 300 m&aacute;y games, khu xe điện đụng, khu chiếu phim v&agrave; khu vui chơi trẻ e, với c&aacute;c tr&ograve; như đu quay Ho&agrave;ng gia 12 chỗ ngồi, T&agrave;u hỏa mini, Mini-car, C&acirc;u c&aacute;, N&eacute;m v&ograve;ng v&agrave;o lọ, N&eacute;m b&oacute;ng v&agrave;o cốc,&hellip;</p>\n<p class=\"justifyfull\" style=\"text-align: justify;\">\n	Gi&aacute; v&eacute; (cho cả ng&agrave;y vui chơi): Trẻ em: 100.000đ (từ thứ 2 đến thứ 6); 150.000đ (thứ 7, chủ nhật v&agrave; ng&agrave;y lễ)</p>\n<p class=\"justifyfull\" style=\"text-align: justify;\">\n	Người lớn: 170.000đ (từ thứ 2 đến thứ 6); 220.000đ (thứ 7, chủ nhật v&agrave; ng&agrave;y lễ)</p>\n<p class=\"justifyfull\" style=\"text-align: justify;\">\n	<strong><em>Khu s&acirc;n băng: th&ocirc;ng tầng B1 v&agrave; B2</em></strong></p>\n<table align=\"center\" border=\"0\" class=\"justifyfull\" style=\"text-align: justify;\">\n	<tbody>\n		<tr>\n			<td>\n				<img alt=\"\" src=\"http://media.tinmoi.vn/2013/07/30/33.JPG\" width=\"500\" /></td>\n		</tr>\n	</tbody>\n</table>\n<p class=\"justifyfull\" style=\"text-align: justify;\">\n	Gi&aacute; v&eacute;: Trẻ em: 100.000đ (thứ 2 đến thứ 6); 150.000đ (thứ 7, chủ nhật v&agrave; ng&agrave;y lễ)</p>\n<p class=\"justifyfull\" style=\"text-align: justify;\">\n	Người lớn: 170.000đ (thứ 2 đến thứ 6); 220.000 đ (thứ 7, chủ nhật v&agrave; ng&agrave;y lễ)</p>\n<p class=\"justifyfull\" style=\"text-align: justify;\">\n	Thu&ecirc; gi&agrave;y trượt: 50.000đ/1 đ&ocirc;i</p>\n<p class=\"justifyfull\" style=\"text-align: justify;\">\n	Thu&ecirc; c&ocirc;ng cụ h&igrave;nh chim c&aacute;nh cụt: 100.000đ/1phi&ecirc;n</p>\n<p class=\"justifyfull\" style=\"text-align: justify;\">\n	Thu&ecirc; c&ocirc;ng cụ h&igrave;nh hải cẩ (cho người lớn): 130.000đ/phi&ecirc;n</p>\n<p class=\"justifyfull\" style=\"text-align: justify;\">\n	<strong><em>Khu chơi bowling:</em></strong></p>\n<p class=\"justifyfull\" style=\"text-align: justify;\">\n	T&iacute;nh theo game: 1 người/10 lượt n&eacute;m: 35.000đ (thứ 2 đến thứ 6); 50.000đ (thứ 7, chủ nhật v&agrave; ng&agrave;y lễ)</p>\n<p class=\"justifyfull\" style=\"text-align: justify;\">\n	T&iacute;nh theo l&agrave;n/giờ (tối đa 7 người chơi): 175.000đ (thứ 2 &ndash; thứ 6); 250.000đ (thứ 7, chủ nhật v&agrave; ng&agrave;y lễ)</p>\n<p class=\"justifyfull\" style=\"text-align: justify;\">\n	<em><strong>Khu Kidcity:</strong></em></p>\n<p class=\"justifyfull\" style=\"text-align: justify;\">\n	1 b&eacute; cuối tuần gi&aacute; 250K, ng&agrave;y thường 200K, người lớn đi theo (ko giới hạn) l&agrave; 30K/người.</p>\n<p class=\"justifyfull\" style=\"text-align: justify;\">\n	<strong>G.G</strong></p>', '0', '0', '0', '0', null, '0', 'hug_6894_thumb_248x156.jpg', '1', '0', null, null, '0', '0', '', '1', 'img/news/', '0', '0', null, null, '2013-08-02 17:13:33', null, null, null, null, null, null, null, null, null, null, null, null, null, null, null);
INSERT INTO articles VALUES ('361', null, '74', null, '360', '0', 'NEWS00360', '7 suất học bổng toàn phần của 3 trường Đại học và Cao đẳng Hàn quốc', '7-suat-hoc-bong-toan-phan-cua-3-truong-dai-hoc-va-cao-dang-han-quoc', '(GDVN) - Ngày 7/11/2012, tại Hà nội, ông Jung Keun Kim - Chủ tich Công ty Tư vấn Du hoc Quốc tế ISEP Hàn quốc đã gặp gỡ và phỏng vấn trực tiếp các học sinh Việt nam đăng ký đi du học theo học bổng của các trường Đại học và Cao đẳng Hàn quốc.', '<p>\n	(GDVN) - Ng&agrave;y 7/11/2012, tại H&agrave; nội, &ocirc;ng Jung Keun Kim - Chủ tich C&ocirc;ng ty Tư vấn Du hoc Quốc tế ISEP H&agrave;n quốc đ&atilde; gặp gỡ v&agrave; phỏng vấn trực tiếp c&aacute;c học sinh Việt nam đăng k&yacute; đi du học theo học bổng của c&aacute;c trường Đại học v&agrave; Cao đẳng H&agrave;n quốc.</p>\n<div style=\"text-align: justify;\">\n	C&oacute; 9 học sinh &nbsp;được lựa chọn trong số h&agrave;ng chục học sinh nộp đơn đ&atilde; trả lời c&aacute;c c&acirc;u hỏi li&ecirc;n quan đến nguyện vọng &nbsp;ng&agrave;nh học cũng như c&aacute;c trường m&agrave; c&aacute;c em sẽ &nbsp;sang học tập.<br />\n	&nbsp;</div>\n<div style=\"text-align: justify;\">\n	<table align=\"center\" class=\"picBox\">\n		<tbody>\n			<tr>\n				<td style=\"text-align: center;\">\n					<img alt=\"\" src=\"http://giaoduc.net.vn/Uploaded/phamlieu/2012_11_14/du-hoc-han-quoc-giaoduc.net.vn.png\" width=\"475\" /></td>\n			</tr>\n			<tr>\n				<td class=\"desc\">\n					Ng&agrave;y 7/11/2012, tại H&agrave; nội, &ocirc;ng Jung Keun &nbsp;Kim - Chủ tich C&ocirc;ng ty Tư vấn Du hoc Quốc tế &nbsp;ISEP H&agrave;n quốc &nbsp;đ&atilde; gặp gỡ v&agrave; phỏng vấn trực tiếp c&aacute;c &nbsp;học sinh &nbsp;Việt nam đăng k&yacute; đi du học theo học bổng của c&aacute;c trường Đại học v&agrave; Cao đẳng H&agrave;n quốc.&nbsp;</td>\n			</tr>\n		</tbody>\n	</table>\n	<br />\n	&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<br />\n	Số học bổng n&agrave;y của c&aacute;c trường Đại học v&agrave; Cao đẳng H&agrave;n quốc l&agrave; kết quả m&agrave; C&ocirc;ng ty Tư vấn du học IEC Quốc anh k&yacute; kết với c&aacute;c trường nh&acirc;n &nbsp;chuyến c&ocirc;ng t&aacute;c th&aacute;p t&ugrave;ng TT Nguyễn tấn Dũng thăm ch&iacute;nh thức H&agrave;n quốc hồi th&aacute;ng 4/2012 vừa qua.<br />\n	<br />\n	Theo đ&oacute;,7 học bổng to&agrave;n phần &nbsp;của 3 trường ĐH &nbsp;l&agrave; Ansan University, Salm Yook Health University, Seokyeong University &nbsp;v&agrave; Trường Cao đẳng &nbsp;Deawon sẽ trao cho c&aacute;c em hoc sinh đạt ti&ecirc;u chuẩn th&ocirc;ng qua C&ocirc;ng ty IEC Quốc Anh. Trong số học sinh được phỏng vấn c&oacute; nhiều học sinh c&oacute; th&agrave;nh t&iacute;ch học tập xuất sắc.<br />\n	<br />\n	Một trong số l&agrave; em Đinh Ngọc Hải ở H&agrave; Nam đạt huy chương Bạc kỳ thi Olympic Vật l&yacute; ch&acirc;u &Aacute; lần th&uacute; 13 tại New Delhi, Ấn độ v&agrave; Huy chương V&agrave;ng &nbsp;tại kỳ thi Vật l&yacute; Quốc tế lần thứ 43 tại Estonia sẽ được cấp học bổng với &nbsp;ng&agrave;nh học Nano tại H&agrave;n quốc.<br />\n	&nbsp;</div>\n<div style=\"text-align: justify;\">\n	Theo nhận định của b&agrave; Đ&agrave;o Li&ecirc;n Hương, gi&aacute;m đốc c&ocirc;ng ty &nbsp;IEC Quốc Anh th&igrave; hiện nay xu hướng đi du học sang c&aacute;c nước c&oacute; nền gi&aacute;o dục ph&aacute;t triển &nbsp;ở ch&acirc;u &Aacute; như H&agrave;n quốc, Nhật bản đang được đẩy mạnh v&agrave; hấp dẫn đối với nhiều học sinh tr&ecirc;n thế giới, trong đ&oacute; c&oacute; học sinh Việt nam./.</div>\n<div style=\"text-align: justify;\">\n	&nbsp;</div>\n<div style=\"text-align: justify;\">\n	C&aacute;c bạn quan t&acirc;m c&oacute; thể ấn v&agrave;o đ&acirc;y để được tư vấn trực tuyến hoặc &nbsp;li&ecirc;n hệ với C&ocirc;ng ty Tư vấn du học Quốc Anh IEC :<br />\n	&nbsp;</div>\n<div style=\"text-align: justify;\">\n	Địa chỉ: Biệt thự B2, Phố Nguyễn Thị Thập,Trung H&ograve;a ,Thanh Xu&acirc;n, H&agrave; Nội</div>\n<div style=\"text-align: justify;\">\n	Tel : + 84 4 62816888/62818888 &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Fax : + 84 4 62819888</div>\n<p>\n	&nbsp;Email : iec1@quocanhiec.com.vn &nbsp; &nbsp; &nbsp; &nbsp; Website : www.quocanhiec.com.vn</p>', '0', '0', '0', '0', null, '0', 'du-hoc-han-quoc-giaoduc.net_.vn_.png.ashx_.png', '1', '0', null, null, '0', '0', '', '1', 'img/news/', '0', '0', null, null, '2013-08-02 17:15:26', null, null, null, null, null, null, null, null, null, null, null, null, null, null, null);
INSERT INTO articles VALUES ('362', null, '74', null, '361', '0', 'NEWS00361', 'Những nét đặc biệt của kỳ thi ĐH tại Hàn Quốc', 'nhung-net-dac-biet-cua-ky-thi-dh-tai-han-quoc', '(GDVN) - Hôm qua, 8/11, kỳ thi đại học tại Hàn Quốc đã diễn ra. Năm nay, số lượng học sinh đăng kí dự thi đại học ở nước này lên tới hơn 670.000 sĩ tử tại hơn 1.191 địa điểm thi.', '<p>\n	(GDVN) - H&ocirc;m qua, 8/11, kỳ thi đại học tại H&agrave;n Quốc đ&atilde; diễn ra. Năm nay, số lượng học sinh đăng k&iacute; dự thi đại học ở nước n&agrave;y l&ecirc;n tới hơn 670.000 sĩ tử tại hơn 1.191 địa điểm thi.</p>\n<p>\n	Theo quy định v&agrave; quan niệm của người d&acirc;n H&agrave;n Quốc, kỳ thi sẽ bắt đầu v&agrave;o ng&agrave;y thứ 5 tuần thứ 2 trong th&aacute;ng 11 h&agrave;ng năm.<br />\n	<br />\n	Th&ocirc;ng thường kỳ thi diễn ra từ 8h s&aacute;ng đến 17h chiều v&agrave; chia l&agrave;m 5 m&ocirc;n thi như sau:<br />\n	<br />\n	M&ocirc;n 1: m&ocirc;n Văn 80 ph&uacute;t<br />\n	<br />\n	M&ocirc;n 2: m&ocirc;n To&aacute;n100 ph&uacute;t<br />\n	<br />\n	M&ocirc;n 3: Tiếng Anh gồm 50 c&acirc;u hỏi, 17 mục nghe (70 ph&uacute;t)<br />\n	<br />\n	M&ocirc;n 4: C&aacute;c m&ocirc;n thuộc khối X&atilde; hội (như Đạo đức, Lịch sử, Địa l&yacute;, Ph&aacute;p luật v&agrave; X&atilde; hội, Ch&iacute;nh trị, Kinh tế, X&atilde; hội v&agrave; Văn h&oacute;a)&nbsp; hoặc c&aacute;c m&ocirc;n Khoa học (như L&yacute;, H&oacute;a, Sinh&hellip;)<br />\n	<br />\n	M&ocirc;n 5: C&aacute;c m&ocirc;n ngoại ngữ chuy&ecirc;n ng&agrave;nh như tiếng Đức, Ph&aacute;p, T&acirc;y Ban Nha, Trung Quốc, Nhật Bản, Nga, tiếng Ả Rập&hellip;<br />\n	&nbsp;</p>\n<table align=\"center\" class=\"picBox\">\n	<tbody>\n		<tr>\n			<td>\n				<img alt=\"\" src=\"http://giaoduc.net.vn/Uploaded/nguyenhai/2012_11_09/du-hoc-han-quoc-giaoduc.net.vn-1.jpg\" width=\"475\" /></td>\n		</tr>\n		<tr>\n			<td class=\"desc\">\n				&nbsp;</td>\n		</tr>\n	</tbody>\n</table>\n<table align=\"center\" class=\"picBox\">\n	<tbody>\n		<tr>\n			<td>\n				<img alt=\"\" src=\"http://giaoduc.net.vn/Uploaded/nguyenhai/2012_11_09/du-hoc-han-quoc-giaoduc.net.vn-2.jpg\" width=\"475\" /></td>\n		</tr>\n		<tr>\n			<td class=\"desc\">\n				Cầu nguyện kỳ thi may mắn</td>\n		</tr>\n	</tbody>\n</table>\n<p>\n	<br />\n	<p>\n		<br />\n		C&acirc;u hỏi của mỗi m&ocirc;n thi sẽ được những gi&aacute;o sư, gi&aacute;o vi&ecirc;n giỏi nhất quốc gia lập đề. C&aacute;c vị gi&aacute;o sư n&agrave;y bị nhốt trong những kh&aacute;ch sạn với &ocirc; cửa sổ bị b&ocirc;i đen hoặc d&aacute;n băng đen, họ phải cắt đứt ho&agrave;n to&agrave;n li&ecirc;n lạc với b&ecirc;n ngo&agrave;i v&agrave; lựa chọn c&acirc;u hỏi trong một &ldquo;ng&acirc;n h&agrave;ng c&acirc;u hỏi&rdquo; để l&agrave;m đề thi&hellip; Thời gian nhốt k&eacute;o d&agrave;i từ ng&agrave;y bắt đầu l&agrave;m đề thi cho tới khi kỳ thi ho&agrave;n to&agrave;n kết th&uacute;c.<br />\n		<br />\n		Bắt đầu từ năm 1993, kỳ thi đại học tại H&agrave;n Quốc trở n&ecirc;n nghi&ecirc;m ngặt v&agrave; trở th&agrave;nh kỳ thi quan trọng quyết định &ldquo;số phận&rdquo; của học sinh lớp 12 đồng thời được đảm bảo an to&agrave;n cao nhất.</p>\n</p>\n<p>\n	<br />\n	<p>\n		<br />\n		<table align=\"center\" class=\"picBox\">\n			<tbody>\n				<tr>\n					<td>\n						<img alt=\"\" src=\"http://giaoduc.net.vn/Uploaded/nguyenhai/2012_11_09/du-hoc-han-quoc-giaoduc.net.vn-3.jpg\" width=\"475\" /></td>\n				</tr>\n				<tr>\n					<td class=\"desc\">\n						C&aacute;c học sinh lớp dưới cổ vũ tinh thần cho đ&agrave;n anh đ&agrave;n chị</td>\n				</tr>\n			</tbody>\n		</table>\n	</p>\n</p>\n<table align=\"center\" class=\"picBox\">\n	<tbody>\n		<tr>\n			<td>\n				<img alt=\"\" src=\"http://giaoduc.net.vn/Uploaded/nguyenhai/2012_11_09/du-hoc-han-quoc-giaoduc.net.vn-4.jpg\" width=\"475\" /></td>\n		</tr>\n		<tr>\n			<td class=\"desc\">\n				&nbsp;</td>\n		</tr>\n	</tbody>\n</table>\n<table align=\"center\" class=\"picBox\">\n	<tbody>\n		<tr>\n			<td>\n				<img alt=\"\" src=\"http://giaoduc.net.vn/Uploaded/nguyenhai/2012_11_09/du-hoc-han-quoc-giaoduc.net.vn-5.jpg\" width=\"475\" /></td>\n		</tr>\n		<tr>\n			<td class=\"desc\">\n				&nbsp;</td>\n		</tr>\n	</tbody>\n</table>\n<table align=\"center\" class=\"picBox\">\n	<tbody>\n		<tr>\n			<td>\n				<img alt=\"\" src=\"http://giaoduc.net.vn/Uploaded/nguyenhai/2012_11_09/du-hoc-han-quoc-giaoduc.net.vn-6.jpg\" width=\"475\" /></td>\n		</tr>\n		<tr>\n			<td class=\"desc\">\n				&nbsp;</td>\n		</tr>\n	</tbody>\n</table>\n<table align=\"center\" class=\"picBox\">\n	<tbody>\n		<tr>\n			<td>\n				<img alt=\"\" src=\"http://giaoduc.net.vn/Uploaded/nguyenhai/2012_11_09/du-hoc-han-quoc-giaoduc.net.vn-7.jpg\" width=\"475\" /></td>\n		</tr>\n		<tr>\n			<td class=\"desc\">\n				Học sinh nhận hoa may mắn trước ng&agrave;y thi</td>\n		</tr>\n	</tbody>\n</table>\n<p>\n	<br />\n	<br />\n	V&agrave;o ng&agrave;y 8/11 h&agrave;ng năm, cả đất nước H&agrave;n Quốc tập trung to&agrave;n lực để kỳ thi diễn ra tốt đẹp. C&ocirc;ng chức được nghỉ l&agrave;m hoặc đi l&agrave;m muộn hơn so với c&aacute;c ng&agrave;y kh&aacute;c để tr&aacute;nh tắc đường của th&iacute; sinh, c&aacute;c nh&agrave; xưởng dừng hoạt động. M&aacute;y bay cũng cất c&aacute;nh v&agrave; hạ c&aacute;nh trong y&ecirc;n lặng. Cảnh s&aacute;t giao th&ocirc;ng c&oacute; mặt ở khắp mọi nơi để đưa đ&oacute;n th&iacute; sinh.<br />\n	<br />\n	Về ph&iacute;a gia đ&igrave;nh, c&aacute;c bậc cha mẹ, &ocirc;ng b&agrave; l&ecirc;n ch&ugrave;a từ sớm để cầu nguyện cho con c&aacute;i họ vượt qua kỳ thi n&agrave;y. Những ng&ocirc;i ch&ugrave;a thường xuy&ecirc;n qu&aacute; tải. Ngay cả c&aacute;c chương tr&igrave;nh truyền h&igrave;nh quốc gia cũng ph&aacute;t s&oacute;ng những b&agrave;i học nấu ăn cho c&aacute;c b&agrave; mẹ, gi&uacute;p họ l&agrave;m những m&oacute;n ăn ngon v&agrave; nhiều dinh dưỡng để bồi bổ cho con c&aacute;i họ.</p>', '0', '0', '0', '0', null, '0', 'du-hoc-han-quoc-giaoduc.net_.vn-16_.jpg.ashx_.jpg', '1', '0', null, null, '0', '0', '', '1', 'img/news/', '0', '0', null, null, '2013-08-02 17:16:23', null, null, null, null, null, null, null, null, null, null, null, null, null, null, null);
INSERT INTO articles VALUES ('363', null, '73', null, '362', '0', 'NEWS00362', 'Bắt giữ gần 100 thùng thực phẩm chức năng giả', 'bat-giu-gan-100-thung-thuc-pham-chuc-nang-gia', 'Đang ngồi đóng hộp, gắn nhãn mác cho các loại thực phẩm chức năng giả, Dũng bất ngờ bị lực lượng chức năng ập vào bắt quả tang. Kiểm tra ban đầu, phát hiện trong ngôi nhà Dũng thuê gần 100 thùng thực phẩm với các loại hộp đã đóng sẵn như sữa ong chúa, tỏi đen, vây cá mập…', '<div class=\"baiviet-TopContent200\">\n	<div class=\"baiviet-head-noidung\">\n		Đang ngồi đ&oacute;ng hộp, gắn nh&atilde;n m&aacute;c cho c&aacute;c loại thực phẩm chức năng giả, Dũng bất ngờ bị lực lượng chức năng ập v&agrave;o bắt quả tang.</div>\n</div>\n<p>\n	Kiểm tra ban đầu, ph&aacute;t hiện trong ng&ocirc;i nh&agrave; Dũng thu&ecirc; gần 100 th&ugrave;ng thực phẩm với c&aacute;c loại hộp đ&atilde; đ&oacute;ng sẵn như sữa ong ch&uacute;a, tỏi đen, v&acirc;y c&aacute; mập&hellip;</p>\n<p>\n	Khoảng 9h ng&agrave;y 2/8, đội cảnh s&aacute;t m&ocirc;i trường c&ocirc;ng quận Đống Đa phối hợp với đội quản l&yacute; thị trường số 1, ph&ograve;ng cảnh s&aacute;t m&ocirc;i trường, C&ocirc;ng an th&agrave;nh phố H&agrave; Nội ập v&agrave;o số nh&agrave; 166B, phường Trung Liệt, quận Đống Đa bắt giữ gần 100 th&ugrave;ng thực phẩm chức năng giả.</p>\n<p>\n	Khi ập v&agrave;o ng&ocirc;i nh&agrave;, cơ quan chức năng ph&aacute;t hiện Dũng đang ngồi ở tầng 1 đ&oacute;ng hộp, d&aacute;n nh&atilde;n m&aacute;c cho c&aacute;c sản phẩm chức năng. Tại đ&acirc;y, Dũng đ&atilde; kh&ocirc;ng xuất tr&igrave;nh được giấy tờ cũng như nguồn gốc của sản phẩm v&agrave; đ&atilde; thừa nhận đ&oacute; l&agrave; thực phẩm chức năng giả.</p>\n<p>\n	Chủ của của l&ocirc; h&agrave;ng tr&ecirc;n được x&aacute;c minh l&agrave; Ho&agrave;ng Nghĩa Dũng, SN 1983, qu&ecirc; ở x&atilde; Hưng Ch&iacute;nh, huyện Hưng Nguy&ecirc;n, tỉnh Nghệ An.</p>\n<p style=\"text-align: center;\">\n	<b><img alt=\"Bắt giữ gần 100 thùng thực phẩm chức năng giả, Thị trường - Tiêu dùng, thuc pham chuc nang gia, bat lo thuc pham chuc nang gia, gan nhan mac cho thuc pham chuc nang gia, lo thuc pham chuc nang gia, tin tuc, tin nhanh, tin hot, vn\" class=\"news-image\" src=\"http://img-hcm.24hstatic.com:8008/upload/3-2013/images/2013-08-02/1375428180-thuc-pham-chuc-nang-gia.jpg\" /></b></p>\n<p style=\"text-align: center; font-style: italic; color: rgb(0, 0, 255);\">\n	Đội quản l&yacute; thị trường số 1 kiểm tra sản phẩm chức năng</p>\n<p>\n	Dũng khai nhận: &ldquo;T&ocirc;i thu&ecirc; nh&agrave; của b&agrave; Khu&ecirc; v&agrave;o th&aacute;ng 12/2012 với gi&aacute; 5,2 triệu đồng/1 th&aacute;ng. T&ocirc;i thu&ecirc; nh&agrave; để kinh doanh c&aacute;c mặt h&agrave;ng như, sữa ong ch&uacute;a, Glucosamin, tảo nhật, vi c&aacute; mập, tỏi đen, colagel. Số h&agrave;ng tr&ecirc;n t&ocirc;i mua lại của một người Việt Nam tr&ecirc;n cửa khẩu Lạng Sơn v&agrave; h&agrave;ng đều do người nước ngo&agrave;i sản xuất. To&agrave;n bộ số h&agrave;ng n&agrave;y t&ocirc;i mua kh&ocirc;ng c&oacute; chứng từ g&igrave;, t&ocirc;i c&ocirc;ng nhận l&agrave; m&igrave;nh sai&rdquo;.</p>\n<p>\n	Anh Nguyễn Duy Hưng, kiểm so&aacute;t vi&ecirc;n, Đội quản l&yacute; thị trường số 1 cho biết, tại thời điểm kiểm tra chỉ c&oacute; một m&igrave;nh Dũng đang đ&oacute;ng hộp. Ng&ocirc;i nh&agrave; ba tầng, nhưng cả 1 v&agrave; tầng 2 đều d&ugrave;ng để h&agrave;ng v&agrave; đ&oacute;ng hộp, gắn nh&atilde; m&aacute;c cho sản phẩm chức năng giả.</p>\n<p>\n	Kiểm tra sơ bộ c&oacute; gần 100 hộp đựng c&aacute;c sản phẩm chức năng gồm vỏ hộp, nh&atilde;n m&aacute;c, c&aacute;c sản phẩm đ&atilde; đ&oacute;ng sẵn như sữa ong ch&uacute;a, tỏi đen, v&acirc;y c&aacute; mập, colagen...C&aacute;c loại thực phẩm n&agrave;y đều gắn nh&atilde;n m&aacute;c xuất sứ giả từ thị trường &Uacute;c, Mỹ.</p>\n<p>\n	Theo anh Hưng, trong 6 th&aacute;ng đầu năm, đội đ&atilde; bắt được 4 vụ l&agrave;m giả c&aacute;c thực phẩm chức năng. &ldquo;Tuy nhi&ecirc;n, đ&acirc;y l&agrave; vụ l&agrave;m giả thực phẩm chức năng lớn nhất kể từ đầu năm đến nay,với quy m&ocirc; gần 100 th&ugrave;ng&rdquo;, anh Hưng n&oacute;i.</p>\n<p>\n	Ghi nhận của ph&oacute;ng vi&ecirc;n, to&agrave;n bộ&nbsp; tầng 1 v&agrave; tầng 2 của ng&ocirc;i nh&agrave; đều để k&iacute;n c&aacute;c hộp chứa thực phẩm chức năng. B&ecirc;n tr&ecirc;n tầng 2, c&oacute; nhiều vỏ của hộp thực phẩm chưa đ&oacute;ng sản phẩm, tem nh&atilde;n m&aacute;c.</p>\n<p>\n	Hiện chưa c&oacute; thống k&ecirc; về gi&aacute; trị l&ocirc; h&agrave;ng thực phẩm chức năng l&agrave;m giả.</p>\n<p style=\"text-align: center;\">\n	<img alt=\"Bắt giữ gần 100 thùng thực phẩm chức năng giả, Thị trường - Tiêu dùng, thuc pham chuc nang gia, bat lo thuc pham chuc nang gia, gan nhan mac cho thuc pham chuc nang gia, lo thuc pham chuc nang gia, tin tuc, tin nhanh, tin hot, vn\" class=\"news-image\" src=\"http://img-hcm.24hstatic.com:8008/upload/3-2013/images/2013-08-02/1375428180-thuc-pham-chuc-nang-gia-2.jpg\" /></p>\n<p style=\"text-align: center; font-style: italic; color: rgb(0, 0, 255);\">\n	Lọ đựng sản phẩm chức năng chưa gắn nh&atilde;n m&aacute;c</p>\n<p style=\"text-align: center;\">\n	<img alt=\"Bắt giữ gần 100 thùng thực phẩm chức năng giả, Thị trường - Tiêu dùng, thuc pham chuc nang gia, bat lo thuc pham chuc nang gia, gan nhan mac cho thuc pham chuc nang gia, lo thuc pham chuc nang gia, tin tuc, tin nhanh, tin hot, vn\" class=\"news-image\" src=\"http://img-hcm.24hstatic.com:8008/upload/3-2013/images/2013-08-02/1375428180-thuc-pham-chuc-nang-gia-3.jpg\" /></p>\n<p style=\"text-align: center; font-style: italic; color: rgb(0, 0, 255);\">\n	Sản phẩm chức năng l&agrave;m giả</p>\n<p style=\"text-align: center;\">\n	<img alt=\"Bắt giữ gần 100 thùng thực phẩm chức năng giả, Thị trường - Tiêu dùng, thuc pham chuc nang gia, bat lo thuc pham chuc nang gia, gan nhan mac cho thuc pham chuc nang gia, lo thuc pham chuc nang gia, tin tuc, tin nhanh, tin hot, vn\" class=\"news-image\" src=\"http://img-hcm.24hstatic.com:8008/upload/3-2013/images/2013-08-02/1375428180-thuc-pham-chuc-nang-gia-4.jpg\" /></p>\n<p style=\"text-align: center; font-style: italic; color: rgb(0, 0, 255);\">\n	Sản phẩm chức năng vi c&aacute; mập</p>\n<p style=\"text-align: center;\">\n	<img alt=\"Bắt giữ gần 100 thùng thực phẩm chức năng giả, Thị trường - Tiêu dùng, thuc pham chuc nang gia, bat lo thuc pham chuc nang gia, gan nhan mac cho thuc pham chuc nang gia, lo thuc pham chuc nang gia, tin tuc, tin nhanh, tin hot, vn\" class=\"news-image\" src=\"http://img-hcm.24hstatic.com:8008/upload/3-2013/images/2013-08-02/1375428356-thuc-pham-chuc-nang-gia-5.jpg\" /></p>\n<p style=\"text-align: center; font-style: italic; color: rgb(0, 0, 255);\">\n	Sản phẩm chức năng tỏi đen</p>', '0', '0', '0', '0', null, '0', '1375428643_thuc-pham-chuc-nang-gia-8.jpg', '1', '0', null, null, '0', '0', '', '1', 'img/news/', '0', '0', null, 'thực phẩm chức năng, bắt giữ', '2013-08-02 17:51:29', '2013-08-05 11:38:29', null, null, null, null, null, null, 'Nguồn tin tuổi trẻ', null, null, null, null, null, null, null);
INSERT INTO articles VALUES ('364', null, '75', null, '363', '0', 'NEWS00363', 'Hong Kong, Singapore có chi phí mua nhà đắt nhất thế giới', 'hong-kong-singapore-co-chi-phi-mua-nha-dat-nhat-the-gioi', 'Theo báo cáo toàn cảnh phát triển thị trường BĐS toàn cầu của Knight Frank trong quý II-2013, Hong Kong và Singapore- hai thị trường có mức lạm phát giá nhà nhiều nhất trong vài năm qua thì nay lại có chi phí mua bán cao nhất trên thế giới.', '<div class=\"detailsView-contents-style\">\n	<h2 class=\"summary\" id=\"ctl29_ctl01_divSummary\">\n		Theo b&aacute;o c&aacute;o to&agrave;n cảnh ph&aacute;t triển thị trường BĐS to&agrave;n cầu của Knight Frank trong qu&yacute; II-2013, Hong Kong v&agrave; Singapore- hai thị trường c&oacute; mức lạm ph&aacute;t gi&aacute; nh&agrave; nhiều nhất trong v&agrave;i năm qua th&igrave; nay lại c&oacute; chi ph&iacute; mua b&aacute;n cao nhất tr&ecirc;n thế giới.</h2>\n	<div class=\"article-subject-content\">\n		&nbsp;</div>\n</div>\n<p>\n	Hong Kong l&agrave; nơi c&oacute; gi&aacute; nh&agrave; đắt đỏ nhất sau khi xem x&eacute;t tất cả c&aacute;c chi ph&iacute; li&ecirc;n quan giữa 15 th&agrave;nh phố được điều tra trong b&aacute;o c&aacute;o. Người kh&ocirc;ng lưu tr&uacute; sẽ c&oacute; thể phải trả 25% trong tổng gi&aacute; mua khi mua một ng&ocirc;i nh&agrave; trị gi&aacute; 3 triệu USD. Phần lớn chi ph&iacute; n&agrave;y l&agrave; ph&iacute; trước bạ v&agrave; thuế nh&agrave; đất đối với cư d&acirc;n nước ngo&agrave;i kh&ocirc;ng định cư.<br />\n	&nbsp;</p>\n<p style=\"text-align: center;\">\n	<img alt=\"\" src=\"http://file1.batdongsan.com.vn/file.303573.jpg\" style=\"width: 500px; height: 293px;\" /></p>\n<p>\n	<br />\n	Tại Singapore, cư d&acirc;n kh&ocirc;ng lưu tr&uacute; sẽ chịu mức thuế trước bạ 18%- mức cao nhất so với tất cả c&aacute;c th&agrave;nh phố tr&ecirc;n thế giới được ph&acirc;n t&iacute;ch trong b&aacute;o c&aacute;o. Mức thuế n&agrave;y tăng trong th&aacute;ng 1/2013 khi c&aacute;c nh&agrave; hoạch định ch&iacute;nh s&aacute;ch từng bước cố gắng để &ldquo;hạ nhiệt&rdquo; tốc độ tăng gi&aacute; BĐS.<br />\n	<br />\n	Do c&aacute;c ch&iacute;nh s&aacute;ch tr&ecirc;n, thị trường BĐS Hong Kong v&agrave; Singapore đều c&oacute; sự suy giảm về hoạt động mua b&aacute;n giữa những người mua nh&agrave; nước ngo&agrave;i. Số lượng người mua quốc tế giảm 24% tại Singapore trong năm 2012, trong khi tại thị trường BĐS cao cấp của Hong Kong, tỉ lệ người đại lục Trung Quốc mua nh&agrave; giảm khoảng 30% trong th&aacute;ng 10-2012 v&agrave; tới th&aacute;ng 1/2013 chỉ c&ograve;n 9%.<br />\n	<br />\n	&Ocirc;ng Oliver Knight- gi&aacute;m đốc điều h&agrave;nh v&agrave; trưởng ban bi&ecirc;n tập tại Knight Frank cho hay: &ldquo;C&oacute; khả năng nhu cầu cho thị trường cao cấp sẽ tăng, ch&uacute;ng t&ocirc;i hi vọng sẽ c&oacute; một xu hướng ph&aacute;t triển mới hướng tới nỗ lực quản l&yacute; thị trường BĐS của ch&iacute;nh phủ. C&aacute;c nh&agrave; đầu tư n&ecirc;n c&oacute; sự chuẩn bị, t&acirc;m l&yacute; sẵn s&agrave;ng cho những thay đổi về thuế tại nhiều khu vực tr&ecirc;n thế giới.&rdquo;</p>', '0', '0', '0', '0', null, '0', 'crop216x152.303573_.hongkongsingaporecochiphimuanhadatnhatthegioi_.jpg', '1', '0', null, null, '0', '0', '', '1', 'img/news/', '0', '0', null, null, '2013-08-02 17:53:18', null, null, null, null, null, null, null, null, null, null, null, null, null, null, null);
INSERT INTO articles VALUES ('365', null, '77', null, '364', '0', 'NEWS00364', 'Ngọc Ánh, Diễm My: Đụng “gout” biệt thự trắng', 'ngoc-anh-diem-my-dung-gout-biet-thu-trang', 'Từ lâu, khu biệt thự cao cấp Thảo Điền nổi tiếng là vị trí \"đắc địa\" ven sông Sài Gòn. Rất nhiều nghệ sỹ Việt đã chọn khu Thảo Điền làm nơi \"an cư lạc nghiệp\" như vợ chồng diễn viên Trương Ngọc Ánh - Trần Bảo Sơn, vợ chồng người đẹp không tuổi Diễm Mỹ, vợ chồng Tăng Thanh Hà', '<p style=\"font-family: Arial, Helvetica, sans-serif; line-height: 16px; color: rgb(51, 51, 51); text-align: justify;\">\n	Từ l&acirc;u, khu biệt thự cao cấp Thảo Điền nổi tiếng l&agrave; vị tr&iacute; &quot;đắc địa&quot; ven s&ocirc;ng S&agrave;i G&ograve;n. Rất nhiều nghệ sỹ Việt đ&atilde; chọn khu Thảo Điền l&agrave;m nơi &quot;an cư lạc nghiệp&quot; như vợ chồng diễn vi&ecirc;n Trương Ngọc &Aacute;nh - Trần Bảo Sơn, vợ chồng người đẹp kh&ocirc;ng tuổi Diễm Mỹ, vợ chồng Tăng Thanh H&agrave;...</p>\n<p style=\"font-family: Arial, Helvetica, sans-serif; line-height: 16px; color: rgb(51, 51, 51); text-align: justify;\">\n	Sớm biết đến khu đất v&agrave;ng n&agrave;y từ cuối thập ni&ecirc;n 90, người đẹp kh&ocirc;ng tuổi Diễm Mỹ sở hữu một ng&ocirc;i biệt thự 2 tầng rộng hơn 1000m<sup style=\"font-size: 12px;\">2</sup>. Trong khi đ&oacute; gia đ&igrave;nh 3 người hạnh ph&uacute;c của nữ diễn vi&ecirc;n &Aacute;o lụa H&agrave; Đ&ocirc;ng Trương Ngọc &Aacute;nh chỉ mới chuyển về ng&ocirc;i biệt thự rộng 500m<sup style=\"font-size: 12px;\">2</sup>&nbsp;từ đầu năm 2013.</p>\n<p style=\"font-family: Arial, Helvetica, sans-serif; line-height: 16px; color: rgb(51, 51, 51); text-align: justify;\">\n	N&oacute;i về hai ng&ocirc;i biệt thự một cũ một mới nổi tiếng n&agrave;y th&igrave; dễ d&agrave;ng nhận ra một điểm chung, đ&oacute; ch&iacute;nh l&agrave; m&agrave;u trắng tinh kh&ocirc;i, đầy qu&yacute; ph&aacute;i. Cả hai người đẹp Ngọc &Aacute;nh v&agrave; Diễm My đều chọn lối thiết kế hiện đại, trang nh&atilde;, h&agrave;i h&ograve;a với thi&ecirc;n nhi&ecirc;n.</p>\n<p style=\"font-family: Arial, Helvetica, sans-serif; line-height: 16px; color: rgb(51, 51, 51); text-align: justify;\">\n	Tổ ấm của mới của vợ chồng Trương Ngọc &Aacute;nh - Trần Bảo Sơn c&oacute; nhiều điểm kh&aacute;c biệt so với căn hộ chung cư cao cấp trước đ&acirc;y. Xung quanh ng&ocirc;i nh&agrave; thiết kế nhiều kh&ocirc;ng gian để thư gi&atilde;n, nghỉ ngơi như hồ c&aacute;, khu&ocirc;n vi&ecirc;n xanh, c&acirc;y cối bố tr&iacute; hợp l&yacute;. C&ocirc; con g&aacute;i cưng Bảo Ti&ecirc;n của hai người c&oacute; kh&ocirc;ng gian vui đ&ugrave;a, chạy nhảy thoải m&aacute;i nhất.</p>\n<p style=\"font-family: Arial, Helvetica, sans-serif; line-height: 16px; color: rgb(51, 51, 51); text-align: center;\">\n	<img alt=\"Ngọc Ánh, Diễm My: Đụng “gout” biệt thự trắng - 1\" class=\"news-image\" src=\"http://hcm.eva.vn/upload/3-2013/images/2013-08-04/1375612619-2.jpg\" style=\"border: 0px;\" /></p>\n<p style=\"font-family: Arial, Helvetica, sans-serif; line-height: 16px; color: rgb(0, 0, 255); font-style: italic; text-align: center;\">\n	Biệt thự trắng sang trọng của vợ chồng nữ diễn vi&ecirc;n Trương Ngọc &Aacute;nh - Trần Bảo Sơn.</p>\n<p style=\"font-family: Arial, Helvetica, sans-serif; line-height: 16px; color: rgb(51, 51, 51); text-align: center;\">\n	<img alt=\"Ngọc Ánh, Diễm My: Đụng “gout” biệt thự trắng - 2\" class=\"news-image\" src=\"http://hcm.eva.vn/upload/3-2013/images/2013-08-04/1375612619-5.jpg\" style=\"border: 0px;\" /></p>\n<p style=\"font-family: Arial, Helvetica, sans-serif; line-height: 16px; color: rgb(0, 0, 255); font-style: italic; text-align: center;\">\n	To&agrave;n bộ khu biệt thự c&oacute; diện t&iacute;ch 500m2 được thiết kế h&agrave;i h&ograve;a đảm bảo kh&ocirc;ng gian sinh hoạt v&agrave; kh&ocirc;ng gian xanh b&ecirc;n ngo&agrave;i.</p>\n<p style=\"font-family: Arial, Helvetica, sans-serif; line-height: 16px; color: rgb(51, 51, 51); text-align: center;\">\n	<img alt=\"Ngọc Ánh, Diễm My: Đụng “gout” biệt thự trắng - 3\" class=\"news-image\" src=\"http://hcm.eva.vn/upload/3-2013/images/2013-08-04/1375612619-4.jpg\" style=\"border: 0px;\" /></p>\n<p style=\"font-family: Arial, Helvetica, sans-serif; line-height: 16px; color: rgb(0, 0, 255); font-style: italic; text-align: center;\">\n	Nữ diễn vi&ecirc;n chọn m&agrave;u trắng v&agrave; phong c&aacute;ch trang tr&iacute; đơn giản, tinh tế cho tổ ấm mới của m&igrave;nh.</p>\n<p style=\"font-family: Arial, Helvetica, sans-serif; line-height: 16px; color: rgb(51, 51, 51); text-align: center;\">\n	<img alt=\"Ngọc Ánh, Diễm My: Đụng “gout” biệt thự trắng - 4\" class=\"news-image\" src=\"http://hcm.eva.vn/upload/3-2013/images/2013-08-04/1375612619-3.jpg\" style=\"border: 0px;\" /></p>\n<p style=\"font-family: Arial, Helvetica, sans-serif; line-height: 16px; color: rgb(0, 0, 255); font-style: italic; text-align: center;\">\n	Hồ c&aacute; được thiết kế cầu kỳ kết hợp tiểu cảnh đẹp mắt.</p>\n<p style=\"font-family: Arial, Helvetica, sans-serif; line-height: 16px; color: rgb(51, 51, 51); text-align: justify;\">\n	Ba tầng lầu của ng&ocirc;i nh&agrave; c&oacute; m&agrave;u trắng chủ đạo, ngay cả nội thất b&ecirc;n trong vẫn sử dụng t&ocirc;ng m&agrave;u n&agrave;y. T&ugrave;y v&agrave;o từng kh&ocirc;ng gian kh&aacute;c nhau, Ngọc &Aacute;nh phối hợp th&ecirc;m những m&agrave;u sắc mới nhưng chủ yếu vẫn l&agrave; những gam m&agrave;u trung t&iacute;nh, nhẹ nh&agrave;ng mang lại cảm gi&aacute;c y&ecirc;n b&igrave;nh, dễ chịu.</p>\n<p style=\"font-family: Arial, Helvetica, sans-serif; line-height: 16px; color: rgb(51, 51, 51); text-align: justify;\">\n	<em>(Mời c&aacute;c bạn ngắm lại biệt thự trắng đẳng cấp của vợ chồng Trương Ngọc &Aacute;nh - Trần Bảo Sơn&nbsp;<a href=\"http://hcm.eva.vn/nha-dep/eva.vn/nha-dep/mung-1-tet-xong-500m2-nha-truong-ngoc-anh-c169a126754.html\" style=\"color: rgb(0, 0, 255); text-decoration: none;\">tại đ&acirc;y</a>)</em></p>\n<p style=\"font-family: Arial, Helvetica, sans-serif; line-height: 16px; color: rgb(51, 51, 51); text-align: justify;\">\n	Được x&acirc;y dựng từ cuối những năm 90, ng&ocirc;i biệt thự của người đẹp kh&ocirc;ng tuổi Diễm My vẫn đẹp m&atilde;i với thời gian. Điều n&agrave;y nhờ v&agrave;o b&agrave;n tay chăm s&oacute;c chu đ&aacute;o của nữ chủ nh&acirc;n xinh đẹp.</p>\n<p style=\"font-family: Arial, Helvetica, sans-serif; line-height: 16px; color: rgb(51, 51, 51); text-align: justify;\">\n	Trong khu đất rộng 1000m<sup style=\"font-size: 12px;\">2</sup>, biệt thự 2 tầng với kiến tr&uacute;c đẹp mắt nổi bật bởi một m&agrave;u trắng tinh khiết, trang nh&atilde;. V&agrave;o ban ng&agrave;y, ng&ocirc;i nh&agrave; c&agrave;ng nổi bật hơn nhờ thảm cỏ xanh mướt v&agrave; rất nhiều c&acirc;y cối xum xu&ecirc; quanh nh&agrave;. Trong khu&ocirc;n vi&ecirc;n biệt thự c&ograve;n c&oacute; cả một bể bơi để hai c&ocirc;ng ch&uacute;a nhỏ tha hồ bơi lội, vui đ&ugrave;a c&ugrave;ng nhau.</p>\n<p style=\"font-family: Arial, Helvetica, sans-serif; line-height: 16px; color: rgb(51, 51, 51); text-align: center;\">\n	<img alt=\"Ngọc Ánh, Diễm My: Đụng “gout” biệt thự trắng - 5\" class=\"news-image\" src=\"http://hcm.eva.vn/upload/3-2013/images/2013-08-04/1375612619-9.jpg\" style=\"border: 0px;\" /></p>\n<p style=\"font-family: Arial, Helvetica, sans-serif; line-height: 16px; color: rgb(0, 0, 255); font-style: italic; text-align: center;\">\n	Biệt thự kết hợp h&agrave;i h&ograve;a của gam m&agrave;u trắng v&agrave; m&aacute;i ngỏ đỏ tươi, tạo n&ecirc;n ấn tượng trang nh&atilde;, lịch l&atilde;m ngay từ c&aacute;i nh&igrave;n đầu ti&ecirc;n.</p>\n<p style=\"font-family: Arial, Helvetica, sans-serif; line-height: 16px; color: rgb(51, 51, 51); text-align: center;\">\n	<img alt=\"Ngọc Ánh, Diễm My: Đụng “gout” biệt thự trắng - 6\" class=\"news-image\" src=\"http://hcm.eva.vn/upload/3-2013/images/2013-08-04/1375612619-8.jpg\" style=\"border: 0px;\" /></p>\n<p style=\"font-family: Arial, Helvetica, sans-serif; line-height: 16px; color: rgb(51, 51, 51); text-align: justify;\">\n	Diễn My chia sẻ:&nbsp;<em>&quot;Đ&acirc;y l&agrave; nơi để mọi th&agrave;nh vi&ecirc;n trong gia đ&igrave;nh c&ugrave;ng tắm m&aacute;t, thư gi&atilde;n, r&egrave;n luyện v&oacute;c d&aacute;ng, h&igrave;nh thể sau những giờ l&agrave;m việc mệt mỏi&quot;</em>.</p>\n<p style=\"font-family: Arial, Helvetica, sans-serif; line-height: 16px; color: rgb(51, 51, 51); text-align: justify;\">\n	B&ecirc;n trong ng&ocirc;i nh&agrave; xanh ngắt b&oacute;ng c&acirc;y của Diễm My, c&aacute;c kh&ocirc;ng gian được thiết kế v&agrave; b&agrave;i tr&iacute; theo từng sở th&iacute;ch mỗi người nhưng tr&ecirc;n hết, gam m&agrave;u trắng vẫn được sử dụng nhiều nhất v&igrave; Diễm My v&agrave; &ocirc;ng x&atilde; đều &quot;t&ocirc;n s&ugrave;ng&quot; gam m&agrave;u n&agrave;y.</p>\n<p style=\"font-family: Arial, Helvetica, sans-serif; line-height: 16px; color: rgb(51, 51, 51); text-align: justify;\">\n	Đầu năm 2013, gia đ&igrave;nh Diễm My vui đ&oacute;n xu&acirc;n tại ng&ocirc;i&nbsp;<a href=\"http://hcm.eva.vn/nha-dep/eva.vn/nha-dep/soi-nha-my-nhan-khong-tuoi-trong-dem-c169a94603.html\" style=\"color: rgb(0, 0, 255); text-decoration: none;\">biệt thự mới</a>&nbsp;cực kỳ sang trọng. Điều th&uacute; vị l&agrave; tổ ấm mới n&agrave;y ki&ecirc;u sa trong sắc trắng v&agrave; ngập tr&agrave;n m&agrave;u xanh của cỏ c&acirc;y.</p>\n<p style=\"font-family: Arial, Helvetica, sans-serif; line-height: 16px; color: rgb(51, 51, 51); text-align: center;\">\n	<img alt=\"Ngọc Ánh, Diễm My: Đụng “gout” biệt thự trắng - 7\" class=\"news-image\" src=\"http://hcm.eva.vn/upload/3-2013/images/2013-08-04/1375615552-7.jpg\" style=\"border: 0px;\" /></p>\n<p style=\"font-family: Arial, Helvetica, sans-serif; line-height: 16px; color: rgb(0, 0, 255); font-style: italic; text-align: center;\">\n	Biệt thự mới của người đẹp kh&ocirc;ng tuổi cũng mang vẻ ki&ecirc;u sa với gam m&agrave;u trắng.</p>', '0', '0', '0', '0', null, '0', 'ava-nha.jpg', '1', '0', null, null, '0', '0', '', '1', 'img/news/', '0', '0', null, 'sao Việt, thư giãn', '2013-08-05 12:11:50', null, null, null, null, null, null, null, 'Nguồn tin eva', null, null, null, null, null, null, null);
INSERT INTO articles VALUES ('366', null, '77', null, '365', '0', 'NEWS00365', 'Nghi án Ngọc Tình \"mượn\" spa khoe của', 'nghi-an-ngoc-tinh-muon-spa-khoe-cua', 'Sau Angela Phương Trinh, siêu mẫu Ngọc Tình cũng dính phải nghi án mượn spa để khoe của.', '<p style=\"font-family: Arial, Helvetica, sans-serif; line-height: 16px; color: rgb(51, 51, 51); text-align: justify;\">\n	C&aacute;ch đ&acirc;y kh&ocirc;ng l&acirc;u, ch&agrave;ng người mẫu từng đoạt giải V&agrave;ng Si&ecirc;u mẫu Việt Nam 2010 Ngọc T&igrave;nh khoe ng&ocirc;i nh&agrave; nhiều đồ cổ v&agrave; c&oacute; hoa ưu đ&agrave;m khiến mọi người x&ocirc;n xao. Tuy nhi&ecirc;n, cư d&acirc;n mạng đ&atilde; chỉ ra đ&acirc;y l&agrave; một spa d&agrave;nh cho nam khi &quot;soi&quot; c&aacute;c bức ảnh.</p>\n<p style=\"font-family: Arial, Helvetica, sans-serif; line-height: 16px; color: rgb(51, 51, 51); text-align: justify;\">\n	Trong bộ ảnh khoe tr&ecirc;n b&aacute;o, kh&ocirc;ng gian sống của ch&agrave;ng người mẫu được anh giới thiệu l&agrave; một căn nh&agrave; khang trang với phong c&aacute;ch cổ tại quận Thủ Đức, TP.HCM. Ngay từ d&agrave;n c&acirc;y leo xanh mướt nơi cổng ra v&agrave;o v&agrave; những bước tường gạch đ&atilde; tạo n&ecirc;n một cảm gi&aacute;c an b&igrave;nh, nhẹ nh&agrave;ng.</p>\n<p style=\"font-family: Arial, Helvetica, sans-serif; line-height: 16px; color: rgb(51, 51, 51); text-align: justify;\">\n	Ngọc T&igrave;nh t&acirc;m sự, đ&acirc;y l&agrave; nơi đem lại cho anh cảm gi&aacute;c y&ecirc;n b&igrave;nh nhất. Khi về tới nh&agrave; cũng l&agrave; l&uacute;c anh sẽ bỏ lại những x&ocirc; bồ, n&aacute;o nhiệt của cuộc sống, c&ocirc;ng việc b&ecirc;n ngo&agrave;i cửa.</p>\n<p style=\"font-family: Arial, Helvetica, sans-serif; line-height: 16px; color: rgb(51, 51, 51); text-align: center;\">\n	<img alt=\"Nghi án Ngọc Tình &quot;mượn&quot; spa khoe của - 1\" class=\"news-image\" src=\"http://hcm.eva.vn/upload/3-2013/images/2013-08-03/1375486436-1.jpg\" style=\"border: 0px;\" /></p>\n<p style=\"font-family: Arial, Helvetica, sans-serif; line-height: 16px; color: rgb(0, 0, 255); font-style: italic; text-align: center;\">\n	Ng&ocirc;i nh&agrave; tuyệt đẹp m&agrave; Ngọc T&igrave;nh khoe tr&ecirc;n b&aacute;o.</p>\n<p style=\"font-family: Arial, Helvetica, sans-serif; line-height: 16px; color: rgb(51, 51, 51); text-align: center;\">\n	<img alt=\"Nghi án Ngọc Tình &quot;mượn&quot; spa khoe của - 2\" class=\"news-image\" src=\"http://hcm.eva.vn/upload/3-2013/images/2013-08-03/1375486436-2.jpg\" style=\"border: 0px;\" /></p>\n<p style=\"font-family: Arial, Helvetica, sans-serif; line-height: 16px; color: rgb(0, 0, 255); font-style: italic; text-align: center;\">\n	Lối v&agrave;o nh&agrave; Ngọc T&igrave;nh c&oacute; những h&agrave;ng x&iacute;ch đu gỗ.</p>\n<p style=\"font-family: Arial, Helvetica, sans-serif; line-height: 16px; color: rgb(51, 51, 51); text-align: center;\">\n	<img alt=\"Nghi án Ngọc Tình &quot;mượn&quot; spa khoe của - 3\" class=\"news-image\" src=\"http://hcm.eva.vn/upload/3-2013/images/2013-08-03/1375486436-3.jpg\" style=\"border: 0px;\" /></p>\n<p style=\"font-family: Arial, Helvetica, sans-serif; line-height: 16px; color: rgb(0, 0, 255); font-style: italic; text-align: center;\">\n	Một g&oacute;c thư gi&atilde;n ngo&agrave;i trời để đọc s&aacute;ch, tr&ograve; chuyện c&ugrave;ng bạn b&egrave; hay ngắm c&aacute;.</p>\n<p style=\"font-family: Arial, Helvetica, sans-serif; line-height: 16px; color: rgb(51, 51, 51); text-align: center;\">\n	<img alt=\"Nghi án Ngọc Tình &quot;mượn&quot; spa khoe của - 4\" class=\"news-image\" src=\"http://hcm.eva.vn/upload/3-2013/images/2013-08-03/1375486436-4.jpg\" style=\"border: 0px;\" /></p>\n<p style=\"font-family: Arial, Helvetica, sans-serif; line-height: 16px; color: rgb(51, 51, 51); text-align: center;\">\n	<img alt=\"Nghi án Ngọc Tình &quot;mượn&quot; spa khoe của - 5\" class=\"news-image\" src=\"http://hcm.eva.vn/upload/3-2013/images/2013-08-03/1375486436-5.jpg\" style=\"border: 0px;\" /></p>\n<p style=\"font-family: Arial, Helvetica, sans-serif; line-height: 16px; color: rgb(0, 0, 255); font-style: italic; text-align: center;\">\n	Ph&ograve;ng kh&aacute;ch l&agrave; nơi trưng b&agrave;y kh&aacute; nhiều đồ cổ - một th&uacute; sưu tầm c&ocirc;ng phu v&agrave; tốn k&eacute;m.</p>\n<p style=\"font-family: Arial, Helvetica, sans-serif; line-height: 16px; color: rgb(51, 51, 51); text-align: center;\">\n	<img alt=\"Nghi án Ngọc Tình &quot;mượn&quot; spa khoe của - 6\" class=\"news-image\" src=\"http://hcm.eva.vn/upload/3-2013/images/2013-08-03/1375486436-6.jpg\" style=\"border: 0px;\" /></p>\n<p style=\"font-family: Arial, Helvetica, sans-serif; line-height: 16px; color: rgb(0, 0, 255); font-style: italic; text-align: center;\">\n	Ngọc T&igrave;nh th&iacute;ch nằm nghỉ ngơi tr&ecirc;n chiếc phản gỗ trong ph&ograve;ng ngủ của m&igrave;nh.</p>\n<p style=\"font-family: Arial, Helvetica, sans-serif; line-height: 16px; color: rgb(0, 0, 255); font-style: italic; text-align: center;\">\n	<img alt=\"Nghi án Ngọc Tình &quot;mượn&quot; spa khoe của - 7\" class=\"news-image\" src=\"http://hcm.eva.vn/upload/3-2013/images/2013-08-03/1375486436-7.jpg\" style=\"border: 0px;\" /></p>\n<p style=\"font-family: Arial, Helvetica, sans-serif; line-height: 16px; color: rgb(0, 0, 255); font-style: italic; text-align: center;\">\n	Hoa ưu đ&agrave;m cũng l&agrave; thứ khiến anh ch&agrave;ng tự h&agrave;o.</p>\n<p style=\"font-family: Arial, Helvetica, sans-serif; line-height: 16px; color: rgb(51, 51, 51); text-align: justify;\">\n	Tuy nhi&ecirc;n, ngay sau khi những h&igrave;nh ảnh n&agrave;y được đăng tải, cư d&acirc;n mạng x&ocirc;n xao b&agrave;y tỏ &yacute; kiến về sự &quot;khoe mẽ&quot; của ch&agrave;ng người mẫu. Theo đ&oacute;, rất nhiều người tỏ ra kh&ocirc;ng tin ở khả năng người mẫu đến từ v&ugrave;ng qu&ecirc; trồng l&uacute;a c&oacute; thể sở hữu một ng&ocirc;i nh&agrave; &quot;khủng&quot; như thế tại TP.HCM đắt đỏ, trong khi thu nhập từ nghề mẫu nam rất bạc bẽo.</p>\n<p style=\"font-family: Arial, Helvetica, sans-serif; line-height: 16px; color: rgb(51, 51, 51); text-align: justify;\">\n	Một số kh&aacute;c c&ograve;n trưng những bằng chứng cho thấy đ&acirc;y kh&ocirc;ng hẳn l&agrave; ng&ocirc;i nh&agrave; m&agrave; l&agrave; một spa d&agrave;nh cho nam kh&aacute; nổi tiếng của Ph&uacute;c Nguyễn - &ocirc;ng bầu của Ngọc T&igrave;nh. Một trong những chi tiết g&acirc;y sốc bị ph&aacute;t hiện l&agrave; trong loạt ảnh gửi b&aacute;o, anh ch&agrave;ng người mẫu v&ocirc; t&igrave;nh để lọt tấm h&igrave;nh thấp tho&aacute;ng b&oacute;ng một người đ&agrave;n &ocirc;ng mặc như kh&ocirc;ng mặc ph&iacute;a xa xa.</p>', '0', '0', '0', '0', null, '0', '1375488170-_ava-1.jpg', '1', '0', null, null, '0', '0', '', '1', 'img/news/', '0', '0', null, 'Ngọc Tình, spa', '2013-08-05 12:13:07', null, null, null, null, null, null, null, 'Nguồn tin eva', null, null, null, null, null, null, null);
INSERT INTO articles VALUES ('367', null, '77', null, '366', '0', 'NEWS00366', 'Taeyang (Bigbang) khoe nhà cá tính trên Vogue', 'taeyang-bigbang-khoe-nha-ca-tinh-tren-vogue', 'Fan hâm mộ chàng ca sỹ Taeyang (Bigbang) háo hức ngắm nhìn ngôi nhà \"Disney Land\" của thần tượng trên tạp chí Vogue Hàn Quốc số ra tháng 8.', '<p style=\"font-family: Arial, Helvetica, sans-serif; line-height: 16px; color: rgb(51, 51, 51); text-align: justify;\">\n	Một biểu tượng &acirc;m nhạc v&agrave; thời trang được c&ocirc;ng nhận, th&agrave;nh vi&ecirc;n t&agrave;i năng của nh&oacute;m nhạc đ&igrave;nh đ&aacute;m Bigbang l&agrave; Tae Yang mới đ&acirc;y đ&atilde; &ldquo;khoe&rdquo; nh&agrave; của m&igrave;nh lần đầu ti&ecirc;n. Tae Yang gọi ng&ocirc;i nh&agrave; th&acirc;n y&ecirc;u của m&igrave;nh l&agrave; &ldquo;Disney Land&rdquo;. Rất nhiều bức tranh nghệ thuật bắt mắt v&agrave; những m&oacute;n đồ chơi ng&acirc;y thơ của trẻ em được kết hợp với đồ nội thất m&agrave;u đen tạo n&ecirc;n một phong c&aacute;ch rất ri&ecirc;ng của anh.</p>\n<p style=\"font-family: Arial, Helvetica, sans-serif; line-height: 16px; color: rgb(51, 51, 51); text-align: justify;\">\n	Trong ph&ograve;ng kh&aacute;ch nh&igrave;n thẳng ra s&ocirc;ng H&agrave;n, bộ sofa m&agrave;u đen v&agrave; chiếc b&agrave;n &quot;B&amp;B Italia&quot; c&oacute; tỷ lệ c&acirc;n xứng. Bức tranh nghệ thuật của David Mach l&agrave; bức tranh Tae Yang &ldquo;y&ecirc;u từ c&aacute;i nh&igrave;n đầu ti&ecirc;n&rdquo;, đ&egrave;n chụp đứng Philippe Starck bắt mắt v&agrave; chiếc ghế đệm của nghệ sỹ nhạc pop Kaws mang tới cảm gi&aacute;c h&agrave;i hước cho kh&ocirc;ng gian.</p>\n<p style=\"font-family: Arial, Helvetica, sans-serif; line-height: 16px; color: rgb(51, 51, 51); text-align: center;\">\n	<img alt=\"Taeyang (Bigbang) khoe nhà cá tính trên Vogue - 1\" class=\"news-image\" src=\"http://hcm.eva.vn/upload/3-2013/images/2013-07-31/1375266510-1.jpg\" style=\"border: 0px;\" /></p>\n<p style=\"font-family: Arial, Helvetica, sans-serif; line-height: 16px; color: rgb(51, 51, 51); text-align: center;\">\n	<img alt=\"Taeyang (Bigbang) khoe nhà cá tính trên Vogue - 2\" class=\"news-image\" src=\"http://hcm.eva.vn/upload/3-2013/images/2013-07-31/1375266510-2.jpg\" style=\"border: 0px;\" /></p>\n<p style=\"font-family: Arial, Helvetica, sans-serif; line-height: 16px; color: rgb(51, 51, 51); text-align: justify;\">\n	C&aacute;c căn hộ ở Manpo được n&acirc;ng cấp với tầm nh&igrave;n mở về ph&iacute;a s&ocirc;ng H&agrave;n. Đ&acirc;y cũng ch&iacute;nh l&agrave; nơi ở mới nhất của th&agrave;nh vi&ecirc;n nh&oacute;m nhạc Bigbang Tae Yang. H&atilde;y quay ngược thời gian về thời điểm c&aacute;ch đ&acirc;y v&agrave;i th&aacute;ng, t&ocirc;i c&oacute; cơ hội thực hiện bộ ảnh thời trang của G-Dragon v&agrave; Taeyang &ndash; hai ch&agrave;ng ca sỹ v&agrave; l&agrave; biểu tượng thời trang tuyệt vời nhất cho số ra th&aacute;ng 3 của Vogue.</p>\n<p style=\"font-family: Arial, Helvetica, sans-serif; line-height: 16px; color: rgb(51, 51, 51); text-align: justify;\">\n	Khi ấy, Taeyang đ&atilde; n&oacute;i với t&ocirc;i rằng anh bắt đầu trang tr&iacute; ng&ocirc;i nh&agrave; mới v&agrave; hứa sẽ mời t&ocirc;i đến bữa tiệc t&acirc;n gia. Tất nhi&ecirc;n, t&ocirc;i đ&atilde; gợi &yacute; anh ấy &ldquo;khoe&rdquo; nh&agrave; v&agrave;o số th&aacute;ng 8 của Vogue Living, v&agrave; anh ấy đ&atilde; vui vẻ đồng &yacute;. Mỗi khi xem chương tr&igrave;nh &quot;I Live Alone&quot; tr&ecirc;n tivi, t&ocirc;i hay đặt c&acirc;u hỏi &ldquo;Ng&ocirc;i nh&agrave; của Taeyang tr&ocirc;ng như thế n&agrave;o?&rdquo;. Cuối ch&ugrave;ng th&igrave; c&aacute;nh cửa b&iacute; mật ấy đ&atilde; mở ra.</p>\n<p style=\"font-family: Arial, Helvetica, sans-serif; line-height: 16px; color: rgb(51, 51, 51); text-align: center;\">\n	<img alt=\"Taeyang (Bigbang) khoe nhà cá tính trên Vogue - 3\" class=\"news-image\" src=\"http://hcm.eva.vn/upload/3-2013/images/2013-07-31/1375266510-3.jpg\" style=\"border: 0px;\" /></p>\n<p style=\"font-family: Arial, Helvetica, sans-serif; line-height: 16px; color: rgb(51, 51, 51); text-align: justify;\">\n	Một người đ&agrave;n &ocirc;ng tr&agrave;n đầy t&agrave;i năng &acirc;m nhạc v&agrave; thời trang, một người đ&agrave;n &ocirc;ng ở độ tuổi 20 sở hữu n&eacute;t duy&ecirc;n vui vẻ sẽ sống trong một kh&ocirc;ng gian như thế n&agrave;o? Ngay khi c&aacute;nh cửa mở ra, một m&ugrave;i hương d&acirc;u ngọt ng&agrave;o ch&agrave;o đ&oacute;n ch&uacute;ng t&ocirc;i, tiếp theo đ&oacute; l&agrave; h&igrave;nh ảnh vui nhộn của ch&uacute; chuột Mickey v&agrave; c&ocirc; n&agrave;ng Minnie Mouse trong một bức tranh treo tường. Đ&uacute;ng với những g&igrave; t&ocirc;i mong đợi.</p>\n<p style=\"font-family: Arial, Helvetica, sans-serif; line-height: 16px; color: rgb(51, 51, 51); text-align: center;\">\n	<img alt=\"Taeyang (Bigbang) khoe nhà cá tính trên Vogue - 4\" class=\"news-image\" src=\"http://hcm.eva.vn/upload/3-2013/images/2013-07-31/1375266510-4.jpg\" style=\"border: 0px;\" /></p>\n<p style=\"font-family: Arial, Helvetica, sans-serif; line-height: 16px; color: rgb(51, 51, 51); text-align: justify;\">\n	<em>&ldquo;T&ocirc;i đ&atilde; sưu tầm từng thứ từng thứ một bất kỳ l&uacute;c n&agrave;o khi t&ocirc;i đi lưu diễn trong suốt chuyến lưu diễn The World Tour. T&ocirc;i thực sự th&iacute;ch poster Mickey v&agrave; Minnie Mouse cổ điển mua được ở Hồng K&ocirc;ng&rdquo;</em>. Nhưng, khi ch&uacute;ng t&ocirc;i đi qua h&agrave;nh lang với rất nhiều bức tranh nghệ thuật của lichtenstein v&agrave; những tấm poster Disney cũ, ph&ograve;ng kh&aacute;ch với chiếc b&agrave;n tr&ograve;n v&agrave; bộ ghế sofa m&agrave;u đen khiến ch&uacute;ng t&ocirc;i cho&aacute;ng ngợp trong &iacute;t ph&uacute;t.</p>\n<p style=\"font-family: Arial, Helvetica, sans-serif; line-height: 16px; color: rgb(51, 51, 51); text-align: justify;\">\n	Thực ra, t&ocirc;i đ&atilde; nghi ngờ rằng ng&ocirc;i nh&agrave; của anh tr&agrave;n ngập sắc m&agrave;u v&agrave; c&aacute;c h&igrave;nh tượng điển h&igrave;nh. Thế nhưng, m&agrave;u sắc ch&agrave;o đ&oacute;n ch&uacute;ng t&ocirc;i ở ph&ograve;ng kh&aacute;ch lại l&agrave; m&agrave;u đen.&nbsp;<em>&ldquo;Ah, t&ocirc;i th&iacute;ch m&agrave;u đen lắm, v&igrave; thế, t&ocirc;i đ&atilde; chọn nhiều đồ nội thất m&agrave;u đen. T&ocirc;i mua chiếc b&agrave;n tr&ograve;n lớn n&agrave;y ở cửa h&agrave;ng &ldquo;B&amp;B Italia&rdquo;</em>.</p>', '0', '0', '0', '0', null, '0', '1375321554-_ava-1.jpg', '1', '0', null, null, '0', '0', '', '1', 'img/news/', '0', '0', null, 'Bigbang, Vogue', '2013-08-05 12:14:06', null, null, null, null, null, null, null, 'Nguồn tin eva', null, null, null, null, null, null, null);
INSERT INTO articles VALUES ('368', null, '78', null, '367', '0', 'NEWS00367', 'Soi căn hộ trong chung cư của Lee Min-Ho', 'soi-can-ho-trong-chung-cu-cua-lee-min-ho', 'Lee Min-Ho khá kín tiếng về tổ ấm riêng tư. Tuy nhiên, hình ảnh về một căn hộ trong khu chung cư anh đang sống sẽ cho người hâm mộ hình dung phần nào về nơi ở thực sự của anh.', '<p style=\"font-family: Arial, Helvetica, sans-serif; line-height: 16px; color: rgb(51, 51, 51); text-align: justify;\">\n	Năm 2011, ch&agrave;ng &quot;<em>Thợ săn th&agrave;nh phố</em>&quot; Lee Min-Ho v&agrave; gia đ&igrave;nh ch&iacute;nh thức chuyển về sống tại khu chung cư cao cấp Heukseok-dong M ở Seoul. Đ&acirc;y cũng l&agrave; nơi c&oacute; ng&ocirc;i nh&agrave; của cặp đ&ocirc;i Jang Dong-Gun, Go So Young, đồng thời l&agrave; nơi bố mẹ diễn vi&ecirc;n Huyn Bin đang sống. N&oacute;i c&aacute;ch kh&aacute;c, Lee Min-Ho đ&atilde; trở th&agrave;nh h&agrave;ng x&oacute;m của c&aacute;c đ&agrave;n anh trong l&agrave;ng diễn.</p>\n<p style=\"font-family: Arial, Helvetica, sans-serif; line-height: 16px; color: rgb(51, 51, 51); text-align: justify;\">\n	Được biết, ch&agrave;ng c&ocirc;ng tử trong phim&nbsp;<em>Boys Over Flowers</em>&nbsp;đ&atilde; đến ch&agrave;o c&aacute;c đ&agrave;n anh, đ&agrave;n chị khi mới chuyển về đ&acirc;y. Theo một số nguồn tin, Lee Min-Ho sống ở tầng 7, Hyun Bin sống tại tầng 5 c&ograve;n vợ chồng Jang Dong-Gun ở tầng 8.</p>\n<p style=\"font-family: Arial, Helvetica, sans-serif; line-height: 16px; color: rgb(51, 51, 51); text-align: center;\">\n	<img alt=\"Soi căn hộ trong chung cư của Lee Min-Ho - 1\" class=\"news-image\" src=\"http://hcm.eva.vn/upload/3-2013/images/2013-07-31/1375239385-images480632_htm_20110329100706c000c010_001.jpg\" style=\"border: 0px;\" /></p>\n<p style=\"font-family: Arial, Helvetica, sans-serif; line-height: 16px; color: rgb(0, 0, 255); font-style: italic; text-align: center;\">\n	Lee Min-Ho l&agrave; h&agrave;ng x&oacute;m với Huyn Bin v&agrave; vợ chồng Jang Dong-Gun.</p>\n<p style=\"font-family: Arial, Helvetica, sans-serif; line-height: 16px; color: rgb(51, 51, 51); text-align: justify;\">\n	Heuksuk-dong Mark Hill l&agrave; t&ecirc;n đầy đủ của khu chung cư cao cấp n&agrave;y. Khu chung cư được x&acirc;y dựng ở vị tr&iacute; cực kỳ đẹp tại thủ đ&ocirc; Seoul, ph&iacute;a nam c&acirc;y cầu Hangang bắc qua s&ocirc;ng H&agrave;n nổi tiếng. V&igrave; thế, c&aacute;c căn hộ đều c&oacute; tầm nh&igrave;n tuyệt đẹp hướng ra s&ocirc;ng H&agrave;n.</p>\n<p style=\"font-family: Arial, Helvetica, sans-serif; line-height: 16px; color: rgb(51, 51, 51); text-align: center;\">\n	<img alt=\"Soi căn hộ trong chung cư của Lee Min-Ho - 2\" class=\"news-image\" src=\"http://hcm.eva.vn/upload/3-2013/images/2013-07-31/1375239852-images480635_t291644.jpg\" style=\"border: 0px;\" /></p>\n<p style=\"font-family: Arial, Helvetica, sans-serif; line-height: 16px; color: rgb(0, 0, 255); font-style: italic; text-align: center;\">\n	Khu chung cư cao cấp Heuksuk-dong M - nơi ở của nhiều ng&ocirc;i sao nổi tiếng H&agrave;n Quốc.</p>\n<p style=\"font-family: Arial, Helvetica, sans-serif; line-height: 16px; color: rgb(51, 51, 51); text-align: justify;\">\n	Những ai muốn sở hữu một căn villa hạng sang của Heuksuk-dong Mark Hill đều phải chi một khoản kh&ocirc;ng nhỏ l&agrave; 3 tỷ won H&agrave;n Quốc, xấp xỉ 57 tỷ VNĐ. Mỗi căn hộ c&oacute; diện t&iacute;ch gần 400m2, bao gồm 4 ph&ograve;ng ngủ, 3 ph&ograve;ng tắm v&agrave; nhiều kh&ocirc;ng gian sinh hoạt kh&aacute;c.</p>\n<p style=\"font-family: Arial, Helvetica, sans-serif; line-height: 16px; color: rgb(51, 51, 51); text-align: center;\">\n	<img alt=\"Soi căn hộ trong chung cư của Lee Min-Ho - 3\" class=\"news-image\" src=\"http://hcm.eva.vn/upload/3-2013/images/2013-07-31/1375237002-1.jpg\" style=\"border: 0px;\" /></p>\n<p style=\"font-family: Arial, Helvetica, sans-serif; line-height: 16px; color: rgb(0, 0, 255); font-style: italic; text-align: center;\">\n	Ph&ograve;ng kh&aacute;ch căn hộ đầy chuẩn mực với bộ sofa da n&acirc;u sang trọng.</p>\n<p style=\"font-family: Arial, Helvetica, sans-serif; line-height: 16px; color: rgb(51, 51, 51); text-align: center;\">\n	&nbsp;</p>\n<p style=\"font-family: Arial, Helvetica, sans-serif; line-height: 16px; color: rgb(51, 51, 51); text-align: center;\">\n	<img alt=\"Soi căn hộ trong chung cư của Lee Min-Ho - 4\" class=\"news-image\" src=\"http://hcm.eva.vn/upload/3-2013/images/2013-07-31/1375237002-2.jpg\" style=\"border: 0px;\" /></p>\n<p style=\"font-family: Arial, Helvetica, sans-serif; line-height: 16px; color: rgb(0, 0, 255); font-style: italic; text-align: center;\">\n	Lối v&agrave;o h&agrave;nh lang gọn g&agrave;ng v&agrave; đ&aacute;ng y&ecirc;u.</p>', '0', '0', '0', '0', null, '0', '1375238288-_ava-1.jpg', '1', '0', null, null, '0', '0', '', '0', 'img/news/', '0', '0', null, 'Lee Min-Ho, chung cư', '2013-08-05 12:16:41', null, null, null, null, null, null, null, 'Nguồn tin eva', null, null, null, null, null, null, null);
INSERT INTO articles VALUES ('369', null, '78', null, '368', '0', 'NEWS00368', 'Thăm nhà độc thân của diễn viên Kinh Quốc', 'tham-nha-doc-than-cua-dien-vien-kinh-quoc', 'Dù sống đời độc thân nhưng ngôi nhà của diễn viên Kinh Quốc rất gọn gàng, ngăn nắp.', '<p style=\"font-family: Arial, Helvetica, sans-serif; line-height: 16px; color: rgb(51, 51, 51); text-align: justify;\">\n	Tự nhận m&igrave;nh l&agrave; &quot;ch&agrave;ng khờ&quot; trong nghệ thuật v&agrave; ngo&agrave;i đời, Kinh Quốc được kh&aacute;n giả nhớ đến với những vai diễn ấn tượng trong&nbsp;<em>Hướng nghiệp, D&ograve;ng đời, Qu&yacute; b&agrave; lắm chi&ecirc;u, Tr&uacute;ng số...&nbsp;</em>Đam m&ecirc; phim ảnh như một c&aacute;i nghiệp, &iacute;t ai biết Kinh Quốc vốn xuất th&acirc;n từ gia đ&igrave;nh c&oacute; gốc kinh doanh, nhưng t&igrave;nh y&ecirc;u nghệ thuật đ&atilde; khiến anh từ bỏ tất cả để đi theo nghiệp diễn.</p>\n<p style=\"font-family: Arial, Helvetica, sans-serif; line-height: 16px; color: rgb(51, 51, 51); text-align: center;\">\n	<img alt=\"Thăm nhà độc thân của diễn viên Kinh Quốc - 1\" class=\"news-image\" src=\"http://hcm.eva.vn/upload/3-2013/images/2013-07-29/1375089276-kinhquoc-eva10.jpg\" style=\"border: 0px; width: 450px; height: 482px;\" /></p>\n<p style=\"font-family: Arial, Helvetica, sans-serif; line-height: 16px; color: rgb(51, 51, 51); text-align: center;\">\n	<span style=\"color: rgb(0, 0, 255);\"><em>DV Kinh Quốc l&agrave; gương mặt quen thuộc của điện ảnh, anh được nhớ đến với kh&aacute; nhiều vai diễn</em></span></p>\n<p style=\"font-family: Arial, Helvetica, sans-serif; line-height: 16px; color: rgb(51, 51, 51); text-align: justify;\">\n	Sau cuộc h&ocirc;n nh&acirc;n kh&ocirc;ng th&agrave;nh c&ocirc;ng,&nbsp;<em>Kinh Quốc</em>&nbsp;chọn cuộc sống độc th&acirc;n&nbsp; trong căn nh&agrave; kh&aacute; y&ecirc;n tĩnh với 4 tầng lầu ở Quận 6. D&ugrave; c&oacute; 6 ph&ograve;ng ngủ nhưng anh chỉ sống một m&igrave;nh, kh&ocirc;ng rủ ai về ở c&ugrave;ng bởi anh muốn d&agrave;nh trọn sự ri&ecirc;ng tư khi về đến nh&agrave;. Kh&ocirc;ng gian sống kh&aacute; rộng r&atilde;i, được Kinh Quốc sắp xếp gọn g&agrave;ng d&ugrave; thiếu b&agrave;n tay của người phụ nữ. Ng&ocirc;i nh&agrave; gắn b&oacute; với anh rất nhiều kỷ niệm từ những ng&agrave;y bắt đầu đi đ&oacute;ng phim.</p>\n<p style=\"font-family: Arial, Helvetica, sans-serif; line-height: 16px; color: rgb(51, 51, 51); text-align: center;\">\n	<img alt=\"Thăm nhà độc thân của diễn viên Kinh Quốc - 2\" class=\"news-image\" src=\"http://hcm.eva.vn/upload/3-2013/images/2013-07-29/1375089388-foto-Duy-England--12-.jpg\" style=\"border: 0px; width: 500px; height: 750px;\" /></p>\n<p style=\"font-family: Arial, Helvetica, sans-serif; line-height: 16px; color: rgb(51, 51, 51); text-align: center;\">\n	<span style=\"color: rgb(0, 0, 255);\"><em>Sau cuộc h&ocirc;n nh&acirc;n kh&ocirc;ng hạnh ph&uacute;c, Kinh Quốc hiện sống một m&igrave;nh ở căn nh&agrave; 4 lầu thuộc quận 6</em></span></p>\n<p style=\"font-family: Arial, Helvetica, sans-serif; line-height: 16px; color: rgb(51, 51, 51); text-align: justify;\">\n	Nh&agrave; bếp l&agrave; nơi &iacute;t khi anh sử dụng v&igrave; thường anh d&ugrave;ng bữa ở phim trường n&ecirc;n anh &iacute;t khi nấu nướng v&agrave; ăn tại nh&agrave;. Ph&ograve;ng ngủ l&agrave; nơi anh thường nghỉ ngơi v&agrave;o buổi tối, s&acirc;n thượng l&agrave; nơi anh thư gi&atilde;n đọc truyện hay xem phim những ng&agrave;y kh&ocirc;ng đi diễn v&agrave; l&agrave; nơi c&oacute; kh&ocirc;ng khi trong l&agrave;nh nhất trong nh&agrave;.</p>\n<p style=\"font-family: Arial, Helvetica, sans-serif; line-height: 16px; color: rgb(51, 51, 51); text-align: center;\">\n	<img alt=\"Thăm nhà độc thân của diễn viên Kinh Quốc - 3\" class=\"news-image\" src=\"http://hcm.eva.vn/upload/3-2013/images/2013-07-29/1375089133-kinhquoc-eva7.jpg\" style=\"border: 0px; width: 500px; height: 333px;\" /></p>\n<p style=\"font-family: Arial, Helvetica, sans-serif; line-height: 16px; color: rgb(51, 51, 51); text-align: center;\">\n	<span style=\"color: rgb(0, 0, 255);\"><em>Ph&ograve;ng kh&aacute;ch l&agrave; nơi Kinh Quốc tiếp kh&aacute;ch, d&ugrave; kh&ocirc;ng c&oacute; b&agrave;n tay phụ nữ nhưng mọi đồ đạc được anh sắp xếp rất ngăn nắp</em></span></p>', '0', '0', '0', '0', null, '0', '1375089569-avar-kinhquoc.jpg', '1', '0', null, null, '0', '0', '', '1', 'img/news/', '0', '0', null, 'Kinh Quốc, độc thân, diễn viên', '2013-08-05 12:17:48', null, null, null, null, null, null, null, 'Nguồn tin eva', null, null, null, null, null, null, null);
INSERT INTO articles VALUES ('370', null, '79', null, '369', '0', 'NEWS00369', 'SeABank phát hành thẻ tín dụng Visa và Visa Platinum', 'seabank-phat-hanh-the-tin-dung-visa-va-visa-platinum', 'Thẻ tín dụng quốc tế SeABank Visa và dòng thẻ Platinum sử dụng công nghệ thẻ chip với nhiều tiện ích', '<p>\n	<strong style=\"list-style: none; border: 0px; margin: 0px; padding: 0px; font-size: 15px; outline: none; color: rgb(0, 0, 0); font-family: \'Segoe UI\';\">Từ ng&agrave;y 1/8/2013, Ng&acirc;n h&agrave;ng Thương mại Cổ phần Đ&ocirc;ng Nam &Aacute; (SeABank) sẽ ch&iacute;nh thức ph&aacute;t h&agrave;nh thẻ t&iacute;n dụng quốc tế SeABank Visa v&agrave; d&ograve;ng thẻ Platinum sử dụng c&ocirc;ng nghệ thẻ chip với nhiều tiện &iacute;ch, ph&ugrave; hợp với nhu cầu sử dụng của kh&aacute;ch h&agrave;ng.</strong><br style=\"list-style: none; color: rgb(0, 0, 0); font-family: \'Segoe UI\'; font-size: 15px;\" />\n	<br style=\"list-style: none; color: rgb(0, 0, 0); font-family: \'Segoe UI\'; font-size: 15px;\" />\n	<span style=\"color: rgb(0, 0, 0); font-family: \'Segoe UI\'; font-size: 15px;\">Với thẻ t&iacute;n dụng Visa của SeABank, chủ thẻ quản l&yacute; được mức chi ti&ecirc;u một c&aacute;ch r&otilde; r&agrave;ng nhất với khoản tiền ho&agrave;n trả cố định h&agrave;ng th&aacute;ng. Đồng thời, với 12 mức l&atilde;i suất kh&aacute;c nhau, thẻ t&iacute;n dụng của SeABank l&agrave; sản phẩm duy nhất tr&ecirc;n thị trường cung cấp cho chủ thẻ phương thức chi ti&ecirc;u c&agrave;ng nhiều l&atilde;i suất c&agrave;ng giảm.&nbsp;</span><br style=\"list-style: none; color: rgb(0, 0, 0); font-family: \'Segoe UI\'; font-size: 15px;\" />\n	<br style=\"list-style: none; color: rgb(0, 0, 0); font-family: \'Segoe UI\'; font-size: 15px;\" />\n	<span style=\"color: rgb(0, 0, 0); font-family: \'Segoe UI\'; font-size: 15px;\">Hơn thế, chủ thẻ c&ograve;n được bảo vệ tốt tr&ecirc;n thị trường với c&aacute;c sản phẩm bảo hiểm to&agrave;n cầu, tối đa l&ecirc;n tới 7 loại bảo hiểm cao cấp cho hạng thẻ Platinum.</span><br style=\"list-style: none; color: rgb(0, 0, 0); font-family: \'Segoe UI\'; font-size: 15px;\" />\n	<br style=\"list-style: none; color: rgb(0, 0, 0); font-family: \'Segoe UI\'; font-size: 15px;\" />\n	<span style=\"color: rgb(0, 0, 0); font-family: \'Segoe UI\'; font-size: 15px;\">Chủ thẻ t&iacute;n dụng SeABank Visa c&oacute; thể thanh to&aacute;n v&agrave; r&uacute;t tiền tại h&agrave;ng triệu m&aacute;y POS v&agrave; ATM tr&ecirc;n to&agrave;n cầu đồng thời thẻ được bảo mật bằng c&ocirc;ng nghệ chip EMV đảm bảo an to&agrave;n cho kh&aacute;ch h&agrave;ng khi sử dụng, gi&uacute;p ngăn ngừa việc sao trộm th&ocirc;ng tin của chủ thẻ, giảm thiểu gian lận giả mạo thẻ. Thẻ t&iacute;n dụng Visa cho ph&eacute;p đổi PIN gi&uacute;p kh&aacute;ch h&agrave;ng dễ d&agrave;ng lưu giữ mật khẩu của m&igrave;nh một c&aacute;ch tiện lợi v&agrave; bảo mật.</span><br style=\"list-style: none; color: rgb(0, 0, 0); font-family: \'Segoe UI\'; font-size: 15px;\" />\n	<br style=\"list-style: none; color: rgb(0, 0, 0); font-family: \'Segoe UI\'; font-size: 15px;\" />\n	<span style=\"color: rgb(0, 0, 0); font-family: \'Segoe UI\'; font-size: 15px;\">Đặc biệt, sử dụng thẻ t&iacute;n dụng Visa v&agrave; Platinum của SeABank, chủ thẻ c&oacute; thể thực hiện việc thanh to&aacute;n nợ mọi l&uacute;c, mọi nơi với c&aacute;c k&ecirc;nh ho&agrave;n trả đa dạng nhất tr&ecirc;n thị trường: thanh to&aacute;n tự động từ t&agrave;i khoản tiền gửi trực tiếp, thanh to&aacute;n tại quầy, qua SeANet, qua ATM, qua Autobank, v&agrave; chuyển khoản. Thẻ t&iacute;n dụng SeABank Visa cho ph&eacute;p kh&aacute;ch h&agrave;ng chi ti&ecirc;u trước - trả tiền sau trong v&ograve;ng 45 ng&agrave;y m&agrave; kh&ocirc;ng bị t&iacute;nh l&atilde;i, với hạn mức t&iacute;n dụng tối đa l&ecirc;n tới 500 triệu đồng.</span><br style=\"list-style: none; color: rgb(0, 0, 0); font-family: \'Segoe UI\'; font-size: 15px;\" />\n	<br style=\"list-style: none; color: rgb(0, 0, 0); font-family: \'Segoe UI\'; font-size: 15px;\" />\n	<span style=\"color: rgb(0, 0, 0); font-family: \'Segoe UI\'; font-size: 15px;\">&ldquo;Hiện nay nhu cầu sử dụng thẻ t&iacute;n dụng tại Việt Nam đang tăng l&ecirc;n nhanh ch&oacute;ng. SeABank l&agrave; một trong những ng&acirc;n h&agrave;ng dẫn đầu về c&ocirc;ng nghệ th&ocirc;ng tin tại Việt Nam, ch&uacute;ng t&ocirc;i lu&ocirc;n nỗ lực hết m&igrave;nh để mang lại cho kh&aacute;ch h&agrave;ng những sản phẩm dịch vụ ng&acirc;n h&agrave;ng tiện &iacute;ch, hiện đại v&agrave; chuy&ecirc;n nghiệp nhất. Sự kiện ph&aacute;t h&agrave;nh thẻ t&iacute;n dụng quốc tế Visa v&agrave; d&ograve;ng thẻ Platinum của SeABank đ&aacute;nh dấu bước tiến mới trong việc ph&aacute;t triển sản phẩm của SeABank, qua đ&oacute; g&oacute;p phần đa dạng h&oacute;a sản phẩm dịch vụ của ng&acirc;n h&agrave;ng đồng thời gia tăng lợi &iacute;ch cho kh&aacute;ch h&agrave;ng&rdquo;, &ocirc;ng Đặng Bảo Kh&aacute;nh, Tổng gi&aacute;m đốc SeABank cho biết.</span><br style=\"list-style: none; color: rgb(0, 0, 0); font-family: \'Segoe UI\'; font-size: 15px;\" />\n	<br style=\"list-style: none; color: rgb(0, 0, 0); font-family: \'Segoe UI\'; font-size: 15px;\" />\n	<span style=\"color: rgb(0, 0, 0); font-family: \'Segoe UI\'; font-size: 15px;\">Nhằm khuyến kh&iacute;ch kh&aacute;ch h&agrave;ng sử dụng thẻ t&iacute;n dụng quốc tế SeABank Visa, từ 1/8&ndash; 29/10/2013, SeABank triển khai chương tr&igrave;nh ưu đ&atilde;i ho&agrave;n tiền 50% gi&aacute; trị chi ti&ecirc;u của 3 giao dịch đầu ti&ecirc;n trong v&ograve;ng 45 ng&agrave;y kể từ ng&agrave;y nhận thẻ. Mức ho&agrave;n tiền tối đa cho thẻ Platinum l&agrave; 1.000.000 đồng, cho thẻ hạng V&agrave;ng v&agrave; Chuẩn l&agrave; 500.000 đồng.</span></p>', '0', '0', '0', '0', null, '0', 'Visa-Credit-mat-the_2013-01-176a2.gif', '1', '0', null, null, '0', '0', '', '1', 'img/news/', '0', '0', null, 'Visa Platinum, Visa, SeABank', '2013-08-05 12:31:45', null, null, null, null, null, null, null, 'Nguồn tin vneconomy', null, null, null, null, null, null, null);
INSERT INTO articles VALUES ('371', null, '80', null, '370', '0', 'NEWS00370', 'Năm Quý Tỵ (2013): Nhà phạm Tam sát và cách hóa giải Tam sát', 'nam-quy-ty-2013-nha-pham-tam-sat-va-cach-hoa-giai-tam-sat', 'Ngoài việc nhà ở, văn phòng, cửa hàng, phòng ngủ, phòng khách, phạm phải hướng có sao xấu “Ngũ Hoàng Đại Sát” chiếu, hoặc bay vào trung cung, và sao xấu “Nhị Hắc” chiếu ngay hướng nhà mình đang ở, hoặc hướng văn phòng, hướng cửa hàng, mỗi năm mỗi hướng, thì bên cạnh đó', '<p>\n	Ngo&agrave;i việc nh&agrave; ở, văn ph&ograve;ng, cửa h&agrave;ng, ph&ograve;ng ngủ, ph&ograve;ng kh&aacute;ch, phạm phải hướng c&oacute; sao xấu &ldquo;<a href=\"http://www.blogphongthuy.com/ngu-hoang-dai-sat.html\" title=\"Ngũ Hoàng Đại Sát\">Ngũ Ho&agrave;ng Đại S&aacute;t</a>&rdquo; chiếu, hoặc bay v&agrave;o trung cung, v&agrave; sao xấu &ldquo;Nhị Hắc&rdquo; chiếu ngay hướng nh&agrave; m&igrave;nh đang ở, hoặc hướng văn ph&ograve;ng, hướng cửa h&agrave;ng, mỗi năm mỗi hướng, th&igrave; b&ecirc;n cạnh đ&oacute; cần phải ch&uacute; &yacute; đến hướng nh&agrave;, hướng văn ph&ograve;ng, hướng cửa h&agrave;ng, hướng ph&ograve;ng ngủ&nbsp;<a href=\"http://www.blogphongthuy.com/tag/pham-tam-sat\" rel=\"tag\" title=\"Posts tagged with phạm tam sát\">phạm Tam s&aacute;t</a>.</p>\n<p style=\"text-align: center;\">\n	<img alt=\"Năm Quý Tỵ (2013): Nhà phạm Tam sát và cách hóa giải Tam sát\" src=\"http://www.blogphongthuy.com/wp-content/uploads/2013/01/tam-sat-nam-quy-ty-2013.jpg\" title=\"Năm Quý Tỵ (2013): Nhà phạm Tam sát và cách hóa giải Tam sát\" /></p>\n<p>\n	Hướng nh&agrave;, hướng văn ph&ograve;ng, hướng cửa h&agrave;ng, hướng ph&ograve;ng ngủ phạm&nbsp;<a href=\"http://www.blogphongthuy.com/tag/tam-sat\" rel=\"tag\" title=\"Posts tagged with tam sát\">Tam s&aacute;t</a>, th&igrave; người nh&agrave; mắc nhiều bệnh, gặp hạn xấu&hellip;Trong phong thủy lấy hướng phạm&nbsp;<a href=\"http://www.blogphongthuy.com/tag/tam-sat\" rel=\"tag\" title=\"Posts tagged with tam sát\">Tam s&aacute;t</a>&nbsp;l&agrave; đại kỵ.</p>\n<p>\n	C&acirc;u quyết đả n&oacute;i :</p>\n<p>\n	- Dần, Ngọ, Tuất: S&aacute;t&nbsp;Bắc.<br />\n	- Th&acirc;n, T&yacute;, Th&igrave;n&nbsp;: S&aacute;t&nbsp;Nam.<br />\n	- Hợi, Mẹo, M&ugrave;i&nbsp;: S&aacute;t&nbsp;T&acirc;y.<br />\n	- Tỵ, Dậu, Sửu&nbsp;: S&aacute;t&nbsp;Đ&ocirc;ng.</p>\n<p>\n	Dần, Ngọ, Tuất hợp Hỏa cục, h&ograve;a vượng ở phương Nam, phương Bắc (Hợi,T&yacute;,Sửu) v&agrave; sự xung của n&oacute; l&agrave; Tam s&aacute;t. (Hợi l&agrave; kiếp s&aacute;t, T&yacute; l&agrave; tai s&aacute;t, Sửu l&agrave; tuế s&aacute;t, Tuế s&aacute;t cũng gọi l&agrave; mộ khố s&aacute;t).</p>\n<p>\n	Th&acirc;n, T&yacute;, Th&igrave;n hợp Thủy cục, thủy vượng ở phương Bắc, phương Nam (Tỵ,Ngọ,M&ugrave;i) v&agrave; sự xung của n&oacute; l&agrave; Tam s&aacute;t. (Tỵ l&agrave; kiếp s&aacute;t, Ngọ l&agrave; tai s&aacute;t, M&ugrave;i l&agrave; tuế s&aacute;t).</p>\n<p>\n	Hợi, Mẹo, M&ugrave;i hợp Mộc cục, mộc vượng ở phương Đ&ocirc;ng, phương T&acirc;y (Th&acirc;n, Dậu, Tuất) v&agrave; sự xung của n&oacute; l&agrave; Tam s&aacute;t.(Th&acirc;n l&agrave; kiếp s&aacute;t, Dậu l&agrave; tai s&aacute;t, Tuất l&agrave; tuế s&aacute;t).</p>\n<p>\n	Tỵ, Dậu, Sửu hợp Kim cục, kim vượng ở phương T&acirc;y, phương Đ&ocirc;ng (Dần, Mẹo, Th&igrave;n) v&agrave; sự xung của n&oacute; l&agrave; Tam s&aacute;t. (Dần l&agrave; kiếp s&aacute;t, Mảo l&agrave; tai s&aacute;t, Th&igrave;n l&agrave; tuế s&aacute;t).</p>\n<p>\n	Lấy năm m&agrave; n&oacute;i,như năm Dần, Ngọ, Tuất. Bắc phương đều n&oacute;i l&agrave; năm Tam s&aacute;t. Năm Tam s&aacute;t ở hướng, kh&ocirc;ng thể tọa. Tọa Tam s&aacute;t tức tọa s&aacute;t, đại kỵ: Năm, th&aacute;ng, ng&agrave;y, giờ Dần, Ngọ, Tuất, đều kỵ tu tạo ở Bắc phương. C&aacute;c phương kh&aacute;c cứ thế m&agrave; suy ra.</p>\n<p>\n	Như vậy Tam s&aacute;t l&agrave; t&ecirc;n gọi chung của kiếp s&aacute;t, tai s&aacute;t v&agrave; tuế s&aacute;t, mổi năm chiếm ba hướng tuyệt, thai v&agrave; dưởng l&agrave; tam hạp của ngũ h&agrave;nh.</p>\n<p>\n	Cụ thể:&nbsp;Năm nay l&agrave; năm&nbsp;QU&Yacute; TỴ&nbsp;(2013), theo c&acirc;u quyết :&nbsp;Tỵ,&nbsp;Dậu, Sửu : S&aacute;tĐ&ocirc;ng. C&oacute; nghĩa l&agrave; nh&agrave; ta&nbsp;ở hướng Đ&ocirc;ng&nbsp;l&agrave;&nbsp;phạm phải Tam s&aacute;t.</p>\n<p>\n	Nếu gặp phải Tam s&aacute;t, người nh&agrave; mắc nhiều bệnh, gặp hạn xấu&hellip;</p>\n<p>\n	Phương ph&aacute;p h&oacute;a giải: L&agrave; đặt 3 con Kỳ l&acirc;n hoặc 3 con Sư tử, v&agrave; đặc biệt l&agrave;&nbsp; 3 con Tỳ Hưu (vừa h&oacute;a giải hạn Tam s&aacute;t, vừa chi&ecirc;u t&agrave;i, t&aacute;c lộc cho gia đạo).</p>\n<p>\n	Đầu Kỳ l&acirc;n hoặc Sư tử, hoặc Tỳ Hưu hướng ra ngo&agrave;i cửa ch&iacute;nh, muốn ngăn ngừa Tam s&aacute;t, ta phải xếp liền 3 con Kỳ l&acirc;n, hoặc 3 con Sư tử, hoặc 3 con Tỳ Hưu c&ugrave;ng một chỗ.</p>\n<p>\n	Về chất liệu của Kỳ l&acirc;n hoặc Sư tử, hoặc Tỳ Hưu, th&igrave; bằng đ&aacute;, bằng ngọc, hay kim loại đều sử dụng được.</p>', '0', '0', '0', '0', null, '0', 'tam-sat-nam-quy-ty-20132-150x150.jpg', '1', '0', null, null, '0', '0', '', '1', 'img/news/', '0', '0', null, 'Tam sát, Năm Quý Tỵ ', '2013-08-05 12:33:50', null, null, null, null, null, null, null, 'Nguồn tin blogphongthuy', null, null, null, null, null, null, null);
INSERT INTO articles VALUES ('372', null, '80', null, '371', '0', 'NEWS00371', 'Cách chọn mua nhà đất theo phong thủy', 'cach-chon-mua-nha-dat-theo-phong-thuy', 'Theo Phong thủy cổ truyền Đông phương, có hai tiêu chí đặt ra cho việc chọn nhà. Quan trọng thứ nhất đó là vị trí của ngôi nhà. Kế đến là hướng nhà. Phong thủy cổ truyền có câu nhất vị nhị hướng chính là ý này.', '<p>\n	Theo&nbsp;<a href=\"http://www.blogphongthuy.com/tag/phong-thuy-am-duong-tap-luan\" rel=\"tag\" title=\"Posts tagged with Phong Thủy\">Phong thủy</a>&nbsp;cổ truyền Đ&ocirc;ng phương, c&oacute; hai ti&ecirc;u ch&iacute; đặt ra cho việc chọn nh&agrave;. Quan trọng thứ nhất đ&oacute; l&agrave; vị tr&iacute; của ng&ocirc;i nh&agrave;.</p>\n<p style=\"text-align: center;\">\n	<img alt=\"Cách chọn mua nhà đất theo phong thủy\" src=\"http://www.blogphongthuy.com/wp-content/uploads/2012/06/nhadepso1.jpg\" title=\"Cách chọn mua nhà đất theo phong thủy\" /></p>\n<p>\n	Kế đến l&agrave; hướng nh&agrave;. Phong thủy cổ truyền c&oacute; c&acirc;u nhất vị nhị hướng ch&iacute;nh l&agrave; &yacute; n&agrave;y.</p>\n<p>\n	Đối với Phong thủy một ng&ocirc;i nh&agrave; nhỏ qui m&ocirc; gia đ&igrave;nh, việc t&igrave;m vị tr&iacute; cũng n&ecirc;n được suy x&eacute;t c&acirc;n nhắc.</p>\n<p>\n	X&eacute;t tổng thể, nếu muốn chọn lựa một ng&ocirc;i nh&agrave; tốt trong th&agrave;nh thị nơi d&acirc;n cư sầm uất, n&ecirc;n t&igrave;m nơi bằng phẳng rộng r&atilde;i, đất nền cao hơn trung b&igrave;nh một ch&uacute;t để tr&aacute;nh kh&ocirc;ng gian bị t&ugrave; t&uacute;ng, ngập lụt ảnh hưởng đến đời sống.</p>\n<p>\n	C&ograve;n nếu ở v&ugrave;ng sơn cước, cao nguy&ecirc;n nhiều nắng gi&oacute; n&ecirc;n chọn nơi l&ograve;ng chảo thấp trũng k&iacute;n đ&aacute;o, nơi c&oacute; ba bề bốn b&ecirc;n l&agrave; n&uacute;i hoặc c&acirc;y cối tr&aacute;nh gi&oacute; thổi tạt ảnh hưởng tới sinh hoạt con người. Đ&acirc;y ch&iacute;nh l&agrave; vấn đề T&agrave;ng phong tụ kh&iacute; theo c&aacute;ch n&oacute;i của Thuyết phong thủy.</p>\n<p>\n	Trong phong thủy yếu tố thứ hai cũng kh&aacute; quan trọng đ&oacute; l&agrave; Hướng nh&agrave;. Hướng nh&agrave; trước khi b&agrave;n tới việc tương quan tới Tuổi của gia chủ, th&igrave; cần quan t&acirc;m tới sự giao tiếp của căn nh&agrave; m&igrave;nh với m&ocirc;i trường xung quanh. Trước mặt nh&agrave; kh&ocirc;ng n&ecirc;n nh&igrave;n ra nơi đổ n&aacute;t, c&oacute; chứa r&aacute;c thải v&igrave; dễ bị uế kh&iacute;, t&agrave; kh&iacute; x&acirc;m nhập v&agrave;o nh&agrave;.</p>\n<p>\n	Trước nh&agrave; kh&ocirc;ng n&ecirc;n bị c&acirc;y lớn, cột điện hoặc vật cản &aacute;n ngữ, khiến tầm nh&igrave;n bị che khuất dẫn tới việc &aacute;nh s&aacute;ng, kh&ocirc;ng kh&iacute; kh&oacute; lưu chuyển. Kh&ocirc;ng n&ecirc;n chọn nh&agrave; đối diện với c&acirc;y kh&ocirc;, cột điện, g&oacute;c nhọn của c&ocirc;ng tr&igrave;nh hoặc bị những vật nhọn lớn, cạnh tường của nh&agrave; kh&aacute;c đ&acirc;m v&agrave;o, l&agrave; những điểm l&agrave;m cho kh&iacute; thế của ng&ocirc;i nh&agrave; k&eacute;m đi nhiều.</p>\n<p>\n	Ngo&agrave;i ra cũng n&ecirc;n qu&aacute;n x&eacute;t tới yếu tố kh&iacute; hậu v&ugrave;ng miền m&agrave; lựa chọn nh&agrave; sao cho ph&ugrave; hợp. Người Việt ta xưa thường c&oacute; kinh nghiệm Lấy vợ hiền h&ograve;a l&agrave;m nh&agrave; hướng nam.</p>\n<p>\n	Nh&agrave; hướng Nam sẽ đ&oacute;n được gi&oacute; m&aacute;t về m&ugrave;a h&egrave; v&agrave; tr&aacute;nh gi&oacute; Đ&ocirc;ng bắc lạnh về m&ugrave;a đ&ocirc;ng, tr&aacute;nh được bức xạ trực tiếp từ hướng T&acirc;y v&agrave;o c&aacute;c buổi chiều h&egrave; rất gay gắt, th&ecirc;m nữa l&agrave; với kiểu hướng nh&agrave; n&agrave;y, &aacute;nh s&aacute;ng chiếu v&agrave;o nh&agrave; với cường độ &iacute;t c&oacute; sự thay đổi l&agrave;m cho t&acirc;m l&yacute; con người trở n&ecirc;n dễ chịu.</p>\n<p>\n	Về vấn đề tương quan hướng nh&agrave; với tuổi của gia chủ, thời gian gần đ&acirc;y cũng rất nhiều người quan t&acirc;m v&agrave; đưa ra thắc mắc v&agrave; muốn t&igrave;m hiểu về vấn đề n&agrave;y. Luật Phong thủy chia con người ta l&agrave;m 2 nh&oacute;m người kh&aacute;c nhau.</p>\n<p>\n	Nh&oacute;m một th&igrave; hợp với c&aacute;c hướng thuộc Đ&ocirc;ng, nh&oacute;m thứ hai hợp với c&aacute;c hướng thuộc T&acirc;y n&ecirc;n gọi l&agrave; T&acirc;y tứ mệnh v&agrave; Đ&ocirc;ng tứ mệnh. Cứ theo đ&oacute; m&agrave; t&igrave;m hướng nh&agrave; cho ph&ugrave; hợp.</p>\n<p>\n	Thật ra quan điểm n&agrave;y cũng c&oacute; c&aacute;i đ&uacute;ng nhưng chưa đầy đủ. Vai tr&ograve; của vị tr&iacute; Tọa lạc rất quan trọng. Vấn đề T&acirc;y tứ v&agrave; Đ&ocirc;ng tứ mệnh ảnh hưởng l&agrave; kh&ocirc;ng qu&aacute; nhiều. Hơn nữa khi găp phải những căn nh&agrave; c&oacute; hướng với c&aacute;i t&ecirc;n đầy th&aacute;ch thức như Ngũ quỉ, Tuyệt mạng đối với c&aacute;c chuy&ecirc;n gia Phong thủy điều đ&oacute; kh&ocirc;ng phải l&agrave; nhiệm vụ bất khả thi.</p>', '0', '0', '0', '0', null, '0', 'nhadepso12-150x150.jpg', '1', '0', null, null, '0', '0', '', '1', 'img/news/', '0', '0', null, 'phong thủy, mua nhà đất', '2013-08-05 12:35:13', '2013-08-05 12:39:11', null, null, null, null, null, null, 'Nguồn tin blogphongthuy', null, null, null, null, null, null, null);
INSERT INTO articles VALUES ('373', null, '81', null, '372', '0', 'NEWS00372', 'Bí quyết sử dụng màu sắc ấn tượng cho phòng khách', 'bi-quyet-su-dung-mau-sac-an-tuong-cho-phong-khach', 'Để có được một phòng khách ấn tượng, những lời khuyên trong bài viết dưới đây sẽ phần nào giúp bạn thêm bí quyết trong việc trang trí, làm đẹp nhà.', '<div class=\"title\" style=\"font-weight: bold; padding-bottom: 5px; padding-top: 5px; color: rgb(6, 108, 147); font-family: arial, Tahoma, \'Times New Roman\'; line-height: 1.5; text-align: justify;\">\n	Để c&oacute; được một ph&ograve;ng kh&aacute;ch ấn tượng, những lời khuy&ecirc;n trong b&agrave;i viết dưới đ&acirc;y sẽ phần n&agrave;o gi&uacute;p bạn th&ecirc;m b&iacute; quyết trong việc trang tr&iacute;, l&agrave;m đẹp nh&agrave;.</div>\n<p>\n	&nbsp;</p>\n<div style=\"text-align: center;\">\n	<font class=\"content\" style=\"color: rgb(0, 0, 0); font-family: arial, Tahoma, \'Times New Roman\';\"><img alt=\"Bi quyet su dung mau an tuong cho phong khach, gach khong nung\" height=\"362\" src=\"http://dmcgroup.vn/image/Tin%20tuc/bi-quyet-su-dung-mau-sac-an-tuong-cho-phong-khach-1.jpg\" width=\"490\" /></font></div>\n<div style=\"text-align: justify;\">\n	<br />\n	<font class=\"content\" style=\"color: rgb(0, 0, 0); font-family: arial, Tahoma, \'Times New Roman\';\">- Bạn c&oacute; thể kết hợp một bức tường m&agrave;u đậm hơn so với c&aacute;c bức tường kh&aacute;c sơn c&ugrave;ng m&agrave;u để tạo điểm nhấn. C&aacute;c m&agrave;u tương phản kết hợp kh&eacute;o l&eacute;o với nhau sẽ l&agrave;m cho ph&ograve;ng kh&aacute;ch trở n&ecirc;n hấp dẫn, bắt mắt hơn.<br />\n	<br />\n	- Nếu bạn sử dụng m&agrave;u trắng hoặc c&aacute;c m&agrave;u trung t&iacute;nh cho c&aacute;c bức tường th&igrave; bạn n&ecirc;n tr&aacute;nh sự đơn điệu cho ph&ograve;ng kh&aacute;ch bằng nội thất m&agrave;u bắt mắt như m&agrave;u l&aacute;, xanh coban, v&agrave;ng...<br />\n	<br />\n	- Sơn một bức tường bằng m&agrave;u ch&agrave;m, xanh l&aacute; hay đỏ tươi v&agrave; sử dụng m&agrave;u sắc tương tự cho một v&agrave;i c&aacute;c vật dụng kh&aacute;c trong ph&ograve;ng kh&aacute;ch như lọ hoa, nệm ghế để tạo sự li&ecirc;n kết trong kh&ocirc;ng gian.<br />\n	&nbsp;</font></div>\n<div style=\"text-align: center;\">\n	<font class=\"content\" style=\"color: rgb(0, 0, 0); font-family: arial, Tahoma, \'Times New Roman\';\"><img alt=\"Bi quyet su dung mau an tuong cho phong khach\" src=\"http://dmcgroup.vn/image/Tin%20tuc/bi-quyet-su-dung-mau-sac-an-tuong-cho-phong-khach-2.jpg\" /></font></div>\n<div style=\"text-align: justify;\">\n	<br />\n	<font class=\"content\" style=\"color: rgb(0, 0, 0); font-family: arial, Tahoma, \'Times New Roman\';\">- Kh&ocirc;ng bao giờ n&ecirc;n sử dụng m&agrave;u đen để sơn cho c&aacute;c gian ph&ograve;ng diện t&iacute;ch nhỏ, n&oacute; c&oacute; thể khiến cho căn ph&ograve;ng trở n&ecirc;n hẹp hơn. V&agrave; n&ecirc;n sử dụng c&aacute;c m&agrave;u s&aacute;ng để l&agrave;m kh&ocirc;ng gian rộng hơn.<br />\n	<br />\n	- Bạn cũng c&oacute; thể tạo ra một kh&ocirc;ng gian tho&aacute;ng đ&atilde;ng v&agrave; y&ecirc;n b&igrave;nh khi sử dụng c&aacute;c m&agrave;u xanh dương nhạt kết hợp với m&agrave;u trắng, m&agrave;u be, m&agrave;u đất.<br />\n	<br />\n	- Để l&agrave;m mới v&agrave; tăng sức sống cho kh&ocirc;ng gian, bạn n&ecirc;n th&ecirc;m hoa tươi hoặc c&acirc;y xanh trong ph&ograve;ng kh&aacute;ch.<br />\n	&nbsp;</font></div>\n<div style=\"text-align: center;\">\n	<font class=\"content\" style=\"color: rgb(0, 0, 0); font-family: arial, Tahoma, \'Times New Roman\';\"><img alt=\"Bi quyet su dung mau an tuong cho phong khach\" src=\"http://dmcgroup.vn/image/Tin%20tuc/bi-quyet-su-dung-mau-sac-an-tuong-cho-phong-khach-3.jpg\" /></font></div>\n<div style=\"text-align: justify;\">\n	<font class=\"content\" style=\"color: rgb(0, 0, 0); font-family: arial, Tahoma, \'Times New Roman\';\">&nbsp;<br />\n	- Sơn trần m&agrave;u trắng sẽ l&agrave;m kh&ocirc;ng gian rộng v&agrave; s&aacute;ng hơn b&igrave;nh thường.<br />\n	<br />\n	- Chọn c&aacute;c đ&egrave;n s&aacute;ng nghệ thuật để l&agrave;m thay đổi ho&agrave;n to&agrave;n kh&ocirc;ng gian ph&ograve;ng kh&aacute;ch<br />\n	<br />\n	- Bạn c&oacute; thể chọn loại đ&egrave;n b&agrave;n với chụp đ&egrave;n bằng k&iacute;nh m&agrave;u để tạo sự mới lạ cho ph&ograve;ng kh&aacute;ch.</font></div>', '0', '0', '0', '0', null, '0', 'bi-quyet-su-dung-mau-sac-an-tuong-cho-phong-khach-1.jpg', '1', '0', null, null, '0', '0', '', '1', 'img/news/', '0', '0', null, 'Bí quyết, phòng khách', '2013-08-05 12:36:41', null, null, null, null, null, null, null, 'Nguồn tin dmcgroup', null, null, null, null, null, null, null);
INSERT INTO articles VALUES ('374', null, '82', null, '373', '0', 'NEWS00373', 'Áp dụng biện pháp cưỡng chế thi hành án đối với từng người phải thi hành án', 'ap-dung-bien-phap-cuong-che-thi-hanh-an-doi-voi-tung-nguoi-phai-thi-hanh-an', 'Ngân hàng XBANK cấp tín dụng cho một Công ty A. Khoản cấp tín dụng được đảm bảo bằng thư bảo lãnh thanh toán cho XBANK của Ngân hàng X và các bất động sản là tài sản đảm bảo bao gồm: Nhà và đất của Công ty A', '<p>\n	Ng&acirc;n h&agrave;ng XBANK cấp t&iacute;n dụng cho một C&ocirc;ng ty A. Khoản cấp t&iacute;n dụng được đảm bảo bằng thư bảo l&atilde;nh thanh to&aacute;n&nbsp;cho XBANK của Ng&acirc;n h&agrave;ng X v&agrave; c&aacute;c bất động sản l&agrave; t&agrave;i sản đảm bảo bao gồm:&nbsp;Nh&agrave; v&agrave; đất&nbsp;của&nbsp;C&ocirc;ng ty&nbsp;A, quyền sử dụng đất v&agrave; t&agrave;i sản gắn liền với đất của người thứ ba.&nbsp;Do C&ocirc;ng ty A vi phạm nghĩa vụ trả nợ với XBANK n&ecirc;n XBANK khởi kiện vụ kiện tại T&ograve;a &aacute;n để y&ecirc;u cầu giải quyết vụ việc. T&ograve;a &aacute;n đ&atilde; x&eacute;t xử sơ thẩm v&agrave;o th&aacute;ng 06/2012 v&agrave; x&eacute;t xử ph&uacute;c thẩm v&agrave;o th&aacute;ng 12/2012 với ph&aacute;n quyết tại bản &aacute;n ph&uacute;c thẩm tuy&ecirc;n:</p>\n<p>\n	- C&ocirc;ng ty A phải thanh to&aacute;n cho Ng&acirc;n h&agrave;ng XBANK số tiền nợ theo hợp đồng t&iacute;n dụng t&iacute;nh đến ng&agrave;y x&eacute;t xử l&agrave; 97.866.989.298 đồng v&agrave; to&agrave;n bộ tiền l&atilde;i ph&aacute;t sinh tr&ecirc;n vốn gốc từ ng&agrave;y x&eacute;t xử cho đến khi trả hết nợ cho XBANK theo mức l&atilde;i suất nợ qu&aacute; hạn quy định trong hợp đồng t&iacute;n dụng giữa XBANK v&agrave; C&ocirc;ng ty A. Thời hạn thanh to&aacute;n ngay sau khi bản &aacute;n c&oacute; hiệu lực ph&aacute;p luật.</p>\n<p>\n	- Qu&aacute; thời hạn m&agrave; C&ocirc;ng ty A kh&ocirc;ng thanh to&aacute;n hoặc thanh to&aacute;n kh&ocirc;ng đầy đủ c&aacute;c khoản nợ th&igrave;:</p>\n<p>\n	+ Ng&acirc;n h&agrave;ng X phải thực hiện nghĩa vụ bảo l&atilde;nh theo c&aacute;c thư bảo l&atilde;nh đ&atilde; ph&aacute;t h&agrave;nh bằng c&aacute;ch thanh to&aacute;n cho XBANK số tiền gốc của nghĩa vụ bảo l&atilde;nh l&agrave; 50.000.000.000 đồng.</p>\n<p>\n	+ Ph&aacute;t m&atilde;i c&aacute;c t&agrave;i sản đảm bảo của C&ocirc;ng ty A v&agrave; của b&ecirc;n thứ ba để thu hồi nợ cho XBANK.</p>\n<p>\n	Th&aacute;ng 01/2013, XBANK c&oacute; đơn y&ecirc;u cầu thi h&agrave;nh &aacute;n. Cơ quan thi h&agrave;nh &aacute;n đ&atilde; y&ecirc;u cầu T&ograve;a &aacute;n giải th&iacute;ch bản &aacute;n nội dung trong trường hợp C&ocirc;ng ty A kh&ocirc;ng thanh to&aacute;n hoặc thanh to&aacute;n kh&ocirc;ng đầy đủ c&aacute;c khoản nợ trong phần quyết định của bản &aacute;n, th&igrave; cơ quan thi h&agrave;nh &aacute;n c&oacute; được xử l&yacute; khấu trừ ngay tiền trong t&agrave;i khoản của Ng&acirc;n h&agrave;ng X để thi h&agrave;nh nghĩa vụ trả nợ theo c&aacute;c thư bảo l&atilde;nh với số tiền gốc của nghĩa vụ bảo l&atilde;nh l&agrave; 50.000.000.000 đồng trước khi k&ecirc; bi&ecirc;n xử l&yacute; c&aacute;c t&agrave;i sản bảo đảm được tuy&ecirc;n trong bản &aacute;n hay kh&ocirc;ng? Th&aacute;ng 03/2013, T&ograve;a &aacute;n c&oacute; văn bản trả lời nếu C&ocirc;ng ty A kh&ocirc;ng thanh to&aacute;n hoặc thanh to&aacute;n kh&ocirc;ng đầy đủ c&aacute;c khoản nợ trong phần quyết định của bản &aacute;n ph&uacute;c thẩm, cơ quan thi h&agrave;nh &aacute;n được quyền đồng thời &aacute;p dụng biện ph&aacute;p t&agrave;i sản sau: Buộc Ng&acirc;n h&agrave;ng X phải thực hiện nghĩa vụ trả nợ theo c&aacute;c thư bảo l&atilde;nh với số tiền gốc của nghĩa vụ bảo l&atilde;nh l&agrave; 50.000.000.000 đồng, ph&aacute;t m&atilde;i c&aacute;c t&agrave;i sản đảm bảo được tuy&ecirc;n trong phần quyết định của bản &aacute;n ph&uacute;c thẩm.</p>\n<p>\n	Vậy, XBANK c&oacute; quyền y&ecirc;u cầu cơ quan thi h&agrave;nh &aacute;n thực hiện cưỡng chế thi h&agrave;nh &aacute;n đối với Ng&acirc;n h&agrave;ng X v&agrave; c&aacute;c đương sự kh&aacute;c kh&ocirc;ng? Hay v&igrave; trong văn bản của T&ograve;a ph&uacute;c thẩm T&ograve;a &aacute;n nh&acirc;n d&acirc;n tối cao c&oacute; giải th&iacute;ch cơ quan thi h&agrave;nh &aacute;n được quyền đồng thời &aacute;p dụng biện ph&aacute;p t&agrave;i sản, do đ&oacute;, việc khấu trừ tiền trong t&agrave;i khoản của Ng&acirc;n h&agrave;ng X phải được thực hiện c&ugrave;ng một thời điểm với việc k&ecirc; bi&ecirc;n, ph&aacute;t m&atilde;i t&agrave;i sản thế chấp của C&ocirc;ng ty A v&agrave; c&aacute;c t&agrave;i sản thế chấp kh&aacute;c?</p>', '0', '0', '0', '0', null, '0', 'tam-sat-nam-quy-ty-20132-150x1501.jpg', '1', '0', null, null, '0', '0', '', '1', 'img/news/', '0', '0', null, 'biện pháp cưỡng chế, hành án', '2013-08-05 12:38:54', null, null, null, null, null, null, null, 'Nguồn tin moj.gov.vn', null, null, null, null, null, null, null);
INSERT INTO articles VALUES ('378', null, '84', null, '377', '0', 'NEWS00377', '4S Riverside Linh Đông', '4s-riverside-linh-dong', 'CẢNH QUAN được thiết kế đúng nhất theo phong cách của resort hiện đại cấp 5 sao bao gồm các tiện ích sang trọng, hoạt động thể chất ngoài trời (tennis, hồ bơi, bóng chuyền, bóng rổ, cầu lông, sân bóng đá mini, đường chạy bộ), khu relaxMÔI TRƯỜNG trong lành ven sông, một trong vài dự án có mật độ sử dụng đất thấp nhất (Sân vườn rộng gấp 4 lần công trình).', '<p>\n	<strong>V&Agrave;I ĐIỀU TH&Uacute; VỊ VỀ NHỮNG C&Aacute;I NHẤT TP HCM CỦA DỰ &Aacute;N</strong></p>\n<p>\n	<strong>4S Riverside Linh Đ&ocirc;ng -&nbsp;</strong>Căn hộ Resort mặt tiền S&ocirc;ng S&agrave;i G&ograve;n c&aacute;ch trung t&acirc;m Quận 1 chỉ 5 km</p>\n<p>\n	<strong>Dự &aacute;n 4S Riverside Linh Đ&ocirc;ng</strong>&nbsp;với tổng diện t&iacute;ch đất : 33.386 m2 (3,34 ha), bao gồm 03 ph&acirc;n khu:</p>\n<ul>\n	<li>\n		Khu nh&agrave; ở : 6.865 m2</li>\n	<li>\n		Khu thể thao : 4.413 m2</li>\n	<li>\n		Khu c&acirc;y xanh : 22.108 m2</li>\n</ul>\n<p>\n	Hội trường 880m2 cho c&aacute;c sinh hoạt tập thể cộng đồng.</p>\n<ul>\n	<li>\n		Diện t&iacute;ch x&acirc;y dựng : 6.865 m2</li>\n	<li>\n		Diện t&iacute;ch s&agrave;n x&acirc;y dựng : 116.924,39 m2</li>\n	<li>\n		Mật độ x&acirc;y dựng : 21 %</li>\n	<li>\n		Hệ số sử dụng đất : 3,5&nbsp;</li>\n</ul>\n<p>\n	<br />\n	<strong>M&Ocirc;I TRƯỜNG</strong>&nbsp;trong l&agrave;nh ven s&ocirc;ng, một trong v&agrave;i dự &aacute;n c&oacute; mật độ sử dụng đất thấp nhất (S&acirc;n vườn rộng gấp 4 lần c&ocirc;ng tr&igrave;nh).&nbsp;<br />\n	<br />\n	<strong>CẢNH QUAN</strong>&nbsp;được thiết kế đ&uacute;ng nhất theo phong c&aacute;ch của resort hiện đại cấp 5 sao bao gồm c&aacute;c tiện &iacute;ch sang trọng, hoạt động thể chất ngo&agrave;i trời (tennis, hồ bơi, b&oacute;ng chuyền, b&oacute;ng rổ, cầu l&ocirc;ng, s&acirc;n b&oacute;ng đ&aacute; mini, đường chạy bộ), khu relax<strong>M&Ocirc;I TRƯỜNG</strong>&nbsp;trong l&agrave;nh ven s&ocirc;ng, một trong v&agrave;i dự &aacute;n c&oacute; mật độ sử dụng đất thấp nhất (S&acirc;n vườn rộng gấp 4 lần c&ocirc;ng tr&igrave;nh).&nbsp;<br />\n	<br />\n	<strong>CẢNH QUAN</strong>&nbsp;được thiết kế đ&uacute;ng nhất theo phong c&aacute;ch của resort hiện đại cấp 5 sao bao gồm c&aacute;c tiện &iacute;ch sang trọng, hoạt động thể chất ngo&agrave;i trời (tennis, hồ bơi, b&oacute;ng chuyền, b&oacute;ng rổ, cầu l&ocirc;ng, s&acirc;n b&oacute;ng đ&aacute; mini, đường chạy bộ), khu relax.&nbsp;<br />\n	<br />\n	<strong>TIỆN &Iacute;CH C&Ocirc;NG CỘNG</strong>&nbsp;hiện đại v&agrave; đầy đủ nhất, được xếp ch&iacute;nh thức v&agrave;o cấp c&ocirc;ng tr&igrave;nh đặc biệt theo ti&ecirc;u ch&iacute; nh&agrave; nước. Bao gồm 21 hạng mục: giao th&ocirc;ng đường thủy - bộ (cano - xe bus), ph&ograve;ng v&eacute;, ăn uống, may mặc, ng&acirc;n h&agrave;ng, ph&ograve;ng kh&aacute;m, nh&agrave; thuốc, mua sắm (shopping), chợ (market), trường học, bưu điện, giải tr&iacute; (c&acirc;u lạc bộ)...., v&agrave; hơn 3.000 chỗ để xe c&aacute;c loại.&nbsp;</p>', '0', '0', '0', '0', null, '0', 'nhadepso12-150x1501.jpg', '1', '0', null, null, '0', '0', '', '1', 'img/news/', '0', '0', null, '', '2013-08-05 16:52:00', '2013-08-14 18:13:15', null, null, null, null, null, null, '0', '5', '14', '11', '100 triệu', '01/01/2010', '05/11/2011', 'A Thắng');
