INSERT INTO articles VALUES ('385', null, '84', null, '384', '0', 'NEWS00384', 'The Everrich 3 - Riverside Villas', 'the-everrich-3-riverside-villas', 'Được triển khai xây dựng trên khu đất có khuôn viên diện tích hơn 12 ha, mật độ xây dựng dưới 35%, với 1,3 ha diện tích mặt nước và công viên cây xanh rộng 5.000 m2. The EverRich 3 Riverside Villas bao gồm 89 biệt thự đơn lập và song lập ven sông và khoảng 2.700 căn hộ.', '<p class=\"big\" style=\"margin: 0px; padding: 0px; color: rgb(153, 153, 153); font-family: Verdana, Arial, Helvetica, sans-serif;\">\n	<span style=\"margin: 0px; padding: 0px; font-size: small;\">Được triển khai x&acirc;y dựng tr&ecirc;n khu đất c&oacute; khu&ocirc;n vi&ecirc;n diện t&iacute;ch hơn 12 ha, mật độ x&acirc;y dựng dưới 35%, với 1,3 ha diện t&iacute;ch mặt nước v&agrave; c&ocirc;ng vi&ecirc;n c&acirc;y xanh rộng 5.000 m2.&nbsp;<strong style=\"margin: 0px; padding: 0px;\">The EverRich 3 Riverside Villas</strong>&nbsp;bao gồm 89 biệt thự đơn lập v&agrave; song lập ven s&ocirc;ng v&agrave; khoảng 2.700 căn hộ.</span></p>\n<p style=\"margin: 0px; padding: 0px; color: rgb(153, 153, 153); font-family: Verdana, Arial, Helvetica, sans-serif; text-align: center;\">\n	<span style=\"margin: 0px; padding: 0px; font-size: small;\"><img alt=\"Phoi-canh-tong-the-the-everrich-3-riverside-villas\" src=\"http://www.vnre.com.vn/root//Du-an/Everrich-3/Phoi-canh-tong-the-the-everrich-3-riverside-villas.jpg\" style=\"margin: 0px; padding: 0px; border: 0px;\" title=\"Phoi-canh-tong-the-the-everrich-3-riverside-villas\" /></span></p>\n<p class=\"big\" style=\"margin: 0px; padding: 0px; color: rgb(153, 153, 153); font-family: Verdana, Arial, Helvetica, sans-serif;\">\n	&nbsp;</p>\n<p class=\"big\" style=\"margin: 0px; padding: 0px; color: rgb(153, 153, 153); font-family: Verdana, Arial, Helvetica, sans-serif;\">\n	<span style=\"margin: 0px; padding: 0px; font-size: small;\"><strong style=\"margin: 0px; padding: 0px;\">Biệt thự phố&nbsp;</strong>vườn c&oacute; diện t&iacute;ch 8x18=144m2</span></p>\n<p class=\"big\" style=\"margin: 0px; padding: 0px; color: rgb(153, 153, 153); font-family: Verdana, Arial, Helvetica, sans-serif;\">\n	<span style=\"margin: 0px; padding: 0px; font-size: small;\"><strong style=\"margin: 0px; padding: 0px;\">Biệt thự ven s&ocirc;ng</strong>&nbsp;c&oacute; diện t&iacute;ch 10x20=200m2</span></p>\n<p class=\"big\" style=\"margin: 0px; padding: 0px; color: rgb(153, 153, 153); font-family: Verdana, Arial, Helvetica, sans-serif;\">\n	&nbsp;</p>\n<p style=\"margin: 0px; padding: 0px; color: rgb(153, 153, 153); font-family: Verdana, Arial, Helvetica, sans-serif; text-align: center;\">\n	<span style=\"margin: 0px; padding: 0px; font-size: small;\"><img alt=\"the everrich 3 riverside villas\" src=\"http://www.vnre.com.vn/root//Du-an/Everrich-3/the-everrich-3-matbang-.jpg\" style=\"margin: 0px; padding: 0px; border: 0px;\" title=\"the-everrich-3-matbang-\" /></span></p>\n<p class=\"big\" style=\"margin: 0px; padding: 0px; color: rgb(153, 153, 153); font-family: Verdana, Arial, Helvetica, sans-serif;\">\n	<span style=\"margin: 0px; padding: 0px; font-size: small;\">Ngo&agrave;i những tiện &iacute;ch b&ecirc;n trong như: Trung t&acirc;m thương mại, nh&agrave; h&agrave;ng, cafe, ph&ograve;ng tập gym, hồ bơi, trường học,...&nbsp;<strong style=\"margin: 0px; padding: 0px;\">The EverRich 3</strong>&nbsp;c&ograve;n thừa hưởng v&ocirc; số tiện &iacute;ch khi năm liền kề với đ&ocirc; thị Ph&uacute; Mỹ Hưng.</span></p>\n<p style=\"margin: 0px; padding: 0px; color: rgb(153, 153, 153); font-family: Verdana, Arial, Helvetica, sans-serif; text-align: center;\">\n	<span style=\"margin: 0px; padding: 0px; font-size: small;\"><img alt=\"the everrich 3 riverside villas\" src=\"http://www.vnre.com.vn/root//Du-an/Everrich-3/Screen%20Shot%202013-06-03%20at%208.37.36%20PM.png\" style=\"margin: 0px; padding: 0px; border: 0px;\" title=\"Screen Shot 2013-06-03 at 8.37.36 PM\" /></span></p>\n<p class=\"big\" style=\"margin: 0px; padding: 0px; color: rgb(153, 153, 153); font-family: Verdana, Arial, Helvetica, sans-serif;\">\n	<span style=\"margin: 0px; padding: 0px; font-size: small;\"><strong style=\"margin: 0px; padding: 0px;\">Th&ocirc;ng tin th&ecirc;m:</strong></span></p>\n<p class=\"big\" style=\"margin: 0px; padding: 0px; color: rgb(153, 153, 153); font-family: Verdana, Arial, Helvetica, sans-serif;\">\n	<span style=\"margin: 0px; padding: 0px; font-size: small;\"><strong style=\"margin: 0px; padding: 0px;\">The EverRich 3 Riverside Villas</strong>&nbsp;do C&ocirc;ng ty Cổ phần Ph&aacute;t triển Bất động sản Ph&aacute;t Đạt l&agrave;m chủ đầu tư. Dự &aacute;n hiện đang được thi c&ocirc;ng cơ sở hạ tầng, dự kiến sẽ ho&agrave;n th&agrave;nh tất cả c&aacute;c hạng mục v&agrave;o năm 2019.</span></p>', '0', '0', '0', '0', null, '0', 'Phoi-canh-tong-the-the-everrich-3-riverside-villas.jpg', '1', '0', null, null, '0', '0', '', '1', 'img/news/', '0', '0', null, 'Villas, The Everrich', '2013-08-05 18:17:53', '2013-08-14 18:09:42', null, null, null, null, null, null, '0', '1', '8', '1', '200 tỷ', '2012', '2015', 'A Thiện');
