INSERT INTO showrooms VALUES ('1', 'Showroom chính', '', 'showroom-chinh', '2', 'Showroom, nội thất, tanlocphat', 'Showroom được thành lập năm 2010, với kinh nghiệm từng thực hiện nhiều dự án nội thất cho khách hàng. Chúng tôi luôn mang tới những sản phẩm đầy chất lượng cho khách hàng.', '<p>\n	<span style=\"color: rgb(0, 0, 0); font-family: \'Times New Roman\'; font-size: 16px; line-height: 24px; background-color: rgb(244, 245, 246);\">T&ocirc;i năm nay 25 tuổi, lấy vợ được 4 năm. T&ocirc;i v&agrave; em quen nhau qua bạn th&acirc;n, sau một thời gian y&ecirc;u nhau say đắm c&oacute; đi qu&aacute; giới hạn v&agrave; cưới. Thời gian đầu em l&agrave;m một người vợ, người con d&acirc;u rất ngoan hiền, biết điều trong gia đ&igrave;nh. Bố t&ocirc;i th&igrave; b&igrave;nh thường c&ograve;n mẹ t&ocirc;i rất thương em v&igrave; ho&agrave;n cảnh đều l&agrave; phụ nữ. Mẹ rất t&acirc;m l&yacute;, xem vợ t&ocirc;i như con ruột, nh&igrave;n ngo&agrave;i ai cũng n&oacute;i ch&uacute;ng t&ocirc;i hạnh ph&uacute;c.</span></p>\n', '', 'img/showroom/7.jpg', '0', '1', '2013-07-11 11:04:48', '2013-07-22 22:59:35');
INSERT INTO showrooms VALUES ('3', 'Xưởng sản xuất', 'Factory', 'xuong-san-xuat', '1', 'xưởng sản xuất, nội thất', 'Showroom được thành lập năm 2010, với kinh nghiệm từng thực hiện nhiều dự án nội thất cho khách hàng. Chúng tôi luôn mang tới những sản phẩm đầy chất lượng cho khách hàng.', '<p>\n	Địa chỉ: 77/23 Nhất Chi Mai, F.13, Q. T&acirc;n B&igrave;nh</p>\n<p>\n	DT: 0908.714.719</p>\n<p>\n	Chi tiết: Showroom lu&ocirc;n cung cấp c&aacute;c mặt h&agrave;ng đồ nội thất đẹp cho c&aacute;c tỉnh miền trung</p>\n<p>\n	Địa chỉ: 77/23 Nhất Chi Mai, F.13, Q. T&acirc;n B&igrave;nh</p>\n<p>\n	DT: 0908.714.719</p>\n<p>\n	Chi tiết: Showroom lu&ocirc;n cung cấp c&aacute;c mặt h&agrave;ng đồ nội thất đẹp cho c&aacute;c tỉnh miền trung</p>\n', '<div>\n	Address: 77/23 Nhat Chi Mai, Ward 13, District Tan Binh</div>\n<div>\n	&nbsp;</div>\n<div>\n	DT: 0908.714.719</div>\n<div>\n	&nbsp;</div>\n<div>\n	Details: Showroom always provides beautiful items of furniture for the central provinces</div>\n<div>\n	&nbsp;</div>\n<div>\n	Address: 77/23 Nhat Chi Mai, Ward 13, District Tan Binh</div>\n<div>\n	&nbsp;</div>\n<div>\n	DT: 0908.714.719</div>\n<div>\n	&nbsp;</div>\n<div>\n	Details: Showroom always provides beautiful items of furniture for the central provinces</div>\n', 'img/showroom/2-phongngu.jpg', 'D8nxL3eRZ2M', '3', '2013-07-12 16:32:23', '2013-07-25 07:14:55');
